INSERT INTO `creature_ai_texts` VALUES ('-1', 'I see those fools at the Abbey sent some fresh meat for us.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '103');
INSERT INTO `creature_ai_texts` VALUES ('-23', 'On guard!', null, null, null, null, null, null, null, null, '11241', '1', '0', '0', '20886');
INSERT INTO `creature_ai_texts` VALUES ('-24', 'Defend yourself, for all the good it will do...', null, null, null, null, null, null, null, null, '11242', '1', '0', '0', '20886');
INSERT INTO `creature_ai_texts` VALUES ('-25', 'Knew this was... the only way out', null, null, null, null, null, null, null, null, '11243', '1', '0', '0', '20886');
INSERT INTO `creature_ai_texts` VALUES ('-26', 'Yes, that was quite satisfying', null, null, null, null, null, null, null, null, '11239', '1', '0', '0', '20886');
INSERT INTO `creature_ai_texts` VALUES ('-27', 'At last, a target for my frustrations!', null, null, null, null, null, null, null, null, '11238', '1', '0', '0', '20886');
INSERT INTO `creature_ai_texts` VALUES ('-28', 'Have you come to kill Dalliah? Can I watch?', null, null, null, null, null, null, null, null, '11237', '1', '0', '0', '20886');
INSERT INTO `creature_ai_texts` VALUES ('-46', '%s becomes enraged!', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'Common Text');
INSERT INTO `creature_ai_texts` VALUES ('-47', '%s attempts to run away in fear!', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'Common Text');
INSERT INTO `creature_ai_texts` VALUES ('-48', 'You are spotted by the eye of Grillok!', null, null, null, null, null, null, null, null, '0', '2', '0', '0', '19440');
INSERT INTO `creature_ai_texts` VALUES ('-49', 'Wait... No! No!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '16414');
INSERT INTO `creature_ai_texts` VALUES ('-50', 'Not... Fair...', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '16409');
INSERT INTO `creature_ai_texts` VALUES ('-51', 'The fates have turned against me!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '16408');
INSERT INTO `creature_ai_texts` VALUES ('-52', 'Where am I? What...', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '16406');
INSERT INTO `creature_ai_texts` VALUES ('-53', 'What is happening?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '16406');
INSERT INTO `creature_ai_texts` VALUES ('-54', 'You... will... pay...', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '16424');
INSERT INTO `creature_ai_texts` VALUES ('-55', 'You will be punished!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '16424');
INSERT INTO `creature_ai_texts` VALUES ('-56', 'Five seconds! I\'m not kidding!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '16460');
INSERT INTO `creature_ai_texts` VALUES ('-57', 'So I said, \"Yeah, but that\'ll cost you extra.\"', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '16460');
INSERT INTO `creature_ai_texts` VALUES ('-58', 'He asked if the imp could join in... can you believe it? Actually, it wasn\'t half bad...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '16460');
INSERT INTO `creature_ai_texts` VALUES ('-59', 'We could have had so much fun!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '16461');
INSERT INTO `creature_ai_texts` VALUES ('-60', 'They fall asleep after. Me, I fall asleep during....', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '16461');
INSERT INTO `creature_ai_texts` VALUES ('-61', 'I\'ll miss the show!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '16468');
INSERT INTO `creature_ai_texts` VALUES ('-62', 'Yes... I think I remember now...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '16468');
INSERT INTO `creature_ai_texts` VALUES ('-63', 'It\'s all... coming back to me.', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '16470');
INSERT INTO `creature_ai_texts` VALUES ('-64', 'Is it really over?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '16470');
INSERT INTO `creature_ai_texts` VALUES ('-65', 'Free... at last.', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '16481');
INSERT INTO `creature_ai_texts` VALUES ('-66', 'Time to move on...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '16481');
INSERT INTO `creature_ai_texts` VALUES ('-67', 'Death is far too merciful for your kind.', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '16485');
INSERT INTO `creature_ai_texts` VALUES ('-68', 'The same fate awaits all who would steal from the master.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '16485');
INSERT INTO `creature_ai_texts` VALUES ('-69', 'Others will take my place!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '16485');
INSERT INTO `creature_ai_texts` VALUES ('-70', 'This... changes nothing. Eternal damnation awaits you!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '16485');
INSERT INTO `creature_ai_texts` VALUES ('-71', 'Turn your back while you can!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '16544');
INSERT INTO `creature_ai_texts` VALUES ('-72', 'You will succeed only in damning yourselves!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '16544');
INSERT INTO `creature_ai_texts` VALUES ('-73', 'It is unwise to anger me.', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '20885');
INSERT INTO `creature_ai_texts` VALUES ('-74', 'Completely ineffective. Just like someone else I know.', null, null, null, null, null, null, null, null, '11087', '1', '0', '0', '20885');
INSERT INTO `creature_ai_texts` VALUES ('-75', 'You chose the wrong opponent.', null, null, null, null, null, null, null, null, '11088', '1', '0', '0', '20885');
INSERT INTO `creature_ai_texts` VALUES ('-76', 'Now I\'m really... angry...', null, null, null, null, null, null, null, null, '11093', '1', '0', '0', '20885');
INSERT INTO `creature_ai_texts` VALUES ('-77', 'Ahh... That is much better.', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '20885');
INSERT INTO `creature_ai_texts` VALUES ('-78', 'Ahh... Just what I needed.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '20885');
INSERT INTO `creature_ai_texts` VALUES ('-79', 'Reap the Whirlwind!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '20885');
INSERT INTO `creature_ai_texts` VALUES ('-80', 'I\'ll cut you to pieces!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '20885');
INSERT INTO `creature_ai_texts` VALUES ('-93', 'The work must continue.', null, null, null, null, null, null, null, null, '10376', '1', '0', '0', '17941');
INSERT INTO `creature_ai_texts` VALUES ('-94', 'Don\'t make me kill you!', null, null, null, null, null, null, null, null, '10378', '1', '0', '0', '17941');
INSERT INTO `creature_ai_texts` VALUES ('-95', 'You brought this on yourselves.', null, null, null, null, null, null, null, null, '10379', '1', '0', '0', '17941');
INSERT INTO `creature_ai_texts` VALUES ('-96', 'It had to be done.', null, null, null, null, null, null, null, null, '10380', '1', '0', '0', '17941');
INSERT INTO `creature_ai_texts` VALUES ('-97', 'You should not have come.', null, null, null, null, null, null, null, null, '10381', '1', '0', '0', '17941');
INSERT INTO `creature_ai_texts` VALUES ('-98', 'I... Deserve this.', null, null, null, null, null, null, null, null, '10382', '1', '0', '0', '17941');
INSERT INTO `creature_ai_texts` VALUES ('-99', 'You will never wake the dreamer!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '3669');
INSERT INTO `creature_ai_texts` VALUES ('-101', 'For the Dragonmaw!', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'Common Dragonmaw Text');
INSERT INTO `creature_ai_texts` VALUES ('-102', 'Long live the Dragonmaw! Die you worthless $R!', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'Common Dragonmaw Text');
INSERT INTO `creature_ai_texts` VALUES ('-104', 'This land belongs to the Dark Iron Dwarves. Prepare to see the afterlife, $C!', null, null, null, null, null, null, null, null, '0', '0', '6', '0', '1364');
INSERT INTO `creature_ai_texts` VALUES ('-105', 'Your bones will break under my boot, $R!', null, null, null, null, null, null, null, null, '0', '0', '1', '0', '2108');
INSERT INTO `creature_ai_texts` VALUES ('-106', '%s goes into a frenzy!', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'Enrage Text');
INSERT INTO `creature_ai_texts` VALUES ('-107', 'Protect the innocent!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '15638');
INSERT INTO `creature_ai_texts` VALUES ('-108', 'Serve the Public Trust!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '15638');
INSERT INTO `creature_ai_texts` VALUES ('-109', 'Uphold the Law!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '15638');
INSERT INTO `creature_ai_texts` VALUES ('-110', 'Take heart! Your friends will not long mourn your passing!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '15638');
INSERT INTO `creature_ai_texts` VALUES ('-111', '%s bursts, showering the area with arcane residue!', null, null, null, null, null, null, null, null, '0', '2', '0', '0', '15648');
INSERT INTO `creature_ai_texts` VALUES ('-113', 'More, more, more!  I must feed soon.', null, null, null, null, null, null, null, null, '0', '0', '10', '0', 'Common Wretched Text');
INSERT INTO `creature_ai_texts` VALUES ('-114', 'I cannot stand the pain.  I must find a new source.', null, null, null, null, null, null, null, null, '0', '0', '10', '0', 'Common Wretched Text');
INSERT INTO `creature_ai_texts` VALUES ('-115', 'Haven\'t you done enough harm?  Must you also disturb my slumber?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '19456');
INSERT INTO `creature_ai_texts` VALUES ('-116', 'What good does this violence serve?  What is done is done.  I have failed my people.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '19456');
INSERT INTO `creature_ai_texts` VALUES ('-123', 'I am the serpent king! I can do anything.', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '3673');
INSERT INTO `creature_ai_texts` VALUES ('-124', 'Insolence!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '16329');
INSERT INTO `creature_ai_texts` VALUES ('-133', 'I be da predator! You da prey... ', null, null, null, null, null, null, null, null, '12013', '1', '0', '0', '23574');
INSERT INTO `creature_ai_texts` VALUES ('-134', 'You can\'t... kill... me spirit!', null, null, null, null, null, null, null, null, '12019', '1', '0', '0', '23574');
INSERT INTO `creature_ai_texts` VALUES ('-135', 'Feed, me bruddahs!', null, null, null, null, null, null, null, null, '12014', '1', '0', '0', '23574');
INSERT INTO `creature_ai_texts` VALUES ('-136', 'Stop your cryin\'!', null, null, null, null, null, null, null, null, '12018', '1', '0', '0', '23574');
INSERT INTO `creature_ai_texts` VALUES ('-137', 'Ya got nothin\'!', null, null, null, null, null, null, null, null, '12017', '1', '0', '0', '23574');
INSERT INTO `creature_ai_texts` VALUES ('-138', 'All you be doin\' is wastin\' me time!', null, null, null, null, null, null, null, null, '12016', '1', '0', '0', '23574');
INSERT INTO `creature_ai_texts` VALUES ('-139', 'Come an\' join me bruddah !', null, null, null, null, null, null, null, null, '12015', '1', '0', '0', '23574');
INSERT INTO `creature_ai_texts` VALUES ('-140', 'The coils of death... Will crush you.', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '3670');
INSERT INTO `creature_ai_texts` VALUES ('-141', 'Invaders! Sound the Alarm!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '23586');
INSERT INTO `creature_ai_texts` VALUES ('-142', 'In the throes of the Sundering, Ameth\'Aran was at the whim of the terror that gripped the land. There was little hope for survival.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '4509');
INSERT INTO `creature_ai_texts` VALUES ('-143', 'Athrikus came to us. He told us that he could save us from harm. He cast a spell upon us to protect us from harm.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '4509');
INSERT INTO `creature_ai_texts` VALUES ('-144', 'When the shaking stopped, his true motives were revealed. We were trapped, and he was slowly draining our powers.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '4509');
INSERT INTO `creature_ai_texts` VALUES ('-145', 'There were hundreds imprisoned by his spell. Now only a few remain in their prisons. He would speak to us sometimes.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '4509');
INSERT INTO `creature_ai_texts` VALUES ('-146', 'He worried that his power weakened, that soon even we last of his precious soulgems would fade and die.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '4509');
INSERT INTO `creature_ai_texts` VALUES ('-147', 'His lieutenant, Ilkurd Magthrull possesses a tome which might indicate the location of the remaining soulgems.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '4509');
INSERT INTO `creature_ai_texts` VALUES ('-148', 'Woot! Thanks!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '17681');
INSERT INTO `creature_ai_texts` VALUES ('-149', '%s becomes unstable and begins to disintegrate!', null, null, null, null, null, null, null, null, '0', '2', '0', '0', '16876');
INSERT INTO `creature_ai_texts` VALUES ('-150', 'Will you help me find my dog...?', null, null, null, null, null, null, null, null, '11797', '0', '0', '0', '23618');
INSERT INTO `creature_ai_texts` VALUES ('-151', 'I miss my dog so much...', null, null, null, null, null, null, null, null, '11798', '0', '0', '0', '23618');
INSERT INTO `creature_ai_texts` VALUES ('-152', '%s grows in size upon seeing $N!', null, null, null, null, null, null, null, null, '0', '2', '0', '0', '17725');
INSERT INTO `creature_ai_texts` VALUES ('-153', '%s strength fades!', null, null, null, null, null, null, null, null, '0', '2', '0', '0', '17725');
INSERT INTO `creature_ai_texts` VALUES ('-154', 'Illidan reigns!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '17727');
INSERT INTO `creature_ai_texts` VALUES ('-155', '%s begins to arm the Proximity Bomb!', null, null, null, null, null, null, null, null, '0', '2', '0', '0', '17414');
INSERT INTO `creature_ai_texts` VALUES ('-156', 'The blood is our power!', null, null, null, null, null, null, null, null, '0', '0', '1', '0', '17491');
INSERT INTO `creature_ai_texts` VALUES ('-157', 'Lok Narash!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '17370');
INSERT INTO `creature_ai_texts` VALUES ('-158', 'For Kargath! For Victory!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '17397');
INSERT INTO `creature_ai_texts` VALUES ('-159', 'Lok\'tar Illidari!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '17397');
INSERT INTO `creature_ai_texts` VALUES ('-160', 'This world is OURS!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '17397');
INSERT INTO `creature_ai_texts` VALUES ('-162', 'Mmm... I love my delicious Southshore stout.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '2440');
INSERT INTO `creature_ai_texts` VALUES ('-163', 'Slay them, my brethren! For the Scourge!', null, null, null, null, null, null, null, null, '5822', '1', '0', '0', '7357');
INSERT INTO `creature_ai_texts` VALUES ('-164', 'We will enslave the quilboar!', null, null, null, null, null, null, null, null, '5819', '1', '0', '0', '7357');
INSERT INTO `creature_ai_texts` VALUES ('-165', 'We will spread across this barren land!', null, null, null, null, null, null, null, null, '5820', '1', '0', '0', '7357');
INSERT INTO `creature_ai_texts` VALUES ('-166', 'Soon, the Scourge will rule the world!', null, null, null, null, null, null, null, null, '5821', '1', '0', '0', '7357');
INSERT INTO `creature_ai_texts` VALUES ('-167', 'KILL!!!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '3569');
INSERT INTO `creature_ai_texts` VALUES ('-168', 'Who dares disturb me?  Die $N!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '315');
INSERT INTO `creature_ai_texts` VALUES ('-169', 'None may challenge the Brotherhood!', null, null, null, null, null, null, null, null, '5780', '1', '0', '0', '639');
INSERT INTO `creature_ai_texts` VALUES ('-170', 'And stay down!', null, null, null, null, null, null, null, null, '5781', '1', '0', '0', '639');
INSERT INTO `creature_ai_texts` VALUES ('-171', 'Lap dogs, all of you!', null, null, null, null, null, null, null, null, '5782', '1', '0', '0', '639');
INSERT INTO `creature_ai_texts` VALUES ('-172', 'Fools! our cause it righteous!', null, null, null, null, null, null, null, null, '5783', '1', '0', '0', '639');
INSERT INTO `creature_ai_texts` VALUES ('-173', 'The Brotherhood will prevail!', null, null, null, null, null, null, null, null, '5784', '1', '0', '0', '639');
INSERT INTO `creature_ai_texts` VALUES ('-174', 'We\'re under attack! Avast ya swabs! Repel the invaders!', null, null, null, null, null, null, null, null, '5777', '1', '0', '0', '646');
INSERT INTO `creature_ai_texts` VALUES ('-175', 'You land lovers are tougher then I thought! I will need to improvise', null, null, null, null, null, null, null, null, '5778', '0', '0', '0', '646');
INSERT INTO `creature_ai_texts` VALUES ('-176', 'D\'ah! Now you\'re making me angry!', null, null, null, null, null, null, null, null, '5779', '0', '0', '0', '646');
INSERT INTO `creature_ai_texts` VALUES ('-177', 'I\'m going to wear your skin as a smoking jacket!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '11058');
INSERT INTO `creature_ai_texts` VALUES ('-178', 'The stogies? You\'ll have to pry them from my cold dead... er... RAWR!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '11058');
INSERT INTO `creature_ai_texts` VALUES ('-179', 'Hold them back while I release the warhounds!', null, null, null, null, null, null, null, null, '10160', '1', '0', '0', '17259');
INSERT INTO `creature_ai_texts` VALUES ('-180', 'My pets are hungry and they will dine on your flesh!', null, null, null, null, null, null, null, null, '10161', '1', '0', '0', '17259');
INSERT INTO `creature_ai_texts` VALUES ('-181', 'We are the true Horde!', null, null, null, null, null, null, null, null, '0', '0', '1', '0', '17259');
INSERT INTO `creature_ai_texts` VALUES ('-182', 'Mine! Mine! Mine! Gizlock is the ruler of this domain! You shall never reveal my presence!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '13601');
INSERT INTO `creature_ai_texts` VALUES ('-183', 'What? Oh no. I don\'t care what you have to say. I just enjoy inflicting pain.', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '4306');
INSERT INTO `creature_ai_texts` VALUES ('-184', 'You will talk eventually. You might as well spill it now.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '4306');
INSERT INTO `creature_ai_texts` VALUES ('-185', 'Confess and we shall set you free.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '4306');
INSERT INTO `creature_ai_texts` VALUES ('-186', 'The living are here!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '10411');
INSERT INTO `creature_ai_texts` VALUES ('-187', '%s calls for his worgs!', null, null, null, null, null, null, null, null, '0', '2', '0', '0', '19442');
INSERT INTO `creature_ai_texts` VALUES ('-192', 'It can get really hot here in the Badlands. But at least it\'s a dry heat.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '2922');
INSERT INTO `creature_ai_texts` VALUES ('-193', 'Gizmos... check. Doothingies... check. Wizzers... check.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '2922');
INSERT INTO `creature_ai_texts` VALUES ('-194', 'One day I\'ll be a real boy.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '2922');
INSERT INTO `creature_ai_texts` VALUES ('-195', 'Brzt... click... whir...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '2922');
INSERT INTO `creature_ai_texts` VALUES ('-196', 'It is dangerous here, master. But I shall help stand guard.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '2922');
INSERT INTO `creature_ai_texts` VALUES ('-197', 'We can make it better, stronger, faster. We have the technology. We have the magic.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '2922');
INSERT INTO `creature_ai_texts` VALUES ('-198', 'You should not be here! Slay them!', null, null, null, null, null, null, null, null, '5799', '1', '0', '0', '4831');
INSERT INTO `creature_ai_texts` VALUES ('-200', 'You will be easy prey, $C.', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '2015,2017,2018');
INSERT INTO `creature_ai_texts` VALUES ('-201', 'My talons will shred your puny body, $R.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '2018');
