INSERT INTO `creature_text` VALUES ('25678', '0', '3', 'I recommened evisceration!', '14', '0', '100', '0', '0', '0', '0', '0', 'Part of the Doctor Razorgin on aggro lines');
INSERT INTO `creature_text` VALUES ('25678', '0', '4', 'It\'s no good... you need more work first.', '14', '0', '100', '0', '0', '0', '0', '0', 'Part of the Doctor Razorgin on aggro lines');
