INSERT INTO `creature_text` VALUES ('25678', '0', '6', 'The doctor is in!', '14', '0', '100', '0', '0', '0', '24870', '0', 'Part of the Doctor Razorgin on aggro lines');
INSERT INTO `creature_text` VALUES ('25753', '0', '0', 'Warning! Warning! Intruder alert! Intruder alert!', '12', '0', '100', '0', '0', '0', '0', '0', 'Sentry-bot 57-K');
INSERT INTO `creature_text` VALUES ('25753', '0', '1', 'You have been detected. You will be assimilated or eliminated.', '12', '0', '100', '0', '0', '0', '0', '0', 'Sentry-bot 57-K');
INSERT INTO `creature_text` VALUES ('25753', '0', '2', 'Activate counter-measures. Repel intruder.', '12', '0', '100', '0', '0', '0', '0', '0', 'Sentry-bot 57-K');
INSERT INTO `creature_text` VALUES ('25758', '0', '0', 'Incoming $r flier! Shoot $g him:her; down!', '12', '0', '100', '0', '0', '0', '0', '0', 'Defendo-tank 66D');
INSERT INTO `creature_text` VALUES ('25773', '0', '1', 'I survived and I\'m flesh again!', '12', '0', '0', '0', '0', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('25773', '0', '2', 'But... but... I was perfect!', '12', '0', '0', '0', '0', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('25773', '0', '3', 'Where am I? Who are you? What\'s that strange feeling?', '12', '0', '0', '0', '0', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('25773', '0', '4', 'I\'m flesh and blood again! Thank you!', '12', '0', '0', '0', '0', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('25783', '0', '0', 'I\'m flesh and blood again. That\'s all that matters!', '12', '7', '100', '5', '0', '0', '0', '0', 'Fizzcrank Survivor');
INSERT INTO `creature_text` VALUES ('25814', '1', '1', 'We are Mechagnome...resistance is futile.', '12', '0', '0', '0', '0', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('25814', '1', '2', 'The flesh is weak. We will make you better, stronger...faster.', '12', '0', '0', '0', '0', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('25814', '1', '3', 'We can decurse you, we have the technology.', '12', '0', '0', '0', '0', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('26073', '0', '0', 'Who disturbs my meditation?!', '14', '7', '100', '15', '0', '0', '0', '0', 'High Priest Talet-Kha');
INSERT INTO `creature_text` VALUES ('26293', '0', '0', 'The Hulking Jormungar falters for a moment, opening its mouth wide.', '41', '0', '100', '0', '0', '0', '0', '0', 'Hulking Jormungar');
INSERT INTO `creature_text` VALUES ('26420', '0', '0', 'Ah, yes. Loken is well known to me.', '12', '0', '100', '0', '0', '0', '0', '0', 'Gavrock');
INSERT INTO `creature_text` VALUES ('26420', '1', '0', 'It is he who commands the sons of iron in their war against us.', '12', '0', '100', '0', '0', '0', '0', '0', 'Gavrock');
INSERT INTO `creature_text` VALUES ('26420', '2', '0', 'From his hiding place, he oversees their preparations for war with the goal of exterminating the stone giants!', '12', '0', '100', '0', '0', '0', '0', '0', 'Gavrock');
INSERT INTO `creature_text` VALUES ('26484', '0', '0', 'He\'s out to get me, too! I\'d be careful around here if I was you, traveler. You never know which tree he\'s hiding behind!', '12', '0', '100', '0', '0', '0', '0', '0', 'Hugh Glass');
INSERT INTO `creature_text` VALUES ('26484', '1', '0', 'That\'s not something Limpy Joe would ask! But yeah, I know Loken.', '12', '0', '100', '0', '0', '0', '0', '0', 'Hugh Glass');
INSERT INTO `creature_text` VALUES ('26537', '0', '0', 'The zeppelin to Warsong Hold has just arrived! All aboard for Borean Tundra!', '14', '0', '100', '0', '0', '0', '0', '0', 'Greeb Ramrocket');
INSERT INTO `creature_text` VALUES ('26566', '0', '0', 'Guards!', '14', '1', '100', '0', '0', '0', '0', '0', 'Narzun Skybreaker');
INSERT INTO `creature_text` VALUES ('26602', '0', '0', 'Guards!', '14', '7', '100', '0', '0', '0', '0', '0', 'Kara Thricestar');
INSERT INTO `creature_text` VALUES ('26668', '0', '0', 'The sensation is... beyond my imagining. I am yours to command, my king.', '14', '0', '0', '0', '0', '13857', '0', '0', 'Svala Sorrowgrave SAY_DIALOG_WITH_ARTHAS_2');
INSERT INTO `creature_text` VALUES ('26668', '1', '0', 'I will be happy to slaughter them in your name! Come, enemies of the Scourge! I will show you the might of the Lich King!', '14', '0', '0', '0', '0', '13858', '0', '0', 'Svala Sorrowgrave SAY_DIALOG_WITH_ARTHAS_3');
INSERT INTO `creature_text` VALUES ('26668', '2', '0', 'I will vanquish your soul!', '14', '0', '0', '0', '0', '13842', '0', '0', 'Svala Sorrowgrave SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('26668', '3', '0', 'You were a fool to challenge the power of the Lich King!', '14', '0', '0', '0', '0', '13845', '0', '0', 'Svala Sorrowgrave SAY_SLAY_1');
INSERT INTO `creature_text` VALUES ('26668', '3', '1', 'Your will is done, my king.', '14', '0', '0', '0', '0', '13847', '0', '0', 'Svala Sorrowgrave SAY_SLAY_2');
INSERT INTO `creature_text` VALUES ('26668', '3', '2', 'Another soul for my master.', '14', '0', '0', '0', '0', '13848', '0', '0', 'Svala Sorrowgrave SAY_SLAY_3');
INSERT INTO `creature_text` VALUES ('26668', '4', '0', 'Nooo! I did not come this far... to...', '14', '0', '0', '0', '0', '13855', '0', '0', 'Svala Sorrowgrave SAY_DEATH');
INSERT INTO `creature_text` VALUES ('26668', '5', '0', 'Your death approaches.', '14', '0', '0', '0', '0', '13850', '0', '0', 'Svala Sorrowgrave SAY_SACRIFICE_1');
INSERT INTO `creature_text` VALUES ('26668', '5', '1', 'Go now to my master.', '14', '0', '0', '0', '0', '13851', '0', '0', 'Svala Sorrowgrave SAY_SACRIFICE_2');
INSERT INTO `creature_text` VALUES ('26668', '5', '2', 'Your end is inevitable.', '14', '0', '0', '0', '0', '13852', '0', '0', 'Svala Sorrowgrave SAY_SACRIFICE_3');
INSERT INTO `creature_text` VALUES ('26668', '5', '3', 'Yor-guul mak!', '14', '0', '0', '0', '0', '13853', '0', '0', 'Svala Sorrowgrave SAY_SACRIFICE_4');
