INSERT INTO `creature_text` VALUES ('27842', '1', '0', 'Arrrhhh...Guards!', '14', '7', '100', '0', '0', '0', '0', '0', 'Common Horde Flight Master');
INSERT INTO `creature_text` VALUES ('27915', '0', '0', 'Good work with the crates! Come talk to me in front of Stratholme for your next assignment!', '15', '0', '0', '0', '0', '0', '0', '3', 'Chromie - SAY_EVENT_START');
