INSERT INTO `creature_text` VALUES ('27990', '6', '0', 'Please, mortal, speak with Arcanist Tybalin in Dalaran. He may be able to negotiate with the Sunreavers for access to the book.', '12', '0', '100', '1', '0', '0', '0', '0', 'Krasus');
