INSERT INTO `creature_text` VALUES ('28012', '0', '0', 'The trickster Mage-Lord Urom protects the third ring. He will appear alone and defenseless, but do not be fooled by appearences! Urom is a powerful conjurer who commands a menagerie of Phantasmal creatures. Seek him out above.', '12', '0', '0', '0', '0', '0', '0', '0', '');
