INSERT INTO `creature_text` VALUES ('30190', '3', '0', 'The ore used in this sample originated deep wihin the crust of Azeroth and was recently unearthed.', '12', '0', '100', '275', '0', '0', '0', '0', 'Attendant Tock');
INSERT INTO `creature_text` VALUES ('30190', '4', '0', 'The saronite in this sample is nearly pure. Ores like this do not occur naturally on the surface of Azeroth.', '12', '0', '100', '0', '0', '0', '0', '0', 'Attendant Tock');
INSERT INTO `creature_text` VALUES ('30190', '5', '0', 'This ore can only have been created by a powerful, malevolent force. Attempting to access databanks for more information...', '12', '0', '100', '1', '0', '0', '0', '0', 'Attendant Tock');
INSERT INTO `creature_text` VALUES ('30190', '6', '0', 'Access denied?!', '12', '0', '100', '6', '0', '0', '0', '0', 'Attendant Tock');
INSERT INTO `creature_text` VALUES ('30190', '7', '0', 'Further information can only be accessed from the archives within Ulduar, by order of Keeper Loken.', '12', '0', '100', '1', '0', '0', '0', '0', 'Attendant Tock');
INSERT INTO `creature_text` VALUES ('30190', '8', '0', 'But one thing is for certain: this plate\'s size, thickness, and bolt holes point to a use as armor for a colossal structure.', '12', '0', '100', '1', '0', '0', '0', '0', 'Attendant Tock');
INSERT INTO `creature_text` VALUES ('30190', '9', '0', 'Directive completed. Returning TO standby mode.', '12', '0', '100', '1', '0', '0', '0', '0', 'Attendant Tock');
INSERT INTO `creature_text` VALUES ('30224', '0', '0', 'What\'s on yer mind, lad?', '12', '0', '100', '6', '0', '0', '0', '0', 'Crusader Lord Dalfors');
INSERT INTO `creature_text` VALUES ('30225', '0', '0', 'Eversong Woods, Dalfors. I\'m thinking about how beautiful it was before Arthas cut a swathe of death through it...', '12', '0', '100', '1', '0', '0', '0', '0', 'Crusader Sunborn');
INSERT INTO `creature_text` VALUES ('30271', '0', '0', 'Guards!', '14', '1', '100', '0', '0', '0', '0', '0', 'Galendror Whitewing');
INSERT INTO `creature_text` VALUES ('30274', '0', '0', 'Thank you and farewell, friend. I must return to the Argent Vanguard.', '12', '0', '100', '0', '0', '0', '0', '0', 'Freed Crusader Speach');
INSERT INTO `creature_text` VALUES ('30377', '0', '0', 'You are dealing with a being that holds within it the consciousness of the most cunning, intelligent, and ruthless individuals to ever live.', '12', '0', '100', '1', '0', '0', '0', '0', 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES ('30377', '1', '0', 'The Lich King is unlike any foe that you have ever faced, Highlord. Though you bested him upon the holy ground of Light\'s Hope Chapel, you tread now upon his domain.$B', '12', '0', '100', '1', '0', '0', '0', '0', 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES ('30377', '2', '0', 'You cannot win. Not like this...$B', '12', '0', '100', '274', '0', '0', '0', '0', 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES ('30377', '3', '0', 'Nothing. There is nothing that you can do while the Light binds you. It controls you wholly, shackling you to the ground with its virtues.', '12', '0', '100', '274', '0', '0', '0', '0', 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES ('30377', '4', '0', '%s shakes his head.', '16', '0', '100', '0', '0', '0', '0', '0', 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES ('30377', '5', '0', 'Look upon the field, Highlord. The Lich King has halted your advance completely and won the upper hand!', '12', '0', '100', '25', '0', '0', '0', '0', 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES ('30377', '6', '0', 'The breach you created was sealed with Nerubian webbing almost as quickly as it was opened.', '12', '0', '100', '1', '0', '0', '0', '0', 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES ('30377', '7', '0', 'Your soldiers are being used as living shields to stave off artillery fire in the Valley of Echoes, allowing the forces of the Lich King to assault your base without impediment.', '12', '0', '100', '1', '0', '0', '0', '0', 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES ('30377', '8', '0', 'The Lich King knows your boundaries, Highlord. He knows that you will not fire on your own men. Do you not understand? He has no boundaries. No rules to abide. $B', '12', '0', '100', '1', '0', '0', '0', '0', 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES ('30377', '9', '0', 'Then you have lost, Highlord.$B', '12', '0', '100', '1', '0', '0', '0', '0', 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES ('30407', '0', '0', 'Let\'s get out of here! Quickly!', '12', '0', '100', '0', '0', '0', '0', '0', 'Captured Crusader Start');
INSERT INTO `creature_text` VALUES ('30407', '1', '0', 'Faster, hero! We must get back to the Argent Vanguard and warn them of the impending attack!', '12', '0', '100', '0', '0', '0', '0', '0', 'Captured Crusader Middle');
INSERT INTO `creature_text` VALUES ('30407', '1', '1', 'There are hundreds of them! Maybe thousands! They will attack at any moment! I\'m sure of it!', '12', '0', '100', '0', '0', '0', '0', '0', 'Captured Crusader Middle');
INSERT INTO `creature_text` VALUES ('30407', '1', '2', 'Had you not come along I would have died for sure!', '12', '0', '100', '0', '0', '0', '0', '0', 'Captured Crusader Middle');
INSERT INTO `creature_text` VALUES ('30407', '1', '3', 'They\'re planning a huge attack! We need to warn Highlord Fordring!', '12', '0', '100', '2', '0', '0', '0', '0', 'Captured Crusader Middle');
INSERT INTO `creature_text` VALUES ('30407', '2', '0', 'Thank you, hero.', '12', '0', '100', '2', '0', '0', '31130', '0', 'Captured Crusader End');
INSERT INTO `creature_text` VALUES ('30407', '2', '1', 'Thank you, friend, but we\'re not out of trouble yet!', '12', '0', '100', '2', '0', '0', '0', '0', 'Captured Crusader End');
INSERT INTO `creature_text` VALUES ('31237', '0', '0', 'You shouldn\'t have done that!  He\'s not going to like it.', '15', '0', '100', '0', '0', '0', '0', '0', 'Matthias Lehner - On quest accept');
INSERT INTO `creature_text` VALUES ('31397', '0', '0', 'AHAHAHAHA... you\'ll join us soon enough!', '14', '0', '100', '0', '0', '0', '0', '0', 'Saronite Mine Slave');
