INSERT INTO `creature_text` VALUES ('28859', '0', '4', 'I give you one chance. Pledge fealty to me, and perhaps I will not slaughter you for your insolence.', '14', '0', '100', '0', '7000', '14516', '0', '0', 'Malygos - Intro');
INSERT INTO `creature_text` VALUES ('28859', '0', '2', 'What could you hope to accomplish? To storm brazenly into my domain... to employ magic... against ME?', '14', '0', '100', '0', '13000', '14514', '0', '0', 'Malygos - Intro');
INSERT INTO `creature_text` VALUES ('28859', '0', '3', 'I am without limits here. The rules of your cherished reality do not apply. In this realm, I am in control!', '14', '0', '100', '0', '10000', '14515', '0', '0', 'Malygos - Intro');
INSERT INTO `creature_text` VALUES ('28859', '0', '0', 'Lesser beings, intruding here! A shame that your excess courage does not compensate for your stupidity!', '14', '0', '100', '0', '10000', '14512', '0', '0', 'Malygos - Intro');
INSERT INTO `creature_text` VALUES ('28859', '0', '1', 'None but the blue dragonflight are welcome here. Perhaps this is the work of Alexstrasza? Well, then, she has sent you to your deaths!', '14', '0', '100', '0', '11000', '14513', '0', '0', 'Malygos - Intro');
