INSERT INTO `creature_text` VALUES ('35642', '0', '0', 'If you\'ll excuse me, I\'m afraid I have other business I must attend to. Please call on me again in the future.', '12', '0', '100', '3', '0', '0', '0', '0', 'Jeeves');
INSERT INTO `creature_text` VALUES ('36597', '0', '0', 'So the Light\'s vaunted justice has finally arrived? Shall I lay down Frostmourne and throw myself at your mercy, Fordring?', '14', '0', '0', '0', '0', '17349', '0', '0', 'The Lich King - SAY_LK_INTRO_1');
INSERT INTO `creature_text` VALUES ('36597', '1', '0', 'You\'ll learn of that first hand. When my work is complete, you will beg for mercy -- and I will deny you. Your anguished cries will be testament to my unbridled power...', '14', '0', '0', '22', '0', '17350', '0', '0', 'The Lich King - SAY_LK_INTRO_2');
INSERT INTO `creature_text` VALUES ('36597', '2', '0', 'I\'ll keep you alive to witness the end, Fordring. I would not want the Light\'s greatest champion to miss seeing this wretched world remade in my image.', '14', '0', '0', '0', '0', '17351', '0', '0', 'The Lich King - SAY_LK_INTRO_3');
INSERT INTO `creature_text` VALUES ('36597', '3', '0', 'Come then champions, feed me your rage!', '14', '0', '0', '0', '0', '0', '0', '0', 'The Lich King - SAY_LK_AGGRO');
INSERT INTO `creature_text` VALUES ('36597', '4', '0', 'I will freeze you from within until all that remains is an icy husk!', '14', '0', '0', '0', '0', '17369', '0', '0', 'The Lich King - SAY_LK_REMORSELESS_WINTER');
INSERT INTO `creature_text` VALUES ('36597', '5', '0', 'Watch as the world around you collapses!', '14', '0', '0', '0', '0', '17370', '0', '0', 'The Lich King - SAY_LK_QUAKE');
