INSERT INTO `creature_text` VALUES ('36597', '7', '0', 'Frostmourne hungers...', '14', '0', '0', '0', '0', '17366', '0', '0', 'The Lich King - SAY_LK_HARVEST_SOUL');
INSERT INTO `creature_text` VALUES ('36597', '8', '0', 'Argh... Frostmourne, obey me!', '14', '0', '0', '0', '0', '17367', '0', '0', 'The Lich King - SAY_LK_FROSTMOURNE_ESCAPE');
INSERT INTO `creature_text` VALUES ('36597', '9', '0', 'Frostmourne feeds on the soul of your fallen ally!', '14', '0', '0', '0', '0', '17368', '0', '0', 'The Lich King - SAY_LK_FROSTMOURNE_KILL');
