INSERT INTO `creature_text` VALUES ('36597', '14', '0', 'No questions remain unanswered. No doubts linger. You ARE Azeroth\'s greatest champions. You overcame every challenge I laid before you. My mightiest servants have fallen before your relentless onslaught... your unbridled fury...', '14', '0', '0', '0', '0', '17353', '0', '0', 'The Lich King - SAY_LK_OUTRO_1');
INSERT INTO `creature_text` VALUES ('36597', '15', '0', 'Is it truly the righteousness that drives you? I wonder...', '14', '0', '0', '0', '0', '17354', '0', '0', 'The Lich King - SAY_LK_OUTRO_2');
INSERT INTO `creature_text` VALUES ('36597', '16', '0', 'You trained them well, Fordring. You delivered the greatest fighting force this world has ever known... right into my hands -- exactly as I intended. You shall be rewarded for your unwitting sacrifice.', '14', '0', '0', '0', '0', '17355', '0', '0', 'The Lich King - SAY_LK_OUTRO_3');
INSERT INTO `creature_text` VALUES ('36597', '17', '0', 'Watch now as I raise them from the dead to become masters of the Scourge. They will shroud this world in chaos and destruction. Azeroth\'s fall will come at their hands -- and you will be the first to die.', '14', '0', '0', '0', '0', '17356', '0', '0', 'The Lich King - SAY_LK_OUTRO_4');
INSERT INTO `creature_text` VALUES ('36597', '18', '0', 'I delight in the irony...', '14', '0', '0', '0', '0', '17357', '0', '0', 'The Lich King - SAY_LK_OUTRO_5');
