INSERT INTO `creature_text` VALUES ('36669', '0', '0', 'It\'s good to finally see you, courier. Krasus has entrusted us with one of the few remaining copies of Ancient Dragonforged Blades.', '12', '0', '100', '1', '0', '0', '0', '0', 'Arcanist Tybalin');
INSERT INTO `creature_text` VALUES ('36669', '1', '0', 'Here is the book. You must ensure it gets to Caladis Brightspear in Icecrown before the Sunreavers realize what is happening.', '12', '0', '100', '1', '0', '0', '0', '0', 'Arcanist Tybalin');
INSERT INTO `creature_text` VALUES ('36669', '2', '0', 'Travel quickly! The Sunreavers will not waste any time once they realize that we have the information they seek.', '12', '0', '100', '1', '0', '0', '0', '0', 'Arcanist Tybalin');
INSERT INTO `creature_text` VALUES ('36670', '0', '0', 'You\'re late, courier. No, I don\'t want any excuses; this errand is far too important.', '12', '0', '100', '1', '0', '0', '0', '0', 'Magister Hathorel');
INSERT INTO `creature_text` VALUES ('36670', '1', '0', 'Here\'s the tome our representative brought from Wyrmrest. Get this to Myralion Sunblaze immediately.', '12', '0', '100', '1', '0', '0', '0', '0', 'Magister Hathorel');
INSERT INTO `creature_text` VALUES ('36670', '2', '0', 'Watch your back, courier. I needn\'t remind you of the value of the book, nor the fact that the Silver Covenant will stop at nothing to take it from us.', '12', '0', '100', '1', '0', '0', '0', '0', 'Magister Hathorel');
INSERT INTO `creature_text` VALUES ('36678', '0', '0', 'Just an ordinary gas cloud. But watch out, because that\'s no ordinary gas cloud!', '14', '0', '0', '432', '0', '17119', '0', '0', 'Professor Putricide - SAY_GASEOUS_BLIGHT');
INSERT INTO `creature_text` VALUES ('36678', '1', '0', 'Oh, Festergut. You were always my favorite. Next to Rotface. The good news is you left behind so much gas, I can practically taste it!', '14', '0', '0', '0', '0', '17124', '0', '0', 'Professor Putricide - SAY_FESTERGUT_DEATH');
INSERT INTO `creature_text` VALUES ('36678', '2', '0', 'Great news, everyone! The slime is flowing again!', '14', '0', '0', '1', '0', '17126', '0', '0', 'Professor Putricide - SAY_ROTFACE_OOZE_FLOOD1');
INSERT INTO `creature_text` VALUES ('36678', '2', '1', 'Good news, everyone! I\'ve fixed the poison slime pipes!', '14', '0', '0', '1', '0', '17123', '0', '0', 'Professor Putricide - SAY_ROTFACE_OOZE_FLOOD2');
INSERT INTO `creature_text` VALUES ('36678', '3', '0', 'Terrible news, everyone, Rotface is dead! But great news everyone, he left behind plenty of ooze for me to use! Whaa...? I\'m a poet, and I didn\'t know it? Astounding!', '14', '0', '0', '0', '0', '17146', '0', '0', 'Professor Putricide - SAY_ROTFACE_DEATH');
INSERT INTO `creature_text` VALUES ('36678', '4', '0', 'Good news, everyone! I think I\'ve perfected a plague that will destroy all life on Azeroth!', '14', '0', '0', '0', '0', '17114', '0', '0', 'Professor Putricide - SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('36678', '5', '0', '%s begins to cast Unstable Experiment!', '41', '0', '0', '0', '0', '0', '0', '0', 'Professor Putricide - EMOTE_UNSTABLE_EXPERIMENT');
INSERT INTO `creature_text` VALUES ('36678', '6', '0', 'Two oozes, one room! So many delightful possibilities...', '14', '0', '0', '0', '0', '17122', '0', '0', 'Professor Putricide - SAY_PHASE_TRANSITION_HEROIC');
INSERT INTO `creature_text` VALUES ('36678', '7', '0', 'Hmm. I don\'t feel a thing. Whaa...? Where\'d those come from?', '14', '0', '0', '15', '0', '17120', '0', '0', 'Professor Putricide - SAY_TRANSFORM_1');
INSERT INTO `creature_text` VALUES ('36678', '8', '0', 'Tastes like... Cherry! Oh! Excuse me!', '14', '0', '0', '15', '0', '17121', '0', '0', 'Professor Putricide - SAY_TRANSFORM_2');
INSERT INTO `creature_text` VALUES ('36678', '9', '0', '|TInterface\\Icons\\inv_misc_herb_evergreenmoss.blp:16|t%s cast |cFF00FF00Malleable Goo!|r', '41', '0', '0', '0', '0', '0', '0', '0', 'Professor Putricide - EMOTE_MALLEABLE_GOO');
INSERT INTO `creature_text` VALUES ('36678', '10', '0', '%s cast |cFFFF7F00Choking Gas Bomb!|r', '41', '0', '0', '0', '0', '0', '0', '0', 'Professor Putricide - EMOTE_CHOKING_GAS_BOMB');
INSERT INTO `creature_text` VALUES ('36678', '11', '0', 'Hmm... Interesting...', '14', '0', '0', '0', '0', '17115', '0', '0', 'Professor Putricide - SAY_KILL_1');
INSERT INTO `creature_text` VALUES ('36678', '11', '1', 'That was unexpected!', '14', '0', '0', '0', '0', '17116', '0', '0', 'Professor Putricide - SAY_KILL_2');
INSERT INTO `creature_text` VALUES ('36678', '12', '0', 'Great news, everyone!', '14', '0', '0', '0', '0', '17118', '0', '0', 'Professor Putricide - SAY_BERSERK');
INSERT INTO `creature_text` VALUES ('36678', '13', '0', 'Bad news, everyone! I don\'t think I\'m going to make it.', '14', '0', '0', '0', '0', '17117', '0', '0', 'Professor Putricide - SAY_DEATH');
INSERT INTO `creature_text` VALUES ('36774', '0', '0', 'You Sunreaver lackeys are all the same.', '12', '0', '100', '1', '0', '0', '0', '0', 'Silver Covenant Agent');
INSERT INTO `creature_text` VALUES ('36774', '0', '1', 'You won\'t escape from us so easily!', '12', '0', '100', '1', '0', '0', '0', '0', 'Silver Covenant Agent');
INSERT INTO `creature_text` VALUES ('36776', '0', '0', 'Did you think you would escape with your life?', '12', '0', '100', '1', '0', '0', '0', '0', 'Sunreaver Agent');
INSERT INTO `creature_text` VALUES ('36776', '0', '1', 'You\'ll never run us out of the city, Silver Covenant thug!', '12', '0', '100', '1', '0', '0', '0', '0', 'Sunreaver Agent');
INSERT INTO `creature_text` VALUES ('36789', '0', '0', 'Heroes, lend me your aid. I... I cannot hold them off much longer. You must heal my wounds!', '14', '0', '0', '0', '0', '17064', '0', '0', 'Valithria Dreamwalker - SAY_VALITHRIA_ENTER_COMBAT');
INSERT INTO `creature_text` VALUES ('36789', '1', '0', 'I have opened a portal into the Dream. Your salvation lies within, heroes...', '14', '0', '0', '0', '0', '17068', '0', '0', 'Valithria Dreamwalker - SAY_VALITHRIA_DREAM_PORTAL');
INSERT INTO `creature_text` VALUES ('36789', '2', '0', 'My strength is returning! Press on, heroes!', '14', '0', '0', '0', '0', '17070', '0', '0', 'Valithria Dreamwalker - SAY_VALITHRIA_75_PERCENT');
INSERT INTO `creature_text` VALUES ('36789', '3', '0', 'I will not last much longer!', '14', '0', '0', '0', '0', '17069', '0', '0', 'Valithria Dreamwalker - SAY_VALITHRIA_25_PERCENT');
INSERT INTO `creature_text` VALUES ('36789', '4', '0', 'Forgive me for what I do! I... cannot... stop... ONLY NIGHTMARES REMAIN!', '14', '0', '0', '0', '0', '17072', '0', '0', 'Valithria Dreamwalker - SAY_VALITHRIA_DEATH');
INSERT INTO `creature_text` VALUES ('36789', '5', '0', 'A tragic loss...', '14', '0', '0', '0', '0', '17066', '0', '0', 'Valithria Dreamwalker - SAY_VALITHRIA_PLAYER_DEATH');
INSERT INTO `creature_text` VALUES ('36789', '6', '0', 'FAILURES!', '14', '0', '0', '0', '0', '17067', '0', '0', 'Valithria Dreamwalker - SAY_VALITHRIA_BERSERK');
INSERT INTO `creature_text` VALUES ('36789', '7', '0', 'I am renewed! Ysera grants me the favor to lay these foul creatures to rest!', '14', '0', '0', '0', '0', '17071', '0', '0', 'Valithria Dreamwalker - SAY_VALITHRIA_SUCCESS');
INSERT INTO `creature_text` VALUES ('36823', '0', '0', 'You have come to bring Arthas to justice? To see the Lich King destroyed?', '14', '0', '0', '0', '0', '17394', '0', '0', 'Terenas Menethil - SAY_TERENAS_INTRO_1');
INSERT INTO `creature_text` VALUES ('36823', '1', '0', 'First, you must escape Frostmourne\'s hold, or be damned as I am; trapped within this cursed blade for all eternity.', '14', '0', '0', '0', '0', '17395', '0', '0', 'Terenas Menethil - SAY_TERENAS_INTRO_2');
INSERT INTO `creature_text` VALUES ('36823', '2', '0', 'Aid me in destroying these tortured souls! Together we will loosen Frostmourne\'s hold and weaken the Lich King from within!', '14', '0', '0', '0', '0', '17396', '0', '0', 'Terenas Menethil - SAY_TERENAS_INTRO_3');
INSERT INTO `creature_text` VALUES ('36853', '0', '0', 'You are fools to have come to this place! The icy winds of Northrend will consume your souls!', '14', '0', '0', '0', '0', '17007', '0', '0', 'Sindragosa - SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('36853', '1', '0', 'Suffer, mortals, as your pathetic magic betrays you!', '14', '0', '0', '0', '0', '17014', '0', '0', 'Sindragosa - SAY_UNCHAINED_MAGIC');
INSERT INTO `creature_text` VALUES ('36853', '2', '0', '%s prepares to unleash a wave of blistering cold!', '41', '0', '0', '0', '0', '0', '0', '0', 'Sindragosa - EMOTE_WARN_BLISTERING_COLD');
INSERT INTO `creature_text` VALUES ('36853', '3', '0', 'Can you feel the cold hand of death upon your heart?', '14', '0', '0', '0', '0', '17013', '0', '0', 'Sindragosa - SAY_BLISTERING_COLD');
INSERT INTO `creature_text` VALUES ('36853', '4', '0', 'Aaah! It burns! What sorcery is this?!', '14', '0', '0', '0', '0', '17015', '0', '0', 'Sindragosa - SAY_RESPITE_FOR_A_TORMENTED_SOUL');
INSERT INTO `creature_text` VALUES ('36853', '5', '0', 'Your incursion ends here! None shall survive!', '14', '0', '0', '0', '0', '17012', '0', '0', 'Sindragosa - SAY_AIR_PHASE');
INSERT INTO `creature_text` VALUES ('36853', '6', '0', 'Now feel my master\'s limitless power and despair!', '14', '0', '0', '0', '0', '17016', '0', '0', 'Sindragosa - SAY_PHASE_2');
INSERT INTO `creature_text` VALUES ('36853', '7', '0', '%s fires a frozen orb towards $N!', '41', '0', '0', '0', '0', '0', '0', '0', 'Sindragosa - EMOTE_WARN_FROZEN_ORB');
INSERT INTO `creature_text` VALUES ('36853', '8', '0', 'Perish!', '14', '0', '0', '0', '0', '17008', '0', '0', 'Sindragosa - SAY_KILL 1');
INSERT INTO `creature_text` VALUES ('36853', '8', '1', 'A flaw of mortality...', '14', '0', '0', '0', '0', '17009', '0', '0', 'Sindragosa - SAY_KILL 2');
INSERT INTO `creature_text` VALUES ('36853', '9', '0', 'Enough! I tire of these games!', '14', '0', '0', '0', '0', '17011', '0', '0', 'Sindragosa - SAY_BERSERK');
INSERT INTO `creature_text` VALUES ('36853', '10', '0', 'Free...at last...', '14', '0', '0', '0', '0', '17010', '0', '0', 'Sindragosa - SAY_DEATH');
INSERT INTO `creature_text` VALUES ('36855', '0', '0', 'You have found your way here, because you are among the few gifted with true vision in a world cursed with blindness.', '14', '0', '0', '0', '0', '17272', '0', '0', 'Lady Deathwhisper - SAY_INTRO_1');
INSERT INTO `creature_text` VALUES ('36855', '1', '0', 'You can see through the fog that hangs over this world like a shroud, and grasp where true power lies.', '14', '0', '0', '0', '0', '17273', '0', '0', 'Lady Deathwhisper - SAY_INTRO_2');
INSERT INTO `creature_text` VALUES ('36855', '2', '0', 'Fix your eyes upon your crude hands: the sinew, the soft meat, the dark blood coursing within.', '14', '0', '0', '0', '0', '16878', '0', '0', 'Lady Deathwhisper - SAY_INTRO_3');
INSERT INTO `creature_text` VALUES ('36855', '3', '0', 'It is a weakness; a crippling flaw.... A joke played by the Creators upon their own creations.', '14', '0', '0', '0', '0', '17268', '0', '0', 'Lady Deathwhisper - SAY_INTRO_4');
INSERT INTO `creature_text` VALUES ('36855', '4', '0', 'The sooner you come to accept your condition as a defect, the sooner you will find yourselves in a position to transcend it.', '14', '0', '0', '0', '0', '17269', '0', '0', 'Lady Deathwhisper - SAY_INTRO_5');
INSERT INTO `creature_text` VALUES ('36855', '5', '0', 'Through our Master, all things are possible. His power is without limit, and his will unbending.', '14', '0', '0', '0', '0', '17270', '0', '0', 'Lady Deathwhisper - SAY_INTRO_6');
INSERT INTO `creature_text` VALUES ('36855', '6', '0', 'Those who oppose him will be destroyed utterly, and those who serve -- who serve wholly, unquestioningly, with utter devotion of mind and soul -- elevated to heights beyond your ken.', '14', '0', '0', '0', '0', '17271', '0', '0', 'Lady Deathwhisper - SAY_INTRO_7');
INSERT INTO `creature_text` VALUES ('36855', '7', '0', 'What is this disturbance?! You dare trespass upon this hallowed ground? This shall be your final resting place.', '14', '0', '0', '0', '0', '16868', '0', '0', 'Lady Deathwhisper - SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('36855', '8', '0', 'Enough! I see I must take matters into my own hands!', '14', '0', '0', '0', '0', '16877', '0', '0', 'Lady Deathwhisper - SAY_PHASE_2');
INSERT INTO `creature_text` VALUES ('36855', '9', '0', '%s\'s Mana Barrier shimmers and fades away!', '41', '0', '0', '0', '0', '0', '0', '0', 'Lady Deathwhisper - SAY_PHASE_2_EMOTE');
INSERT INTO `creature_text` VALUES ('36855', '10', '0', 'You are weak, powerless to resist my will!', '14', '0', '0', '0', '0', '16876', '0', '0', 'Lady Deathwhisper - SAY_DOMINATE_MIND');
INSERT INTO `creature_text` VALUES ('36855', '11', '0', 'Take this blessing and show these intruders a taste of our master\'s power.', '14', '0', '0', '0', '0', '16873', '0', '0', 'Lady Deathwhisper - SAY_DARK_EMPOWERMENT');
INSERT INTO `creature_text` VALUES ('36855', '12', '0', 'I release you from the curse of flesh!', '14', '0', '0', '0', '0', '16874', '0', '0', 'Lady Deathwhisper - SAY_DARK_TRANSFORMATION');
INSERT INTO `creature_text` VALUES ('36855', '13', '0', 'Arise and exult in your pure form!', '14', '0', '0', '0', '0', '16875', '0', '0', 'Lady Deathwhisper - SAY_ANIMATE_DEAD');
INSERT INTO `creature_text` VALUES ('36855', '14', '0', 'Do you yet grasp of the futility of your actions?', '14', '0', '0', '0', '0', '16869', '0', '0', 'Lady Deathwhisper - SAY_KILL_1');
INSERT INTO `creature_text` VALUES ('36855', '14', '1', 'Embrace the darkness... Darkness eternal!', '14', '0', '0', '0', '0', '16870', '0', '0', 'Lady Deathwhisper - SAY_KILL_2');
INSERT INTO `creature_text` VALUES ('36855', '15', '0', 'This charade has gone on long enough.', '14', '0', '0', '0', '0', '16872', '0', '0', 'Lady Deathwhisper - SAY_BERSERK');
INSERT INTO `creature_text` VALUES ('36855', '16', '0', 'All part of the masters plan! Your end is... inevitable!', '14', '0', '0', '0', '0', '16871', '0', '0', 'Lady Deathwhisper - SAY_DEATH');
INSERT INTO `creature_text` VALUES ('37007', '0', '0', 'The master\'s sanctum has been disturbed!', '14', '0', '0', '0', '0', '16865', '0', '0', 'Deathbound Ward - SAY_TRAP_ACTIVATE');
INSERT INTO `creature_text` VALUES ('37007', '0', '1', 'I... awaken!', '14', '0', '0', '0', '0', '16866', '0', '0', 'Deathbound Ward - SAY_TRAP_ACTIVATE');
INSERT INTO `creature_text` VALUES ('37007', '0', '2', 'Who... goes there...?', '14', '0', '0', '0', '0', '16867', '0', '0', 'Deathbound Ward - SAY_TRAP_ACTIVATE');
INSERT INTO `creature_text` VALUES ('37119', '0', '0', 'This is our final stand. What happens here will echo through the ages. Regardless of outcome, they will know that we fought with honor. That we fought for the freedom and safety of our people!', '14', '0', '0', '22', '0', '16653', '0', '0', 'Highlord Tirion Fordring - SAY_TIRION_INTRO_1');
INSERT INTO `creature_text` VALUES ('37119', '1', '0', 'Remember, heroes, fear is your greatest enemy in these befouled halls. Steel your heart and your soul will shine brighter than a thousand suns. The enemy will falter at the sight of you. They will fall as the light of righteousness envelops them!', '14', '0', '0', '22', '0', '16654', '0', '0', 'Highlord Tirion Fordring - SAY_TIRION_INTRO_2');
INSERT INTO `creature_text` VALUES ('37119', '2', '0', 'Our march upon Icecrown Citadel begins now!', '14', '0', '0', '22', '0', '16655', '0', '0', 'Highlord Tirion Fordring - SAY_TIRION_INTRO_3');
INSERT INTO `creature_text` VALUES ('37119', '3', '0', 'ARTHAS! I swore that I would see you dead and the Scourge dismantled! I\'m going to finish what I started at Light\'s Hope!', '14', '0', '0', '22', '0', '16656', '0', '0', 'Highlord Tirion Fordring - SAY_TIRION_INTRO_4');
INSERT INTO `creature_text` VALUES ('37119', '4', '0', 'The power of the Light knows no bounds, Saurfang. His soul is under great strain, but he lives - for now.', '12', '0', '0', '1', '0', '16658', '0', '0', 'Highlord Tirion Fordring - SAY_TIRION_INTRO_5');
INSERT INTO `creature_text` VALUES ('37119', '5', '0', 'By the Light, it must be so!', '12', '0', '0', '5', '0', '16657', '0', '0', 'Highlord Tirion Fordring - SAY_TIRION_INTRO_A_5');
INSERT INTO `creature_text` VALUES ('37122', '0', '0', 'Never... could reach... the top shelf...', '14', '0', '0', '0', '0', '16586', '0', '0', 'Captain Arnath - SAY_ARNATH_DEATH');
INSERT INTO `creature_text` VALUES ('37122', '1', '0', 'You miserable fools never did manage to select a decent bat wing.', '14', '0', '0', '0', '0', '16587', '0', '0', 'Captain Arnath - SAY_ARNATH_RESURRECTED');
INSERT INTO `creature_text` VALUES ('37122', '2', '0', 'THAT was for bringing me spoiled spider ichor!', '14', '0', '0', '0', '0', '16588', '0', '0', 'Captain Arnath - SAY_ARNATH_KILL');
INSERT INTO `creature_text` VALUES ('37122', '3', '0', 'Don\'t... let Finklestein use me... for his potions...', '14', '0', '0', '0', '0', '16589', '0', '0', 'Captain Arnath - SAY_ARNATH_SECOND_DEATH');
INSERT INTO `creature_text` VALUES ('37122', '4', '0', 'The loss of our comrades was unpreventable. They lived and died in the service of the Argent Crusade.', '14', '0', '0', '0', '0', '16590', '0', '0', 'Captain Arnath - SAY_ARNATH_SURVIVE_TALK');
INSERT INTO `creature_text` VALUES ('37122', '5', '0', 'Even dying here beats spending another day collecting reagents for that madman, Finklestein.', '14', '0', '0', '0', '0', '16585', '0', '0', 'Captain Arnath - SAY_ARNATH_INTRO_2');
INSERT INTO `creature_text` VALUES ('37123', '0', '0', 'No amount of healing can save me now. Fight on, brothers...', '14', '0', '0', '0', '0', '16810', '0', '0', 'Captain Brandon - SAY_BRANDON_DEATH');
INSERT INTO `creature_text` VALUES ('37123', '1', '0', 'What? This strength...? All of the pain is gone! You... must join me in the eternal embrace of death!', '14', '0', '0', '0', '0', '16811', '0', '0', 'Captain Brandon - SAY_BRANDON_RESURRECTED');
INSERT INTO `creature_text` VALUES ('37123', '2', '0', 'It doesn\'t hurt anymore, does it?', '14', '0', '0', '0', '0', '16812', '0', '0', 'Captain Brandon - SAY_BRANDON_KILL');
INSERT INTO `creature_text` VALUES ('37123', '3', '0', 'I\'m sorry...', '14', '0', '0', '0', '0', '16813', '0', '0', 'Captain Brandon - SAY_BRANDON_SECOND_DEATH');
INSERT INTO `creature_text` VALUES ('37123', '4', '0', 'You have done much in this war against the Scourge. May the light embrace you.', '14', '0', '0', '0', '0', '16815', '0', '0', 'Captain Brandon - SAY_BRANDON_SURVIVE_TALK');
INSERT INTO `creature_text` VALUES ('37124', '0', '0', 'Please... burn my remains. Let me live warm in the afterlife...', '14', '0', '0', '0', '0', '16844', '0', '0', 'Captain Grondel - SAY_GRONDEL_DEATH');
INSERT INTO `creature_text` VALUES ('37124', '1', '0', 'No! Why was I denied a death by flame? You must all BURN!', '14', '0', '0', '0', '0', '16845', '0', '0', 'Captain Grondel - SAY_GRONDEL_RESURRECTED');
INSERT INTO `creature_text` VALUES ('37124', '2', '0', 'Can you feel the burn?', '14', '0', '0', '0', '0', '16846', '0', '0', 'Captain Grondel - SAY_GRONDEL_KILL');
INSERT INTO `creature_text` VALUES ('37124', '3', '0', 'What... have I done? No!', '14', '0', '0', '0', '0', '16847', '0', '0', 'Captain Grondel - SAY_GRONDEL_SECOND_DEATH');
INSERT INTO `creature_text` VALUES ('37124', '4', '0', 'What can possibly redeem this unholy place? Thank you...', '14', '0', '0', '0', '0', '16849', '0', '0', 'Captain Grondel - SAY_GRONDEL_SURVIVE_TALK');
INSERT INTO `creature_text` VALUES ('37125', '0', '0', 'It was... a worthy afterlife.', '14', '0', '0', '0', '0', '16998', '0', '0', 'Captain Rupert - SAY_RUPERT_DEATH');
INSERT INTO `creature_text` VALUES ('37125', '1', '0', 'There is no escaping the Lich King\'s will. Prepare for an explosive encounter!', '14', '0', '0', '0', '0', '16999', '0', '0', 'Captain Rupert - SAY_RUPERT_RESURRECTED');
INSERT INTO `creature_text` VALUES ('37125', '2', '0', 'So that\'s what happens when you stand too close to a bomb!', '14', '0', '0', '0', '0', '17000', '0', '0', 'Captain Rupert - SAY_RUPERT_KILL');
INSERT INTO `creature_text` VALUES ('37125', '3', '0', 'What an... explosive ending!', '14', '0', '0', '0', '0', '17001', '0', '0', 'Captain Rupert - SAY_RUPERT_SECOND_DEATH');
INSERT INTO `creature_text` VALUES ('37125', '4', '0', 'Beware the dangers that lie ahead... and do try to remain in one piece.', '14', '0', '0', '0', '0', '17003', '0', '0', 'Captain Rupert - SAY_RUPERT_SURVIVE_TALK');
INSERT INTO `creature_text` VALUES ('37126', '0', '0', 'You may have once fought beside me, Crok, but now you are nothing more than a traitor. Come, your second death approaches!', '14', '0', '0', '0', '0', '17017', '0', '0', 'Sister Svalna - SAY_EVENT_START');
INSERT INTO `creature_text` VALUES ('37126', '1', '0', 'Miserable creatures! Die!', '14', '0', '0', '0', '0', '17018', '0', '0', 'Sister Svalna - SAY_KILL_CAPTAIN');
INSERT INTO `creature_text` VALUES ('37126', '2', '0', 'Foolish Crok. You brought my reinforcements with you. Arise, Argent Champions, and serve the Lich King in death!', '14', '0', '0', '0', '0', '17019', '0', '0', 'Sister Svanlna - SAY_RESURRECT_CAPTAINS');
INSERT INTO `creature_text` VALUES ('37126', '3', '0', 'Come, Scourgebane. I\'ll show the master which of us is truly worthy of the title of \"Champion\"!', '14', '0', '0', '0', '0', '17020', '0', '0', 'Sister Svalna - SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('37126', '4', '0', 'What a pitiful choice of an ally, Crok!', '14', '0', '0', '0', '0', '17021', '0', '0', 'Sister Svalna - SAY_KILL');
INSERT INTO `creature_text` VALUES ('37126', '5', '0', 'What? They died so easily? No matter.', '14', '0', '0', '0', '0', '17022', '0', '0', 'Sister Svalna - SAY_CAPTAIN_DEATH');
INSERT INTO `creature_text` VALUES ('37126', '6', '0', 'Perhaps... you were right, Crok.', '14', '0', '0', '0', '0', '17023', '0', '0', 'Sister Svalna - SAY_DEATH');
INSERT INTO `creature_text` VALUES ('37126', '7', '0', '%s has impaled $N!', '41', '0', '0', '0', '0', '0', '0', '0', 'Sister Svalna - EMOTE_SVALNA_IMPALE');
INSERT INTO `creature_text` VALUES ('37126', '8', '0', '%s\'s Aether Shield has been shattered by $N!', '41', '0', '0', '0', '0', '0', '0', '0', 'Sister Svalna - EMOTE_SVALNA_BROKEN_SHIELD');
INSERT INTO `creature_text` VALUES ('37129', '0', '0', 'Ready your arms, my Argent Brothers. The Vrykul will protect the Frost Queen with their lives.', '14', '0', '0', '0', '0', '16819', '0', '0', 'Crok Scourgebane - SAY_CROK_INTRO_1');
INSERT INTO `creature_text` VALUES ('37129', '1', '0', 'Enough idle banter! Our champions have arrived - support them as we push our way through the hall!', '14', '0', '0', '0', '0', '16820', '0', '0', 'Crok Scourgebane - SAY_CROK_INTRO_3');
INSERT INTO `creature_text` VALUES ('37129', '2', '0', 'Draw them back to us, and we\'ll assist you.', '14', '0', '0', '0', '0', '16821', '0', '0', 'Crok Scourgebane - SAY_CROK_COMBAT_WP_0');
INSERT INTO `creature_text` VALUES ('37129', '3', '0', 'Quickly, push on!', '14', '0', '0', '0', '0', '16823', '0', '0', 'Crok Scourgebane - SAY_CROK_COMBAT_WP_1');
INSERT INTO `creature_text` VALUES ('37129', '4', '0', 'Her reinforcements will arrive shortly, we must bring her down quickly!', '14', '0', '0', '0', '0', '16824', '0', '0', 'Crok Scourgebane - SAY_CROK_FINAL_WP');
INSERT INTO `creature_text` VALUES ('37129', '5', '0', 'I\'ll draw her attacks. Return our brothers to their graves, then help me bring her down!', '14', '0', '0', '15', '0', '16825', '0', '0', 'Crok Scourgebane - SAY_CROK_COMBAT_SVALNA');
INSERT INTO `creature_text` VALUES ('37129', '6', '0', 'I must rest for a moment', '14', '0', '0', '0', '0', '16826', '0', '0', 'Crok Scourgebane - SAY_CROK_WEAKENING_GAUNTLET');
INSERT INTO `creature_text` VALUES ('37129', '7', '0', 'Champions, I cannot hold her back any longer!', '14', '0', '0', '0', '0', '16827', '0', '0', 'Crok Scourgebane - SAY_CROK_WEAKENING_SVALNA');
INSERT INTO `creature_text` VALUES ('37129', '8', '0', 'Vengeance alone... was not enough!', '14', '0', '0', '0', '0', '16828', '0', '0', 'Crok Scourgebane - SAY_CROK_DEATH');
INSERT INTO `creature_text` VALUES ('37181', '0', '0', 'You now stand upon the hallowed ground of the Scourge. The Light won\'t protect you here, paladin. Nothing will protect you...', '14', '0', '0', '0', '0', '17230', '0', '0', 'The Lich King - SAY_LK_INTRO_1');
INSERT INTO `creature_text` VALUES ('37181', '1', '0', 'You could have been my greatest champion, Fordring: A force of darkness that would wash over this world and deliver it into a new age of strife.', '14', '0', '0', '0', '0', '17231', '0', '0', 'The Lich King - SAY_LK_INTRO_2');
INSERT INTO `creature_text` VALUES ('37181', '2', '0', 'But that honor is no longer yours. Soon, I will have a new champion.', '14', '0', '0', '0', '0', '17232', '0', '0', 'The Lich King - SAY_LK_INTRO_3');
INSERT INTO `creature_text` VALUES ('37181', '3', '0', 'The breaking of this one has been taxing. The atrocities I have committed upon his soul. He has resisted for so long, but he will bow down before his king soon.', '14', '0', '0', '0', '0', '17233', '0', '0', 'The Lich King - SAY_LK_INTRO_4');
INSERT INTO `creature_text` VALUES ('37181', '4', '0', 'In the end, you will all serve me.', '14', '0', '0', '0', '0', '17234', '0', '0', 'The Lich King - SAY_LK_INTRO_5');
INSERT INTO `creature_text` VALUES ('37183', '0', '0', 'NEVER! I... I will never... serve... you...', '14', '0', '0', '0', '0', '17078', '0', '0', 'Highlord Bolvar Fordragon - SAY_BOLVAR_INTRO_1');
INSERT INTO `creature_text` VALUES ('37187', '0', '0', 'Kor\'kron, move out! Champions, watch your backs. The Scourge have been...', '14', '0', '0', '22', '0', '17103', '0', '0', 'High Overlord Saurfang - SAY_INTRO_HORDE_1');
INSERT INTO `creature_text` VALUES ('37187', '1', '0', 'My boy died at the Wrath Gate. I am here only to collect his body.', '12', '0', '0', '397', '0', '17097', '0', '0', 'High Overlord Saurfang - SAY_INTRO_HORDE_3');
INSERT INTO `creature_text` VALUES ('37187', '2', '0', 'We named him Dranosh. It means \"Heart of Draenor\" in orcish. I would not let the warlocks take him. My boy would be safe, hidden away by the elders of Garadar.', '12', '0', '0', '1', '0', '17098', '0', '0', 'High Overlord Saurfang - SAY_INTRO_HORDE_5');
INSERT INTO `creature_text` VALUES ('37187', '3', '0', 'I made a promise to his mother before she died; that I would cross the Dark Portal alone - whether I lived or died, my son would be safe. Untainted...', '12', '0', '0', '1', '0', '17099', '0', '0', 'High Overlord Saurfang - SAY_INTRO_HORDE_6');
INSERT INTO `creature_text` VALUES ('37187', '4', '0', 'Today, I fulfill that promise.', '12', '0', '0', '397', '0', '17100', '0', '0', 'High Overlord Saurfang - SAY_INTRO_HORDE_7');
INSERT INTO `creature_text` VALUES ('37187', '5', '0', 'High Overlord Saurfang charges!', '16', '0', '0', '53', '0', '17104', '0', '0', 'High Overlord Saurfang - SAY_INTRO_HORDE_8');
INSERT INTO `creature_text` VALUES ('37187', '6', '0', 'Behind you lies the body of my only son. Nothing will keep me from him.', '12', '0', '0', '0', '0', '17094', '0', '0', 'High Overlord Saurfang - SAY_OUTRO_ALLIANCE_8');
INSERT INTO `creature_text` VALUES ('37187', '7', '0', 'High Overlord Saurfang walks over to his son and kneels before his son\'s body.', '16', '0', '0', '0', '0', '0', '0', '0', 'High Overlord Saurfang - SAY_OUTRO_ALLIANCE_12');
INSERT INTO `creature_text` VALUES ('37187', '8', '0', '[Orcish] No\'ku kil zil\'nok ha tar.', '12', '0', '0', '0', '0', '17096', '0', '0', 'High Overlord Saurfang - SAY_OUTRO_ALLIANCE_13');
INSERT INTO `creature_text` VALUES ('37187', '9', '0', 'Higher Overlord Saurfang picks up the body of his son and walks over towards Varian', '16', '0', '0', '0', '0', '0', '0', '0', 'High Overlord Saurfang - SAY_OUTRO_ALLIANCE_14');
INSERT INTO `creature_text` VALUES ('37187', '10', '0', 'I will not forget this... kindness. I thank you, Highness', '12', '0', '0', '0', '0', '17095', '0', '0', 'High Overlord Saurfang - SAY_OUTRO_ALLIANCE_15');
INSERT INTO `creature_text` VALUES ('37187', '11', '0', '%s coughs.', '16', '0', '0', '0', '0', '17105', '37646', '0', 'High Overlord Saurfang - SAY_OUTRO_HORDE_1');
INSERT INTO `creature_text` VALUES ('37187', '12', '0', '%s weeps over the corpse of his son.', '16', '0', '0', '15', '0', '17106', '0', '0', 'High Overlord Saurfang - SAY_OUTRO_HORDE_2');
INSERT INTO `creature_text` VALUES ('37187', '13', '0', 'You will have a proper ceremony in Nagrand next to the pyres of your mother and ancestors.', '12', '0', '0', '0', '0', '17101', '0', '0', 'High Overlord Saurfang - SAY_OUTRO_HORDE_3');
INSERT INTO `creature_text` VALUES ('37187', '14', '0', 'Honor, young heroes... no matter how dire the battle... Never forsake it!', '12', '0', '0', '0', '0', '17102', '0', '0', 'High Overlord Saurfang - SAY_OUTRO_HORDE_4');
INSERT INTO `creature_text` VALUES ('37187', '15', '0', 'The paladin still lives? Is it possible, Highlord? Could he have survived?', '12', '0', '0', '6', '0', '17107', '0', '0', 'High Overlord Saurfang - SAY_SAURFANG_INTRO_1');
INSERT INTO `creature_text` VALUES ('37187', '16', '0', 'Then we must save him! If we rescue Bolvar Fordragon, we may quell the unrest between the Alliance and the Horde.', '12', '0', '0', '5', '0', '17108', '0', '0', 'High Overlord Saurfang - SAY_SAURFANG_INTRO_2');
INSERT INTO `creature_text` VALUES ('37187', '17', '0', 'Our mission is now clear: The Lich King will answer for his crimes and we will save Highlord Bolvar Fordragon!', '12', '0', '0', '15', '0', '17109', '0', '0', 'High Overlord Saurfang - SAY_SAURFANG_INTRO_3');
INSERT INTO `creature_text` VALUES ('37187', '18', '0', 'Kor\'kron, prepare Orgrim\'s Hammer for its final voyage! Champions, our gunship will find a point to dock on the upper reaches of the citadel. Meet us there!', '14', '0', '0', '22', '0', '17110', '0', '0', 'High Overlord Saurfang - SAY_SAURFANG_INTRO_4');
INSERT INTO `creature_text` VALUES ('37188', '0', '0', '%s cries.', '16', '0', '0', '18', '0', '16651', '29772', '0', 'Lady Jaina Proudmoore - SAY_OUTRO_ALLIANCE_17');
INSERT INTO `creature_text` VALUES ('37188', '1', '0', 'It was nothing, your majesty. Just... I\'m proud of my king.', '12', '0', '0', '0', '0', '16652', '0', '0', 'Lady Jaina Proudmoore - SAY_OUTRO_ALLIANCE_19');
INSERT INTO `creature_text` VALUES ('37200', '0', '0', 'Let\'s get a move on then! Move ou...', '14', '0', '0', '0', '0', '16974', '0', '0', 'Muradin Bronzebeard - SAY_INTRO_ALLIANCE_1');
INSERT INTO `creature_text` VALUES ('37200', '1', '0', 'A lone orc against the might of the Alliance???', '14', '0', '0', '0', '0', '16970', '0', '0', 'Muradin Bronzebeard - SAY_INTRO_ALLIANCE_4');
INSERT INTO `creature_text` VALUES ('37200', '2', '0', 'Charge!!!', '14', '0', '0', '0', '0', '16971', '0', '0', 'Muradin Bronzebeard - SAY_INTRO_ALLIANCE_5');
INSERT INTO `creature_text` VALUES ('37200', '3', '0', '%s gasps for air.', '16', '0', '0', '0', '0', '16975', '37502', '0', 'Muradin Bronzebeard - SAY_OUTRO_ALLIANCE_1');
INSERT INTO `creature_text` VALUES ('37200', '4', '0', 'That was Saurfang\'s boy - the Horde commander at the Wrath Gate. Such a tragic end...', '12', '0', '0', '0', '0', '16976', '0', '0', 'Muradin Bronzebeard - SAY_OUTRO_ALLIANCE_2');
INSERT INTO `creature_text` VALUES ('37200', '5', '0', 'What in the... There, in the distance!', '12', '0', '0', '0', '0', '16977', '0', '0', 'Muradin Bronzebeard - SAY_OUTRO_ALLIANCE_3');
INSERT INTO `creature_text` VALUES ('37200', '6', '0', 'A Horde Zeppelin flies up to the rise.', '16', '0', '0', '0', '0', '0', '0', '0', 'Muradin Bronzebeard - SAY_OUTRO_ALLIANCE_4');
INSERT INTO `creature_text` VALUES ('37200', '7', '0', 'Soldiers, fall in! Looks like the Horde are comin\' to take another shot!', '14', '0', '0', '0', '0', '16978', '0', '0', 'Muradin Bronzebeard - SAY_OUTRO_ALLIANCE_5');
INSERT INTO `creature_text` VALUES ('37200', '8', '0', 'The Zeppelin docks, and High Overlord Saurfang hops out, confronting the Alliance soldiers and Muradin', '16', '0', '0', '0', '0', '0', '0', '0', 'Muradin Bronzebeard - SAY_OUTRO_ALLIANCE_6');
INSERT INTO `creature_text` VALUES ('37200', '9', '0', 'Don\'t force me hand, orc. We can\'t let ye pass.', '12', '0', '0', '0', '0', '16972', '0', '0', 'Muradin Bronzebeard - SAY_OUTRO_ALLIANCE_7');
INSERT INTO `creature_text` VALUES ('37200', '10', '0', 'I... I can\'t do it. Get back on yer ship and we\'ll spare yer life.', '12', '0', '0', '0', '0', '16973', '0', '0', 'Muradin Bronzebeard - SAY_OUTRO_ALLIANCE_9');
INSERT INTO `creature_text` VALUES ('37200', '11', '0', 'A mage portal from Stormwind appears between the two and Varian Wrynn and Jaina Proudmoore emerge.', '16', '0', '0', '0', '0', '0', '0', '0', 'Muradin Bronzebeard - SAY_OUTRO_ALLIANCE_10');
INSERT INTO `creature_text` VALUES ('37200', '12', '0', 'Right away, yer majesty!', '12', '0', '0', '0', '0', '16979', '0', '0', 'Muradin Bronzebeard - SAY_OUTRO_ALLIANCE_21');
INSERT INTO `creature_text` VALUES ('37200', '13', '0', 'Could it be, Lord Fordring? If Bolvar lives, mayhap there is hope fer peace between the Alliance and the Horde. We must reach the top o\' this cursed place and free the paladin!', '12', '0', '0', '6', '0', '16980', '0', '0', 'Muradin Bronzebeard - SAY_SAURFANG_INTRO_2');
INSERT INTO `creature_text` VALUES ('37200', '14', '0', 'Prepare the Skybreaker fer an aerial assault on the citadel!', '14', '0', '0', '5', '0', '16981', '0', '0', 'Muradin Bronzebeard - SAY_SAURFANG_INTRO_3');
INSERT INTO `creature_text` VALUES ('37200', '15', '0', 'Heroes, ye must fight yer way to a clear extraction point within Icecrown. We\'ll try an\' rendezvous on the ramparts!', '14', '0', '0', '22', '0', '16982', '0', '0', 'Muradin Bronzebeard - SAY_SAURFANG_INTRO_4');
INSERT INTO `creature_text` VALUES ('37217', '0', '0', '%s cries out with a loud, baying howl!', '41', '0', '0', '0', '0', '0', '0', '0', 'Precious - EMOTE_PRECIOUS_ZOMBIES');
INSERT INTO `creature_text` VALUES ('37491', '1', '0', 'You miserable fools never did manage to select a decent bat wing.', '14', '0', '0', '0', '0', '16587', '0', '0', 'Captain Arnath - SAY_ARNATH_RESURRECTED');
INSERT INTO `creature_text` VALUES ('37491', '2', '0', 'THAT was for bringing me spoiled spider ichor!', '14', '0', '0', '0', '0', '16588', '0', '0', 'Captain Arnath - SAY_ARNATH_KILL');
INSERT INTO `creature_text` VALUES ('37491', '3', '0', 'Don\'t... let Finklestein use me... for his potions...', '14', '0', '0', '0', '0', '16589', '0', '0', 'Captain Arnath - SAY_ARNATH_SECOND_DEATH');
INSERT INTO `creature_text` VALUES ('37493', '1', '0', 'What? This strength...? All of the pain is gone! You... must join me in the eternal embrace of death!', '14', '0', '0', '0', '0', '16811', '0', '0', 'Captain Brandon - SAY_BRANDON_RESURRECTED');
INSERT INTO `creature_text` VALUES ('37493', '2', '0', 'It doesn\'t hurt anymore, does it?', '14', '0', '0', '0', '0', '16812', '0', '0', 'Captain Brandon - SAY_BRANDON_KILL');
INSERT INTO `creature_text` VALUES ('37493', '3', '0', 'I\'m sorry...', '14', '0', '0', '0', '0', '16813', '0', '0', 'Captain Brandon - SAY_BRANDON_SECOND_DEATH');
INSERT INTO `creature_text` VALUES ('37494', '1', '0', 'No! Why was I denied a death by flame? You must all BURN!', '14', '0', '0', '0', '0', '16845', '0', '0', 'Captain Grondel - SAY_GRONDEL_RESURRECTED');
INSERT INTO `creature_text` VALUES ('37494', '2', '0', 'Can you feel the burn?', '14', '0', '0', '0', '0', '16846', '0', '0', 'Captain Grondel - SAY_GRONDEL_KILL');
INSERT INTO `creature_text` VALUES ('37494', '3', '0', 'What... have I done? No!', '14', '0', '0', '0', '0', '16847', '0', '0', 'Captain Grondel - SAY_GRONDEL_SECOND_DEATH');
INSERT INTO `creature_text` VALUES ('37495', '1', '0', 'There is no escaping the Lich King\'s will. Prepare for an explosive encounter!', '14', '0', '0', '0', '0', '16999', '0', '0', 'Captain Rupert - SAY_RUPERT_RESURRECTED');
INSERT INTO `creature_text` VALUES ('37495', '2', '0', 'So that\'s what happens when you stand too close to a bomb!', '14', '0', '0', '0', '0', '17000', '0', '0', 'Captain Rupert - SAY_RUPERT_KILL');
INSERT INTO `creature_text` VALUES ('37495', '3', '0', 'What an... explosive ending!', '14', '0', '0', '0', '0', '17001', '0', '0', 'Captain Rupert - SAY_RUPERT_SECOND_DEATH');
INSERT INTO `creature_text` VALUES ('37813', '0', '0', 'For every Horde soldier that you killed -- for every Alliance dog that fell, the Lich King\'s armies grew. Even now the val\'kyr work to raise your fallen as Scourge.', '14', '0', '0', '0', '0', '16701', '0', '0', 'Deathbringer Saurfang - SAY_INTRO_ALLIANCE_2');
INSERT INTO `creature_text` VALUES ('37813', '1', '0', 'Things are about to get much worse. Come, taste the power that the Lich King has bestowed upon me!', '14', '0', '0', '0', '0', '16702', '0', '0', 'Deathbringer Saurfang - SAY_INTRO_ALLIANCE_3');
INSERT INTO `creature_text` VALUES ('37813', '2', '0', 'Dwarves...', '14', '0', '0', '0', '0', '16703', '0', '0', 'Deathbringer Saurfang - SAY_INTRO_ALLIANCE_6');
INSERT INTO `creature_text` VALUES ('37813', '3', '0', '%s immobilizes Muradin and his guards.', '16', '0', '0', '0', '0', '0', '0', '0', 'Deathbringer Saurfang - SAY_INTRO_ALLIANCE_7');
INSERT INTO `creature_text` VALUES ('37813', '4', '0', 'Join me, father. Join me and we will crush this world in the name of the Scourge -- for the glory of the Lich King!', '14', '0', '0', '0', '0', '16704', '0', '0', 'Deathbringer Saurfang - SAY_INTRO_HORDE_2');
