INSERT INTO `creature_text` VALUES ('29308', '3', '2', 'One final embrace.', '14', '0', '100', '0', '0', '14367', '0', '0', 'prince taldaram SAY_SLAY_2');
INSERT INTO `creature_text` VALUES ('29308', '4', '0', 'Still I hunger. Still... I... thirst.', '14', '0', '100', '0', '0', '14368', '0', '0', 'prince taldaram SAY_DEATH');
