INSERT INTO `creature_text` VALUES ('29308', '5', '1', 'Fresh, warm blood. It has been too long.', '14', '0', '100', '0', '0', '14364', '0', '0', 'prince taldaram SAY_FEED2');
INSERT INTO `creature_text` VALUES ('29308', '6', '0', 'Your heartbeat is... music to my ears.', '14', '0', '100', '0', '0', '14361', '0', '0', 'prince taldaram SAY_VANISH1');
INSERT INTO `creature_text` VALUES ('29308', '6', '1', 'I am nowhere... I am everywhere. I am the watcher, unseen.', '14', '0', '100', '0', '0', '14362', '0', '0', 'prince taldaram SAY_VANISH2');
INSERT INTO `creature_text` VALUES ('34064', '0', '0', 'We did it, lads! We got here before Algalon\'s arrival. Maybe we can rig the systems to interfere with his analysis--', '14', '0', '0', '0', '0', '15824', '0', '0', 'Brann Bronzebeard - SAY_BRANN_ALGALON_INTRO_1');
INSERT INTO `creature_text` VALUES ('34064', '1', '0', 'I\'ll head back to the Archivum and see if I can jam his signal. I might be able to buy us some time while you take care of him.', '12', '0', '0', '0', '0', '15825', '0', '0', 'Brann Bronzebeard - SAY_BRANN_ALGALON_INTRO_2');
INSERT INTO `creature_text` VALUES ('34064', '2', '0', 'I know just the place. Will you be all right?', '14', '0', '0', '6', '0', '15823', '0', '0', 'Brann Bronzebeard - SAY_BRANN_ALGALON_OUTRO');
INSERT INTO `creature_text` VALUES ('32871', '0', '0', 'Translocation complete. Commencing planetary analysis of Azeroth.', '12', '0', '0', '0', '0', '15405', '0', '0', 'Algalon the Observer - SAY_ALGALON_INTRO_1');
INSERT INTO `creature_text` VALUES ('32871', '1', '0', 'Stand back, mortals. I\'m not here to fight you.', '12', '0', '0', '0', '0', '15406', '0', '0', 'Algalon the Observer - SAY_ALGALON_INTRO_2');
INSERT INTO `creature_text` VALUES ('32871', '2', '0', 'It is in the universe\'s best interest to re-originate this planet should my analysis find systemic corruption. Do not interfere.', '12', '0', '0', '0', '0', '15407', '0', '0', 'Algalon the Observer - SAY_ALGALON_INTRO_3');
INSERT INTO `creature_text` VALUES ('32871', '3', '0', 'Your actions are illogical. All possible results for this encounter have been calculated. The Pantheon will receive the Observer\'s message regardless of outcome.', '14', '0', '0', '0', '0', '15386', '0', '0', 'Algalon the Observer - SAY_ALGALON_START_TIMER');
INSERT INTO `creature_text` VALUES ('32871', '4', '0', 'See your world through my eyes: A universe so vast as to be immeasurable - incomprehensible even to your greatest minds.', '14', '0', '0', '1', '0', '15390', '0', '0', 'Algalon the Observer - SAY_ALGALON_AGGRO');
INSERT INTO `creature_text` VALUES ('32871', '5', '0', 'The stars come to my aid!', '14', '0', '0', '0', '0', '15392', '0', '0', 'Algalon the Observer - SAY_ALGALON_COLLAPSING_STAR');
INSERT INTO `creature_text` VALUES ('32871', '6', '0', '%s begins to Summon Collapsing Stars!', '41', '0', '0', '0', '0', '0', '0', '0', 'Algalon the Observer - EMOTE_ALGALON_COLLAPSING_STAR');
INSERT INTO `creature_text` VALUES ('32871', '7', '0', 'Witness the fury of the cosmos!', '14', '0', '0', '0', '0', '15396', '0', '0', 'Algalon the Observer - SAY_ALGALONG_BIG_BANG');
INSERT INTO `creature_text` VALUES ('32871', '8', '0', '%s begins to cast Big Bang!', '41', '0', '0', '0', '0', '0', '0', '0', 'Algalon the Observer - EMOTE_ALGALON_BIG_BANG');
INSERT INTO `creature_text` VALUES ('32871', '9', '0', 'You are out of time.', '14', '0', '0', '0', '0', '15394', '34001', '0', 'Algalon the Observer - SAY_ALGALON_ASCEND');
INSERT INTO `creature_text` VALUES ('32871', '10', '0', '%s begins to cast Cosmic Smash!', '41', '0', '0', '0', '0', '0', '0', '0', 'Algalon the Observer - EMOTE_ALGALON_COSMIC_SMASH');
INSERT INTO `creature_text` VALUES ('32871', '11', '0', 'Behold the tools of creation!', '14', '0', '0', '0', '0', '15397', '0', '0', 'Algalon the Observer - SAY_ALGALON_PHASE_TWO');
INSERT INTO `creature_text` VALUES ('32871', '12', '0', 'I have seen worlds bathed in the Makers\' flames, their denizens fading without as much as a whimper. Entire planetary systems born and razed in the time that it takes your mortal hearts to beat once. Yet all throughout, my own heart devoid of emotion... of empathy. I. Have. Felt. Nothing. A million-million lives wasted. Had they all held within them your tenacity? Had they all loved life as you do?', '14', '0', '0', '1', '0', '15393', '0', '0', 'Algalon the Observer - SAY_ALGALON_OUTRO_1');
INSERT INTO `creature_text` VALUES ('32871', '13', '0', 'Perhaps it is your imperfections... that which grants you free will... that allows you to persevere against all cosmically calculated odds. You prevail where the Titan\'s own perfect creations have failed.', '14', '0', '0', '1', '0', '15401', '0', '0', 'Algalon the Observer - SAY_ALGALON_OUTRO_2');
INSERT INTO `creature_text` VALUES ('32871', '14', '0', 'I\'ve rearranged the reply code - your planet will be spared. I cannot be certain of my own calculations anymore.', '14', '0', '0', '1', '0', '15402', '0', '0', 'Algalon the Observer - SAY_ALGALON_OUTRO_3');
INSERT INTO `creature_text` VALUES ('32871', '15', '0', 'I lack the strength to transmit the signal. You must... hurry... find a place of power... close to the skies.', '14', '0', '0', '1', '0', '15403', '0', '0', 'Algalon the Observer - SAY_ALGALON_OUTRO_4');
INSERT INTO `creature_text` VALUES ('32871', '16', '0', 'Do not worry about my fate, Bronzen. If the signal is not transmitted in time, re-origination will proceed regardless. Save... your world...', '14', '0', '0', '1', '0', '15404', '0', '0', 'Algalon the Observer - SAY_ALGALON_OUTRO_5');
INSERT INTO `creature_text` VALUES ('32871', '17', '0', 'Analysis complete. There is partial corruption in the planet\'s life-support systems as well as complete corruption in most of the planet\'s defense mechanisms.', '12', '0', '0', '0', '0', '15398', '0', '0', 'Algalon the Observer - SAY_ALGALON_DESPAWN_1');
INSERT INTO `creature_text` VALUES ('32871', '18', '0', 'Begin uplink: Reply Code: \'Omega\'. Planetary re-origination requested.', '12', '0', '0', '0', '0', '15399', '0', '0', 'Algalon the Observer - SAY_ALGALON_DESPAWN_2');
