INSERT INTO `creature_text` VALUES ('17767', '4', '0', 'The Legion\'s final conquest has begun! Once again the subjugation of this world is within our grasp. Let none survive!', '14', '0', '100', '0', '0', '11022', '0', '0', 'Rage Winterchill - SAY_ONAGGRO');
