INSERT INTO `creature_text` VALUES ('17968', '5', '0', 'At last it is here. Mourn and lament the passing of all you have ever known and all that would have been! Akmin-kurai!', '14', '0', '100', '0', '0', '10993', '0', '0', 'Archimonde - SAY_ENRAGE');
