INSERT INTO `creature_text` VALUES ('30840', '3', '3', 'We are done here. I am out.', '12', '0', '100', '0', '0', '0', '0', '0', 'Munch oppening portal');
INSERT INTO `creature_text` VALUES ('30839', '1', '1', 'Too weak to finish your own battles? Very well then...', '12', '0', '100', '0', '0', '0', '0', '0', 'Jayde arrival');
INSERT INTO `creature_text` VALUES ('30839', '2', '2', 'Anything else you need help with before I go? Right, I thought not...', '12', '0', '100', '0', '0', '0', '0', '0', 'Oppens portal and dissapears');
INSERT INTO `creature_text` VALUES ('30838', '1', '1', 'You challenge death itself! Come then, \"King.\" Face the might of Mograine!', '12', '0', '100', '0', '0', '0', '0', '0', 'Mograine\"s arrival');
INSERT INTO `creature_text` VALUES ('30838', '2', '1', 'I will see you back at the Valley of Echoes. Suffer well, ally.', '12', '0', '100', '0', '0', '0', '0', '0', 'Opens portal');
INSERT INTO `creature_text` VALUES ('27003', '0', '0', 'I know why you\'ve come - one of those foolish Magnataur on the plains meddled and managed to get the dragons involved. Do you enjoy serving them like a dog?', '14', '0', '100', '0', '0', '0', '0', '0', 'Dregmar Runebrand - yell');
INSERT INTO `creature_text` VALUES ('27003', '1', '0', 'You seek their leader... little thing, you wage war against the clans of Grom\'thar the Thunderbringer himself. Don\'t be so eager to rush to your death.', '14', '0', '100', '0', '0', '0', '0', '0', 'Dregmar Runebrand yell');
INSERT INTO `creature_text` VALUES ('27003', '2', '0', 'Hah! So be it. Blow the horn of a magnataur leader at the ring of torches south of the Azure Dragonshrine. Make peace with your gods... Grom\'thar will come.', '14', '0', '100', '0', '0', '0', '0', '0', 'Dregmar Runebrand yell');
INSERT INTO `creature_text` VALUES ('27482', '0', '0', 'I\'d nearly given up.You\'ve given me new life!', '12', '0', '50', '0', '0', '0', '0', '0', 'Wounded Westfall Infantry say text');
INSERT INTO `creature_text` VALUES ('27482', '0', '1', 'Bless you, friend.I nearly expired....', '12', '0', '50', '0', '0', '0', '0', '0', 'Wounded Westfall Infantry say text');
INSERT INTO `creature_text` VALUES ('27482', '0', '2', 'Without your help, I surely would have died....', '12', '0', '50', '0', '0', '0', '0', '0', 'Wounded Westfall Infantry say text');
INSERT INTO `creature_text` VALUES ('27482', '0', '3', 'Thank you $r.', '12', '0', '50', '0', '0', '0', '0', '0', 'Wounded Westfall Infantry say text');
INSERT INTO `creature_text` VALUES ('28041', '0', '0', 'Careful here, $C. These trolls killed their own snake god!', '15', '0', '50', '0', '0', '0', '0', '0', 'Argent Soldier whisper text');
INSERT INTO `creature_text` VALUES ('28041', '0', '1', 'Watch your back. These Drakkari are a nasty lot.', '15', '0', '50', '0', '0', '0', '0', '0', 'Argent Soldier whisper text');
INSERT INTO `creature_text` VALUES ('28041', '0', '2', 'These Drakkari are just bad news. We need to leave and head back to Justice Keep!', '15', '0', '50', '0', '0', '0', '0', '0', 'Argent Soldier whisper text');
INSERT INTO `creature_text` VALUES ('28041', '0', '3', 'See you around.', '15', '0', '50', '0', '0', '0', '27447', '0', 'Argent Soldier whisper text');
INSERT INTO `creature_text` VALUES ('28041', '0', '4', 'I wonder where we\'re headed to. And who\'s going to deal with these guys?', '15', '0', '50', '0', '0', '0', '0', '0', 'Argent Soldier whisper text');
INSERT INTO `creature_text` VALUES ('28041', '0', '5', 'Right. I\'d better get back to the sergeant then.', '15', '0', '50', '0', '0', '0', '0', '0', 'Argent Soldier whisper text');
INSERT INTO `creature_text` VALUES ('28041', '0', '6', 'Are you $N? I heard you were dead.', '15', '0', '50', '0', '0', '0', '0', '0', 'Argent Soldier whisper text');
INSERT INTO `creature_text` VALUES ('22019', '0', '0', '%s becomes enraged!', '16', '0', '100', '0', '0', '0', '0', '0', 'Kolphis Darkscale');
INSERT INTO `creature_text` VALUES ('26076', '0', '0', 'What is the meaning of this?! I have not yet finished my feast!', '14', '0', '100', '1', '1000', '0', '0', '0', 'Nafarset on activation');
INSERT INTO `creature_text` VALUES ('30461', '0', '0', '%s lets out a bellowing roar as she descends upon the nest.', '16', '0', '100', '15', '0', '0', '0', '0', 'Veranus');
INSERT INTO `creature_text` VALUES ('30462', '0', '0', 'Look out below!', '14', '0', '100', '0', '0', '0', '0', '0', 'Thorim');
INSERT INTO `creature_text` VALUES ('30462', '1', '0', 'Easy there, girl!  Don\'t you recognize your old master?', '12', '0', '100', '0', '0', '0', '0', '0', 'Thorim');
INSERT INTO `creature_text` VALUES ('30462', '2', '0', 'I will see you at the Temple of Storms. Looks like I\'m going to have to break her in again.', '12', '0', '100', '0', '0', '0', '0', '0', 'Thorim');
INSERT INTO `creature_text` VALUES ('34996', '1', '0', 'Hailing from the deepest, darkest caverns of the Storm Peaks, Gormok the Impaler! Battle on, heroes!', '14', '0', '100', '0', '0', '16038', '0', '0', 'Highlord Tirion Fordring - Summing Gormok the Impaler');
INSERT INTO `creature_text` VALUES ('34996', '2', '0', 'Steel yourselves, heroes, for the twin terrors, Acidmaw and Dreadscale, enter the arena!', '14', '0', '100', '0', '0', '16039', '0', '0', 'Highlord Tirion Fordring - Summing Acidmaw and Dreadscale');
INSERT INTO `creature_text` VALUES ('34996', '3', '0', 'The air itself freezes with the introduction of our next combatant, Icehowl! Kill or be killed, champions!', '14', '0', '100', '0', '0', '16040', '0', '0', 'Highlord Tirion Fordring Summing Icehowl');
INSERT INTO `creature_text` VALUES ('34996', '4', '0', 'The monstrous menagerie has been vanquished!', '14', '0', '100', '0', '0', '16041', '0', '0', 'Highlord Tirion Fordring - Northrend Beasts Done');
INSERT INTO `creature_text` VALUES ('34996', '5', '0', 'Tragic... They fought valiantly, but the beasts of Northrend triumphed. Let us observe a moment of silence for our fallen heroes.', '14', '0', '0', '0', '0', '16042', '0', '0', 'Highlord Tirion Fordring - Northrend Beasts FAIL');
INSERT INTO `creature_text` VALUES ('34996', '6', '0', 'Grand Warlock Wilfred Fizzlebang will summon forth your next challenge. Stand by for his entry.', '14', '0', '100', '0', '0', '16043', '0', '0', 'Highlord Tirion Fordring - Summing Wilfred Fizzlebang');
INSERT INTO `creature_text` VALUES ('34996', '7', '0', 'Quickly, heroes, destroy the demon lord before it can open a portal to its twisted demonic realm!', '14', '0', '100', '5', '0', '16044', '0', '0', 'Highlord Tirion Fordring to Wilfred Fizzlebang - Lord Jaraxxus Intro');
INSERT INTO `creature_text` VALUES ('34996', '8', '0', 'The loss of Wilfred Fizzlebang, while unfortunate, should be a lesson to those that dare dabble in dark magic. Alas, you are victorious and must now face the next challenge.', '14', '0', '100', '0', '0', '16045', '0', '0', 'Highlord Tirion Fordring - Lord Jaraxxus Outro');
INSERT INTO `creature_text` VALUES ('34996', '9', '0', 'Everyone calm down! Compose yourselves! There is no conspiracy at play here! The warlock acted on his own volition, outside of influences from the Alliance. The tournament must go on!', '14', '0', '100', '5', '0', '16046', '0', '0', 'Highlord Tirion Fordring - Lord Jaraxxus Outro');
INSERT INTO `creature_text` VALUES ('34996', '10', '0', 'The next battle will be against the Argent Crusade\'s most powerful knights! Only by defeating them will you be deemed worthy...', '14', '0', '100', '0', '0', '16047', '0', '0', 'Highlord Tirion Fordring - Faction Champions Intro');
INSERT INTO `creature_text` VALUES ('34996', '11', '0', 'Very well. I will allow it. Fight with honor!', '14', '0', '100', '1', '0', '16048', '0', '0', 'Highlord Tirion Fordring - Faction Champions Intro');
INSERT INTO `creature_text` VALUES ('34996', '12', '0', 'A shallow and tragic victory. We are weaker as a whole from the losses suffered today. Who but the Lich King could benefit from such foolishness? Great warriors have lost their lives. And for what? The true threat looms ahead - the Lich King awaits us all in death.', '14', '0', '100', '0', '0', '16049', '0', '0', 'Highlord Tirion Fordring - Faction Champions Outro');
INSERT INTO `creature_text` VALUES ('34996', '13', '0', 'Only by working together will you overcome the final challenge. From the depths of Icecrown come two of the Scourge\'s most powerful lieutenants: fearsome val\'kyr, winged harbingers of the Lich King!', '14', '0', '100', '0', '0', '16050', '0', '0', 'Highlord Tirion Fordring - Twin Val\'kyr Intro');
INSERT INTO `creature_text` VALUES ('34996', '14', '0', 'Let the games begin!', '14', '0', '100', '0', '0', '16037', '0', '0', 'Highlord Tirion Fordring - Twin Val\'kyr Intro');
INSERT INTO `creature_text` VALUES ('34996', '15', '0', 'A mighty blow has been dealt to the Lich King! You have proven yourselves as able bodied champions of the Argent Crusade. Together we will strike against Icecrown Citadel and destroy what remains of the Scourge! There is no challenge that we cannot face united!', '14', '0', '100', '5', '0', '16051', '0', '0', 'Highlord Tirion Fordring - Anub\'arak Intro');
INSERT INTO `creature_text` VALUES ('34996', '16', '0', 'Arthas! You are hopelessly outnumbered! Lay down Frostmourne and I will grant you a just death.', '14', '0', '100', '25', '0', '16052', '0', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('34995', '3', '0', 'Show them no mercy, Horde champions! LOK\'TAR OGAR!', '14', '0', '0', '0', '0', '16022', '0', '0', 'Garrosh - Faction Champions Intro');
INSERT INTO `creature_text` VALUES ('34995', '2', '0', 'The Horde demands justice! We challenge the Alliance. Allow us to battle in place of your knights, paladin. We will show these dogs what it means to insult the Horde!', '14', '0', '100', '1', '0', '16023', '0', '0', 'Garrosh Hellscream - Faction Champions Intro');
INSERT INTO `creature_text` VALUES ('34995', '1', '0', 'Treacherous Alliance dogs! You summon a demon lord against warriors of the Horde? Your deaths will be swift!', '14', '0', '100', '5', '0', '16021', '0', '0', 'Garrosh Hellscream - Lord Jaraxxus Outro');
INSERT INTO `creature_text` VALUES ('34995', '0', '0', 'I\'ve seen more worthy challenges in the Ring of Blood. You waste our time, paladin.', '14', '0', '100', '1', '0', '16026', '0', '0', 'Garrosh Hellscream  - Northrend Beasts Outro');
INSERT INTO `creature_text` VALUES ('34990', '6', '3', 'Worthless scrub.', '14', '0', '100', '25', '0', '16062', '0', '0', 'King Varian Wrynn  - Faction Champions Kill Player');
INSERT INTO `creature_text` VALUES ('34990', '6', '2', 'Is this the best the Horde has to offer?', '14', '0', '100', '6', '0', '16063', '0', '0', 'King Varian Wrynn  - Faction Champions Kill Player');
INSERT INTO `creature_text` VALUES ('34990', '6', '1', 'HAH!', '14', '0', '100', '5', '0', '16060', '35684', '0', 'King Varian Wrynn - Faction Champions Kill Player');
INSERT INTO `creature_text` VALUES ('34990', '6', '0', 'Hardly a challenge.', '14', '0', '100', '274', '0', '16061', '0', '0', 'King Varian Wrynn - Faction Champions Kill Player');
INSERT INTO `creature_text` VALUES ('34990', '4', '0', 'GLORY TO THE ALLIANCE!', '14', '0', '100', '0', '0', '16067', '0', '0', 'King Varian Wrynn - Victory');
INSERT INTO `creature_text` VALUES ('34990', '5', '0', 'Not even the Lich King most powerful minions can stand against the Alliance! All hail our victors!', '14', '0', '0', '0', '0', '16068', '0', '0', 'King Varian Wrynn - Faction Champions Outro');
INSERT INTO `creature_text` VALUES ('34990', '3', '0', 'Fight for the glory of the Alliance, heroes! Honor your king and your people!', '14', '0', '100', '5', '0', '16065', '0', '0', 'King Varian Wrynn - Faction Champions Intro');
INSERT INTO `creature_text` VALUES ('34990', '2', '0', 'Our honor has been besmirched! They make wild claims and false accusations against us. I demand justice! Allow my champions to fight in place of your knights, Tirion. We challenge the Horde!', '14', '0', '100', '5', '0', '16066', '0', '0', 'King Varian Wrynn - Faction Champions Intro');
INSERT INTO `creature_text` VALUES ('34990', '1', '0', 'The Alliance doesn\'t need the help of a demon lord to deal with Horde filth! Come, pig!', '14', '0', '100', '5', '0', '16064', '0', '0', 'King Varian Wrynn - Lord Jaraxxus Outro');
INSERT INTO `creature_text` VALUES ('34990', '0', '0', 'Your beasts will be no match for my champions, Tirion!', '14', '0', '0', '0', '0', '16069', '0', '0', 'King Varian Wrynn - Northrend Beasts Outro');
INSERT INTO `creature_text` VALUES ('36095', '0', '0', 'Champions, you\'re alive! Not only have you defeated every challenge of the Trial of the Crusader, but also thwarted Arthas\' plans! Your skill and cunning will prove to be a powerful weapon against the Scourge. Well done! Allow one of the Crusade\'s mages to transport you to the surface!', '14', '0', '100', '5', '0', '16053', '0', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('36095', '1', '0', 'Let me hand you the chests as a reward, and let its contents will serve you faithfully in the campaign against Arthas in the heart of the Icecrown Citadel!', '41', '0', '0', '0', '0', '0', '0', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('34796', '0', '0', 'My slaves! Destroy the enemy!', '41', '0', '0', '0', '0', '0', '0', '0', 'Gormok the Impaler - Snowball');
INSERT INTO `creature_text` VALUES ('35144', '0', '0', 'Upon seeing its companion perish, %s becomes enraged!', '41', '0', '100', '0', '0', '0', '0', '0', 'Acidmaw to Beasts Controller - Enrage');
INSERT INTO `creature_text` VALUES ('34799', '0', '0', 'Upon seeing its companion perish, %s becomes enraged!', '41', '0', '100', '0', '0', '0', '0', '0', 'Dreadscale to Beasts Controller - Enrage');
INSERT INTO `creature_text` VALUES ('34797', '0', '0', '%s glares at $n and lets out a bellowing roar!', '41', '0', '100', '0', '0', '0', '0', '0', 'Icehowl - Start');
INSERT INTO `creature_text` VALUES ('34797', '1', '0', '%s crashes into the Coliseum wall and is stunned!', '41', '0', '100', '0', '0', '0', '0', '0', 'Icehowl - Crash');
INSERT INTO `creature_text` VALUES ('34797', '2', '0', 'Trampling combatants underfoot, %s goes into a frothing rage!', '41', '0', '100', '0', '0', '0', '0', '0', 'Icehowl - Fail');
INSERT INTO `creature_text` VALUES ('35458', '0', '0', 'Thank you, Highlord. Now, challengers, I will begin the ritual of summoning. When I am done a fearsome doomguard will appear!', '14', '0', '100', '2', '0', '16268', '0', '0', 'Wilfred Fizzlebang - Intro');
INSERT INTO `creature_text` VALUES ('35458', '1', '0', 'Prepare for oblivion!', '14', '0', '100', '0', '0', '16269', '0', '0', 'Wilfred Fizzlebang - Intro');
INSERT INTO `creature_text` VALUES ('35458', '2', '0', 'A-HA! I\'ve done it! Behold the absolute power of Wilfred Fizzlebang, master summoner! You are bound to me, demon!', '14', '0', '100', '5', '0', '16270', '0', '0', 'Wilfred Fizzlebang to Wilfred Fizzlebang - Intro');
INSERT INTO `creature_text` VALUES ('35458', '3', '0', 'But I\'m in charge here...', '14', '0', '100', '5', '0', '16271', '0', '0', 'Wilfred Fizzlebang to Wilfred Fizzlebang - Death');
INSERT INTO `creature_text` VALUES ('34780', '0', '0', 'Trifling gnome! Your arrogance will be your undoing!', '14', '0', '100', '397', '0', '16143', '0', '0', 'Lord Jaraxxus to Wilfred Fizzlebang - Intro');
INSERT INTO `creature_text` VALUES ('34780', '1', '0', 'You face Jaraxxus, Eredar Lord of the Burning Legion!', '14', '0', '100', '0', '0', '16144', '0', '0', 'Lord Jaraxxus - Aggro');
