INSERT INTO `creature_text` VALUES ('28016', '12', '0', 'I be needin\' worthy underlings in Zul\'Drak, mon. Ones ready to wield real power.', '12', '0', '100', '396', '0', '14017', '0', '0', 'Drakuru');
INSERT INTO `creature_text` VALUES ('28016', '13', '0', 'You see for yourself now. Da Lich King be generous to dem who obey.', '12', '0', '100', '396', '0', '14018', '0', '0', 'Drakuru');
