INSERT INTO `creature_text` VALUES ('28939', '8', '0', 'I\'ll tear the secrets from your soul! Tell me about the \"Crimson Dawn\" and your life may be spared!', '12', '0', '100', '0', '0', '0', '0', '0', 'player SAY_PERSUADE1');
INSERT INTO `creature_text` VALUES ('28912', '0', '0', 'I\'ll need to get my runeblade and armor... Just need a little more time.', '12', '0', '100', '0', '0', '0', '0', '0', 'koltira deathweaver SAY_BREAKOUT1');
INSERT INTO `creature_text` VALUES ('28912', '1', '0', 'I\'m still weak, but I think I can get an anti-magic barrier up. Stay inside it or you\'ll be destroyed by their spells.', '12', '0', '100', '0', '0', '0', '0', '0', 'koltira deathweaver SAY_BREAKOUT2');
INSERT INTO `creature_text` VALUES ('28912', '2', '0', 'Maintaining this barrier will require all of my concentration. Kill them all!', '12', '0', '100', '0', '0', '0', '0', '0', 'koltira deathweaver SAY_BREAKOUT3');
INSERT INTO `creature_text` VALUES ('28912', '3', '0', 'There are more coming. Defend yourself! Don\'t fall out of the anti-magic field! They\'ll tear you apart without its protection!', '12', '0', '100', '0', '0', '0', '0', '0', 'koltira deathweaver SAY_BREAKOUT4');
INSERT INTO `creature_text` VALUES ('28912', '4', '0', 'I can\'t keep barrier up much longer... Where is that coward?', '12', '0', '100', '0', '0', '0', '0', '0', 'koltira deathweaver SAY_BREAKOUT5');
INSERT INTO `creature_text` VALUES ('28912', '5', '0', 'The High Inquisitor comes! Be ready, death knight! Do not let him draw you out of the protective bounds of my anti-magic field! Kill him and take his head!', '12', '0', '100', '0', '0', '0', '0', '0', 'koltira deathweaver SAY_BREAKOUT6');
INSERT INTO `creature_text` VALUES ('28912', '6', '0', 'Stay in the anti-magic field! Make them come to you!', '12', '0', '100', '0', '0', '0', '0', '0', 'koltira deathweaver SAY_BREAKOUT7');
INSERT INTO `creature_text` VALUES ('28912', '7', '0', 'The death of the High Inquisitor of New Avalon will not go unnoticed. You need to get out of here at once! Go, before more of them show up. I\'ll be fine on my own.', '12', '0', '100', '0', '0', '0', '0', '0', 'koltira deathweaver SAY_BREAKOUT8');
INSERT INTO `creature_text` VALUES ('28912', '8', '0', 'I\'ll draw their fire, you make your escape behind me.', '12', '0', '100', '0', '0', '0', '0', '0', 'koltira deathweaver SAY_BREAKOUT9');
INSERT INTO `creature_text` VALUES ('28912', '9', '0', 'Your High Inquisitor is nothing more than a pile of meat, Crusaders! There are none beyond the grasp of the Scourge!', '14', '0', '100', '0', '0', '0', '0', '0', 'koltira deathweaver SAY_BREAKOUT10');
INSERT INTO `creature_text` VALUES ('29076', '0', '0', 'Hrm, what a strange tree. I must investigate.', '12', '0', '100', '0', '0', '0', '0', '0', 'scarlet courier SAY_TREE1');
