INSERT INTO `creature_text` VALUES ('29173', '5', '0', 'The skies turn red with the blood of the fallen! The Lich King watches over us, minions! Onward! Leave only ashes and misery in your destructive wake!', '14', '0', '100', '0', '0', '14682', '0', '0', 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES ('29176', '6', '0', 'Scourge armies approach!', '14', '0', '100', '0', '0', '0', '0', '0', 'Korfax, Champion of the Light');
