INSERT INTO `creature_text` VALUES ('29173', '8', '9', 'Obey me, blade!', '14', '0', '100', '0', '0', '14692', '0', '0', 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES ('29173', '8', '10', 'You will do as I command! I am in control here!', '12', '0', '100', '0', '0', '14693', '0', '0', 'Highlord Darion Mograine');
