INSERT INTO `creature_text` VALUES ('29173', '8', '15', 'Minions, come to my aid!', '12', '0', '100', '0', '0', '14698', '0', '0', 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES ('29175', '24', '0', 'You cannot win, Darion!', '14', '0', '100', '0', '0', '14584', '0', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('29175', '25', '0', 'Bring them before the chapel!', '14', '0', '100', '0', '0', '14585', '0', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('29173', '26', '0', 'Stand down, death knights. We have lost... The Light... This place... No hope...', '12', '0', '100', '0', '0', '14699', '0', '0', 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES ('29175', '27', '0', 'Have you learned nothing, boy? You have become all that your father fought against! Like that coward, Arthas, you allowed yourself to be consumed by the darkness...the hate... Feeding upon the misery of those you tortured and killed!', '12', '0', '100', '0', '0', '14586', '0', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('29175', '28', '0', 'Your master knows what lies beneath the chapel. It is why he dares not show his face! He\'s sent you and your death knights to meet their doom, Darion.', '12', '0', '100', '0', '0', '14587', '0', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('29175', '29', '0', 'What you are feeling right now is the anguish of a thousand lost souls! Souls that you and your master brought here! The Light will tear you apart, Darion!', '12', '0', '100', '0', '0', '14588', '0', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('29173', '30', '0', 'Save your breath, old man. It might be the last you ever draw.', '12', '0', '100', '0', '0', '14700', '0', '0', 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES ('29227', '31', '0', 'My son! My dear, beautiful boy!', '12', '0', '100', '0', '0', '14493', '0', '0', 'Highlord Alexandros Mograine');
INSERT INTO `creature_text` VALUES ('29173', '32', '0', 'Father!', '12', '0', '100', '0', '0', '14701', '29767', '0', 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES ('29173', '33', '0', 'Argh...what...is...', '12', '0', '100', '0', '0', '14702', '0', '0', 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES ('29228', '34', '0', 'Father, you have returned!', '12', '0', '100', '0', '0', '14703', '0', '0', 'Darion Mograine');
INSERT INTO `creature_text` VALUES ('29228', '35', '0', 'You have been gone a long time, father. I thought...', '12', '0', '100', '0', '0', '14704', '0', '0', 'Darion Mograine');
INSERT INTO `creature_text` VALUES ('29227', '36', '0', 'Nothing could have kept me away from here, Darion. Not from my home and family.', '12', '0', '100', '0', '0', '14494', '0', '0', 'Highlord Alexandros Mograine');
INSERT INTO `creature_text` VALUES ('29228', '37', '0', 'Father, I wish to join you in the war against the undead. I want to fight! I can sit idle no longer!', '12', '0', '100', '0', '0', '14705', '0', '0', 'Darion Mograine');
INSERT INTO `creature_text` VALUES ('29227', '38', '0', 'Darion Mograine, you are barely of age to hold a sword, let alone battle the undead hordes of Lordaeron! I couldn\'t bear losing you. Even the thought...', '12', '0', '100', '0', '0', '14495', '0', '0', 'Highlord Alexandros Mograine');
INSERT INTO `creature_text` VALUES ('29228', '39', '0', 'If I die, father, I would rather it be on my feet, standing in defiance against the undead legions! If I die, father, I die with you!', '12', '0', '100', '0', '0', '14706', '0', '0', 'Darion Mograine');
INSERT INTO `creature_text` VALUES ('29227', '40', '0', 'My son, there will come a day when you will command the Ashbringer and, with it, mete justice across this land. I have no doubt that when that day finally comes, you will bring pride to our people and that Lordaeron will be a better place because of you. But, my son, that day is not today.', '12', '0', '100', '0', '0', '14496', '0', '0', 'Highlord Alexandros Mograine');
INSERT INTO `creature_text` VALUES ('29227', '41', '0', 'Do not forget...', '12', '0', '100', '0', '0', '14497', '0', '0', 'Highlord Alexandros Mograine');
INSERT INTO `creature_text` VALUES ('29183', '42', '0', 'Touching...', '14', '0', '100', '0', '0', '14803', '0', '0', 'The Lich King');
INSERT INTO `creature_text` VALUES ('29173', '43', '0', 'You have\'ve betrayed me! You betrayed us all you monster! Face the might of Mograine!', '14', '0', '100', '0', '0', '14707', '0', '0', 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES ('29183', '44', '0', 'He\'s mine now...', '12', '0', '100', '0', '0', '14805', '0', '0', 'The Lich King');
INSERT INTO `creature_text` VALUES ('29183', '45', '0', 'Pathetic...', '12', '0', '100', '0', '0', '14804', '0', '0', 'The Lich King');
INSERT INTO `creature_text` VALUES ('29175', '46', '0', 'You\'re a damned monster, Arthas!', '12', '0', '100', '0', '0', '14589', '0', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('29183', '47', '0', 'You were right, Fordring. I did send them in to die. Their lives are meaningless, but yours...', '12', '0', '100', '0', '0', '14806', '0', '0', 'The Lich King');
INSERT INTO `creature_text` VALUES ('29183', '48', '0', 'How simple it was to draw the great Tirion Fordring out of hiding. You\'ve left yourself exposed, paladin. Nothing will save you...', '12', '0', '100', '0', '0', '14807', '0', '0', 'The Lich King');
