INSERT INTO `creature_text` VALUES ('29183', '58', '0', 'When next we meet it won\'t be on holy ground, paladin.', '14', '0', '100', '0', '0', '14812', '0', '0', 'The Lich King');
INSERT INTO `creature_text` VALUES ('29175', '59', '0', 'Rise, Darion, and listen...', '12', '0', '100', '0', '0', '14593', '0', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('29175', '60', '0', 'We have all been witness to a terrible tragedy. The blood of good men has been shed upon this soil! Honorable knights, slain defending their lives - our lives!', '12', '0', '100', '0', '0', '14594', '0', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('29175', '61', '0', 'And while such things can never be forgotten, we must remain vigilant in our cause!', '12', '0', '100', '0', '0', '14595', '0', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('29175', '62', '0', 'The Lich King must answer for what he has done and must not be allowed to cause further destruction to our world.', '12', '0', '100', '0', '0', '14596', '0', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('29175', '63', '0', 'I make a promise to you now, brothers and sisters: The Lich King will be defeated! On this day, I call for a union.', '12', '0', '100', '0', '0', '14597', '0', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('29175', '64', '0', 'The Argent Dawn and the Order of the Silver Hand will come together as one! We will succeed where so many before us have failed!', '12', '0', '100', '0', '0', '14598', '0', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('29175', '65', '0', 'We will take the fight to Arthas and tear down the walls of Icecrown!', '12', '0', '100', '0', '0', '14599', '0', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('29175', '66', '0', 'The Argent Crusade comes for you, Arthas!', '14', '0', '100', '0', '0', '14600', '0', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('29173', '67', '0', 'So too do the Knights of the Ebon Blade... While our kind has no place in your world, we will fight to bring an end to the Lich King. This I vow!', '12', '0', '100', '0', '0', '14710', '0', '0', 'Highlord Darion Mograine');
