INSERT INTO `creature_text` VALUES ('12018', '4', '0', 'Impossible! Stay your attack mortals! I submitt! I submitt! Brashly you have come to rest the secrets of the living flame. You will soon regret the recklessness of your quest. I go now to summon the lord whos house this is. Should you seek an audiance with him your paltry lives will surly be forfit. Nevertheless seek out his lair if you dare!', '14', '0', '100', '0', '0', '8038', '0', '0', 'majordomo SAY_DEFEAT');
INSERT INTO `creature_text` VALUES ('12018', '5', '0', 'Behold Ragnaros, the Firelord! He who was ancient when this world was young! Bow before him, mortals! Bow before your ending!', '14', '0', '100', '0', '0', '8040', '0', '0', 'majordomo SAY_SUMMON_MAJ');
INSERT INTO `creature_text` VALUES ('12018', '6', '0', 'These mortal infidels, my lord! They have invaded your sanctum, and seek to steal your secrets!', '14', '0', '100', '0', '0', '8041', '0', '0', 'majordomo SAY_ARRIVAL2_MAJ');
