INSERT INTO `creature_text` VALUES ('349', '0', '0', 'My wounds are grave. Forgive my slow pace but my injuries won\'t allow me to walk any faster.', '12', '0', '100', '0', '0', '0', '0', '0', 'corporal keeshan SAY_CORPORAL_1');
