INSERT INTO `creature_text` VALUES ('349', '2', '0', 'The Blackrock infestation is thick in these parts. I will do my best to keep the pace. Let\'s go!', '12', '0', '100', '0', '0', '0', '0', '0', 'corporal keeshan SAY_CORPORAL_3');
INSERT INTO `creature_text` VALUES ('349', '3', '0', 'Marshal Marris,sir. Corporal Keeshan of the 12th Sabre Regiment returned from battle and reporting for duty!', '12', '0', '100', '0', '0', '0', '0', '0', 'corporal keeshan SAY_CORPORAL_4');
INSERT INTO `creature_text` VALUES ('349', '4', '0', 'Brave adventurer,thank you for rescuing me! I am sure Marshal Marris will reward your kind deed.', '12', '0', '100', '0', '0', '0', '0', '0', 'corporal keeshan SAY_CORPORAL_5');
INSERT INTO `creature_text` VALUES ('17238', '0', '0', 'Beware! We are attacked!', '12', '0', '100', '0', '0', '0', '0', '0', 'anchorite truuen SAY_WP_0');
