INSERT INTO `creature_text` VALUES ('1854', '0', '0', 'Be welcome, friends!', '12', '0', '100', '0', '0', '0', '0', '0', 'anchorite truuen SAY_WP_3');
INSERT INTO `creature_text` VALUES ('17233', '0', '0', 'Thank you for coming here in remembrance of me. Your efforts in recovering that symbol, while unnecessary, are certainly touching to an old man\'s heart. ', '12', '0', '100', '0', '0', '0', '0', '0', 'anchorite truuen SAY_WP_4');
INSERT INTO `creature_text` VALUES ('17233', '1', '0', 'Please, rise my friend. Keep the Blessing as a symbol of the strength of the Light and how heroes long gone might once again rise in each of us to inspire. ', '12', '0', '100', '0', '0', '0', '0', '0', 'anchorite truuen SAY_WP_5');
INSERT INTO `creature_text` VALUES ('17233', '2', '0', 'Thank you my friend for making this possible. This is a day that I shall never forget! I think I will stay a while. Please return to High Priestess MacDonnell at the camp. I know that she\'ll be keenly interested to know of what has transpired here.', '12', '0', '100', '0', '0', '0', '0', '0', 'anchorite truuen SAY_WP_6');
