INSERT INTO `creature_text` VALUES ('26971', '3', '0', 'We\'ll all end up serving the Lich King, kid. Better this way than becoming a rotten corpse.', '12', '0', '100', '0', '0', '0', '0', '0', 'Anatoly');
INSERT INTO `creature_text` VALUES ('26971', '4', '0', 'Stop!  Do not shoot!  Do not hurt Tatjana!', '12', '0', '100', '0', '0', '0', '0', '0', 'Anatoly');
