INSERT INTO `creature_text` VALUES ('26971', '6', '0', 'Forgive me, Tatjana... ', '12', '0', '100', '0', '0', '0', '0', '0', 'Anatoly');
INSERT INTO `creature_text` VALUES ('26971', '7', '0', 'Nothing you can do can compare to what Arugal can do to us!  I will tear you apart myself!', '12', '0', '100', '0', '0', '0', '0', '0', 'Anatoly');
