INSERT INTO `creature_text` VALUES ('27646', '1', '0', 'Sister!  The mean men were going to take me to Badmoon Isle.  They wanted to turn me into one of them.', '12', '0', '100', '0', '0', '0', '0', '0', 'Anya');
INSERT INTO `creature_text` VALUES ('27646', '2', '0', 'That\'s what I said!  Badmoon!  Sasha... don\'t ever leave me again!  First they took papa, then they took you... I don\'t want to be alone with them!', '12', '0', '100', '0', '0', '0', '0', '0', 'Anya');
