INSERT INTO `creature_text` VALUES ('37846', '3', '0', 'As Quel\'Serrar was forged by the dragons and given to the kaldorei, its twin,Quel\'Delar was given to my people.', '12', '0', '100', '1', '0', '16809', '0', '0', 'Blood Queen Lana\'thel - Say 3');
