INSERT INTO `creature_text` VALUES ('18887', '0', '0', 'I\'m free! Thank you all!', '12', '0', '100', '0', '0', '0', '0', '0', 'taretha SAY_TA_FREE');
INSERT INTO `creature_text` VALUES ('18887', '1', '0', 'Thrall, you escaped!', '12', '0', '100', '0', '0', '0', '0', '0', 'taretha SAY_TA_ESCAPED');
INSERT INTO `creature_text` VALUES ('17862', '0', '0', 'Thrall! You didn\'t really think you would escape did you? You and your allies shall answer to Blackmoore - after I\'ve had my fun!', '14', '0', '100', '0', '0', '10406', '0', '0', 'skarloc SAY_ENTER');
INSERT INTO `creature_text` VALUES ('17862', '1', '0', 'You\'re a slave. That\'s all you\'ll ever be.', '14', '0', '100', '0', '0', '10407', '0', '0', 'skarloc SAY_TAUNT1');
INSERT INTO `creature_text` VALUES ('17862', '2', '0', 'I don\'t know what Blackmoore sees in you. For my money, you\'re just another ignorant savage!', '14', '0', '100', '0', '0', '10408', '0', '0', 'skarloc SAY_TAUNT2');
INSERT INTO `creature_text` VALUES ('17862', '3', '0', 'Thrall will never be free!', '14', '0', '100', '0', '0', '10409', '0', '0', 'skarloc SAY_SLAY1');
INSERT INTO `creature_text` VALUES ('17862', '3', '1', 'Did you really think you would leave here alive?', '14', '0', '100', '0', '0', '10410', '0', '0', 'skarloc SAY_SLAY2');
INSERT INTO `creature_text` VALUES ('17862', '4', '0', 'Guards! Urgh..Guards..!', '14', '0', '100', '0', '0', '10411', '0', '0', 'skarloc SAY_DEATH');
INSERT INTO `creature_text` VALUES ('18096', '0', '0', 'Thrall! Come outside and face your fate!', '14', '0', '100', '0', '0', '10418', '0', '0', 'epoch hunter SAY_ENTER1');
INSERT INTO `creature_text` VALUES ('18096', '0', '1', 'Taretha\'s life hangs in the balance. Surely you care for her. Surely you wish to save her...', '14', '0', '100', '0', '0', '10419', '0', '0', 'epoch hunter SAY_ENTER2');
INSERT INTO `creature_text` VALUES ('18096', '0', '2', 'Ah, there you are. I had hoped to accomplish this with a bit of subtlety, but I suppose direct confrontation was inevitable. Your future, Thrall, must not come to pass and so...you and your troublesome friends must die!', '14', '0', '100', '0', '0', '10420', '0', '0', 'epoch hunter SAY_ENTER3');
INSERT INTO `creature_text` VALUES ('18096', '1', '0', 'Enough! I will erase your very existence!', '14', '0', '100', '0', '0', '10421', '0', '0', 'epoch hunter SAY_AGGRO1');
INSERT INTO `creature_text` VALUES ('18096', '1', '1', 'You cannot fight fate!', '14', '0', '100', '0', '0', '10422', '0', '0', 'epoch hunter SAY_AGGRO2');
INSERT INTO `creature_text` VALUES ('18096', '2', '0', 'You are...irrelevant.', '14', '0', '100', '0', '0', '10425', '0', '0', 'epoch hunter SAY_SLAY1');
INSERT INTO `creature_text` VALUES ('18096', '2', '1', 'Thrall will remain a slave. Taretha will die. You have failed.', '14', '0', '100', '0', '0', '10426', '0', '0', 'epoch hunter SAY_SLAY2');
INSERT INTO `creature_text` VALUES ('18096', '3', '0', 'Not so fast!', '14', '0', '100', '0', '0', '10423', '16691', '0', 'epoch hunter SAY_BREATH1');
INSERT INTO `creature_text` VALUES ('18096', '3', '1', 'Struggle as much as you like!', '14', '0', '100', '0', '0', '10424', '0', '0', 'epoch hunter SAY_BREATH2');
INSERT INTO `creature_text` VALUES ('18096', '4', '0', 'No!...The master... will not... be pleased.', '14', '0', '100', '0', '0', '10427', '0', '0', 'epoch hunter SAY_DEATH');
INSERT INTO `creature_text` VALUES ('26529', '0', '0', 'Play time!', '14', '0', '100', '0', '0', '13428', '0', '0', 'meathook SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('26529', '1', '0', 'Boring...', '14', '0', '100', '0', '0', '13430', '0', '0', 'meathook SAY_SLAY_1');
INSERT INTO `creature_text` VALUES ('26529', '1', '1', 'Why you stop moving?', '14', '0', '100', '0', '0', '13431', '0', '0', 'meathook SAY_SLAY_2');
INSERT INTO `creature_text` VALUES ('26529', '1', '2', 'Get up! Me not done!', '14', '0', '100', '0', '0', '13432', '0', '0', 'meathook SAY_SLAY_3');
INSERT INTO `creature_text` VALUES ('26529', '2', '0', 'New toys!', '14', '0', '100', '0', '0', '13429', '0', '0', 'meathook SAY_SPAWN');
INSERT INTO `creature_text` VALUES ('26529', '3', '0', 'This... not fun...', '14', '0', '100', '0', '0', '13433', '0', '0', 'meathook SAY_DEATH');
INSERT INTO `creature_text` VALUES ('26532', '0', '0', 'Prince Arthas Menethil, on this day, a powerful darkness has taken hold of your soul. The death you are destined to visit upon others will this day be your own.', '14', '0', '100', '0', '0', '13408', '0', '0', 'epoch SAY_INTRO | culling SAY_PHASE314');
INSERT INTO `creature_text` VALUES ('26532', '1', '0', 'We\'ll see about that, young prince.', '14', '0', '100', '0', '0', '13409', '0', '0', 'epoch SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('26532', '2', '0', 'Tick tock, tick tock...', '14', '0', '100', '0', '0', '13410', '0', '0', 'epoch SAY_TIME_WARP_1');
INSERT INTO `creature_text` VALUES ('26532', '2', '1', 'Not quick enough!', '14', '0', '100', '0', '0', '13411', '0', '0', 'epoch SAY_TIME_WARP_2');
INSERT INTO `creature_text` VALUES ('26532', '2', '2', 'Let\'s get this over with. ', '14', '0', '100', '0', '0', '13412', '0', '0', 'epoch SAY_TIME_WARP_3');
INSERT INTO `creature_text` VALUES ('26532', '3', '0', 'There is no future for you.', '14', '0', '100', '0', '0', '13413', '0', '0', 'epoch SAY_SLAY_1');
INSERT INTO `creature_text` VALUES ('26532', '3', '1', 'This is the hour of our greatest triumph!', '14', '0', '100', '0', '0', '13414', '0', '0', 'epoch SAY_SLAY_2');
INSERT INTO `creature_text` VALUES ('26532', '3', '2', 'You were destined to fail.', '14', '0', '100', '0', '0', '13415', '0', '0', 'epoch SAY_SLAY_3');
INSERT INTO `creature_text` VALUES ('26532', '4', '0', '', '12', '0', '100', '0', '0', '13416', '0', '0', 'epoch SAY_DEATH');
INSERT INTO `creature_text` VALUES ('26530', '0', '0', 'Ah, the entertainment has arrived!', '14', '0', '100', '0', '0', '13472', '0', '0', 'salramm SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('26530', '1', '0', 'You are too late, champion of Lordaeron. The dead shall have their day.', '14', '0', '100', '0', '0', '13471', '0', '0', 'salramm SAY_SPAWN');
INSERT INTO `creature_text` VALUES ('26530', '2', '0', 'The fun is just beginning!', '14', '0', '100', '0', '0', '13473', '0', '0', 'salramm SAY_SLAY_1');
INSERT INTO `creature_text` VALUES ('26530', '2', '1', 'Aah, quality materials!', '14', '0', '100', '0', '0', '13474', '0', '0', 'salramm SAY_SLAY_2');
INSERT INTO `creature_text` VALUES ('26530', '2', '2', 'Don\'t worry, I\'ll make good use of you.', '14', '0', '100', '0', '0', '13475', '0', '0', 'salramm SAY_SLAY_3');
INSERT INTO `creature_text` VALUES ('26530', '3', '0', 'You only advance... the master\'s plan...', '14', '0', '100', '0', '0', '13483', '0', '0', 'salramm SAY_DEATH');
INSERT INTO `creature_text` VALUES ('26530', '4', '0', 'BOOM! Hahahahah...', '14', '0', '100', '0', '0', '13478', '0', '0', 'salramm SAY_EXPLODE_GHOUL_1');
INSERT INTO `creature_text` VALUES ('26530', '4', '1', 'Blood... destruction... EXHILARATING!', '14', '0', '100', '0', '0', '13479', '0', '0', 'salramm SAY_EXPLODE_GHOUL_2');
INSERT INTO `creature_text` VALUES ('26530', '5', '0', 'I want a sample...', '14', '0', '100', '0', '0', '13480', '0', '0', 'salramm SAY_STEAL_FLESH_1');
INSERT INTO `creature_text` VALUES ('26530', '5', '1', 'Such strength... it must be mine!', '14', '0', '100', '0', '0', '13481', '0', '0', 'salramm SAY_STEAL_FLESH_2');
INSERT INTO `creature_text` VALUES ('26530', '5', '2', 'Your flesh betrays you.', '14', '0', '100', '0', '0', '13482', '0', '0', 'salramm SAY_STEAL_FLESH_3');
INSERT INTO `creature_text` VALUES ('26530', '6', '0', 'Say hello to some friends of mine.', '14', '0', '100', '0', '0', '13476', '0', '0', 'salramm SAY_SUMMON_GHOULS_1');
INSERT INTO `creature_text` VALUES ('26530', '6', '1', 'Come, citizen of Stratholme! Meet your saviors.', '14', '0', '100', '0', '0', '13477', '0', '0', 'salramm SAY_SUMMON_GHOULS_2');
INSERT INTO `creature_text` VALUES ('26499', '0', '0', 'Glad you could make it, Uther.', '12', '0', '100', '0', '0', '12828', '0', '0', 'culling SAY_PHASE101');
INSERT INTO `creature_text` VALUES ('26499', '1', '0', 'As if I could forget. Listen, Uther, there\'s something about the plague you should know...', '12', '0', '100', '0', '0', '12829', '0', '0', 'culling SAY_PHASE103');
INSERT INTO `creature_text` VALUES ('26499', '2', '0', 'Oh, no. We\'re too late. These people have all been infected! They may look fine now, but it\'s just a matter of time before they turn into the undead!', '12', '0', '100', '0', '0', '12830', '0', '0', 'culling SAY_PHASE104');
INSERT INTO `creature_text` VALUES ('26499', '3', '0', 'This entire city must be purged.', '12', '0', '100', '0', '0', '12831', '0', '0', 'culling SAY_PHASE106');
INSERT INTO `creature_text` VALUES ('26499', '4', '0', 'Damn it, Uther! As your future king, I order you to purge this city!', '12', '0', '100', '0', '0', '12832', '0', '0', 'culling SAY_PHASE108');
INSERT INTO `creature_text` VALUES ('26499', '5', '0', 'Then I must consider this an act of treason.', '12', '0', '100', '0', '0', '12833', '0', '0', 'culling SAY_PHASE110');
INSERT INTO `creature_text` VALUES ('26499', '6', '0', 'Have I? Lord Uther, by my right of succession and the sovereignty of my crown, I hereby relieve you of your command and suspend your paladins from service.', '12', '0', '100', '0', '0', '12834', '0', '0', 'culling SAY_PHASE112');
INSERT INTO `creature_text` VALUES ('26499', '7', '0', 'It\'s done! Those of you who have the will to save this land, follow me! The rest of you... get out of my sight!', '12', '0', '100', '0', '0', '12835', '0', '0', 'culling SAY_PHASE114');
INSERT INTO `creature_text` VALUES ('26499', '8', '0', 'Jaina?', '12', '0', '100', '0', '0', '12836', '0', '0', 'culling SAY_PHASE116');
INSERT INTO `creature_text` VALUES ('26499', '9', '0', 'Take position here, and I will lead a small force inside Stratholme to begin the culling. We must contain and purge the infected for the sake of all of Lordaeron!', '14', '0', '100', '0', '0', '14327', '0', '0', 'culling SAY_PHASE118');
INSERT INTO `creature_text` VALUES ('26499', '10', '0', 'Everyone looks ready. Remember, these people are all infected with the plague and will die soon. We must purge Stratholme to protect the remainder of Lordaeron from the Scourge. Let\'s go.', '12', '0', '100', '0', '0', '14293', '0', '0', 'culling SAY_PHASE201');
INSERT INTO `creature_text` VALUES ('26499', '11', '0', 'I can only help you with a clean death.', '12', '0', '100', '0', '0', '14294', '0', '0', 'culling SAY_PHASE203');
INSERT INTO `creature_text` VALUES ('26499', '12', '0', 'That was just the beginning.', '12', '0', '100', '0', '0', '14295', '0', '0', 'culling SAY_PHASE205');
INSERT INTO `creature_text` VALUES ('26499', '13', '0', 'I won\'t allow it, Mal\'Ganis! Better that these people die by my hand than serve as your slaves in death!', '14', '0', '100', '0', '0', '14296', '0', '0', 'culling SAY_PHASE208');
INSERT INTO `creature_text` VALUES ('26499', '14', '0', 'Mal\'ganis will send out some of his Scourge minions to interfere with us. Those of you with the strongest steel and magic shall go forth and destroy them. I will lead the rest of my forces in purging Stratholme of the infected.', '14', '0', '100', '0', '0', '14885', '0', '0', 'culling SAY_PHASE209');
INSERT INTO `creature_text` VALUES ('26499', '15', '0', 'Champions, meet me at the Town Hall at once. We must take the fight to Mal\'Ganis.', '14', '0', '100', '0', '0', '14297', '0', '0', 'culling SAY_PHASE210');
INSERT INTO `creature_text` VALUES ('26499', '16', '0', 'Follow me, I know the way through.', '12', '0', '100', '0', '0', '14298', '0', '0', 'culling SAY_PHASE301');
INSERT INTO `creature_text` VALUES ('26499', '17', '0', 'Yes, I\'m glad I could get to you before the plague.', '12', '0', '100', '0', '0', '14299', '0', '0', 'culling SAY_PHASE303');
INSERT INTO `creature_text` VALUES ('26499', '18', '0', 'What is this sorcery?', '12', '0', '100', '0', '0', '14300', '0', '0', 'culling SAY_PHASE304');
INSERT INTO `creature_text` VALUES ('26499', '19', '0', 'Mal\'Ganis appears to have more than scourge in his arsenal. We should make haste.', '12', '0', '100', '0', '0', '14301', '0', '0', 'culling SAY_PHASE306. NEEDS VERIFICATION');
INSERT INTO `creature_text` VALUES ('26499', '20', '0', 'More vile sorcery! Be ready for anything!', '12', '0', '100', '0', '0', '14302', '0', '0', 'culling SAY_PHASE307');
INSERT INTO `creature_text` VALUES ('26499', '21', '0', 'Let\'s move on.', '12', '0', '100', '0', '0', '14303', '0', '0', 'culling SAY_PHASE308');
INSERT INTO `creature_text` VALUES ('26499', '22', '0', 'Watch your backs: they have us surrounded in this hall.', '12', '0', '100', '0', '0', '14304', '0', '0', 'culling SAY_PHASE309');
INSERT INTO `creature_text` VALUES ('26499', '23', '0', 'One less obstacle to deal with.', '12', '0', '100', '0', '0', '0', '0', '0', 'culling SAY_PHASE310. NEEDS VERIFICATION');
INSERT INTO `creature_text` VALUES ('26499', '24', '0', 'Mal\'Ganis is not making this easy.', '12', '0', '100', '0', '0', '14305', '0', '0', 'culling SAY_PHASE311');
INSERT INTO `creature_text` VALUES ('26499', '25', '0', 'They\'re very persistent.', '12', '0', '100', '0', '0', '14306', '0', '0', 'culling SAY_PHASE312');
INSERT INTO `creature_text` VALUES ('26499', '26', '0', 'What else can he put in my way?', '12', '0', '100', '0', '0', '14307', '0', '0', 'culling SAY_PHASE313');
INSERT INTO `creature_text` VALUES ('26499', '27', '0', 'I do what I must for Lordaeron, and neither your words nor your actions will stop me.', '12', '0', '100', '0', '0', '14309', '0', '0', 'culling SAY_PHASE315');
INSERT INTO `creature_text` VALUES ('26499', '28', '0', 'The quickest path to Mal\'Ganis lies behind that bookshelf ahead.', '12', '0', '100', '0', '0', '14308', '0', '0', 'culling SAY_PHASE401');
INSERT INTO `creature_text` VALUES ('26499', '29', '0', 'This will only take a moment.', '12', '0', '100', '0', '0', '14310', '0', '0', 'culling SAY_PHASE402');
INSERT INTO `creature_text` VALUES ('26499', '30', '0', 'I\'m relieved this secret passage still works.', '12', '0', '100', '0', '0', '14311', '0', '0', 'culling SAY_PHASE403');
INSERT INTO `creature_text` VALUES ('26499', '31', '0', 'Let\'s move through here as quickly as possible. If the undead don\'t kill us, the fires might.', '12', '0', '100', '0', '0', '14312', '0', '0', 'culling SAY_PHASE404');
INSERT INTO `creature_text` VALUES ('26499', '32', '0', 'Rest a moment and clear your lungs, but we must move again soon.', '12', '0', '100', '0', '0', '14313', '0', '0', 'culling SAY_PHASE405');
INSERT INTO `creature_text` VALUES ('26499', '33', '0', 'That\'s enough; we must move again. Mal\'Ganis awaits.', '12', '0', '100', '0', '0', '14314', '0', '0', 'culling SAY_PHASE406');
INSERT INTO `creature_text` VALUES ('26499', '34', '0', 'At last some good luck. Market Row has not caught fire yet. Mal\'Ganis is supposed to be in Crusaders\' Square, which is just ahead. Tell me when you\'re ready to move forward.', '12', '0', '100', '0', '0', '14315', '0', '0', 'culling SAY_PHASE407');
INSERT INTO `creature_text` VALUES ('26499', '35', '0', 'Justice will be done.', '12', '0', '100', '0', '0', '14316', '0', '0', 'culling SAY_PHASE501');
INSERT INTO `creature_text` VALUES ('26499', '36', '0', 'We\'re going to finish this right now, Mal\'Ganis. Just you... and me.', '12', '0', '100', '0', '0', '14317', '0', '0', 'culling SAY_PHASE502');
INSERT INTO `creature_text` VALUES ('26499', '37', '0', 'I\'ll hunt you to the ends of the earth if I have to! Do you hear me? To the ends of the earth!', '12', '0', '100', '0', '0', '14318', '0', '0', 'culling SAY_PHASE503');
INSERT INTO `creature_text` VALUES ('26499', '38', '0', 'You performed well this day. Anything that Mal\'Ganis has left behind is yours. Take it as your reward. I must now begin plans for an expedition to Northrend.', '12', '0', '100', '0', '0', '14319', '0', '0', 'culling SAY_PHASE504');
INSERT INTO `creature_text` VALUES ('26528', '0', '0', 'Watch your tone with me, boy. You may be the prince, but I\'m still your superior as a paladin!', '12', '0', '100', '0', '0', '12839', '0', '0', 'culling SAY_PHASE102');
INSERT INTO `creature_text` VALUES ('26528', '1', '0', 'What?', '12', '0', '100', '0', '0', '12840', '38871', '0', 'culling SAY_PHASE105');
INSERT INTO `creature_text` VALUES ('26528', '2', '0', 'How can you even consider that? There\'s got to be some other way.', '12', '0', '100', '0', '0', '12841', '0', '0', 'culling SAY_PHASE107');
INSERT INTO `creature_text` VALUES ('26528', '3', '0', 'You are not my king yet, boy! Nor would I obey that command even if you were!', '12', '0', '100', '0', '0', '12842', '0', '0', 'culling SAY_PHASE109');
INSERT INTO `creature_text` VALUES ('26528', '4', '0', 'Treason? Have you lost your mind, Arthas?', '12', '0', '100', '0', '0', '12843', '0', '0', 'culling SAY_PHASE111');
INSERT INTO `creature_text` VALUES ('26528', '5', '0', 'You\'ve just crossed a terrible threshold, Arthas.', '12', '0', '100', '0', '0', '12844', '0', '0', 'culling SAY_PHASE115');
INSERT INTO `creature_text` VALUES ('26497', '0', '0', 'Arthas! You can\'t just--', '12', '0', '100', '0', '0', '12837', '0', '0', 'culling SAY_PHASE113');
INSERT INTO `creature_text` VALUES ('26497', '1', '0', 'I\'m sorry, Arthas. I can\'t watch you do this.', '12', '0', '100', '0', '0', '12838', '0', '0', 'culling SAY_PHASE117');
INSERT INTO `creature_text` VALUES ('28167', '0', '0', 'Prince Arthas, may the light be praised! Many people in the town have begun to fall seriously ill, can you help us?', '12', '0', '100', '0', '0', '0', '0', '0', 'culling SAY_PHASE202');
INSERT INTO `creature_text` VALUES ('28169', '0', '0', 'What? This can\'t be!', '12', '0', '100', '0', '0', '0', '0', '0', 'culling SAY_PHASE204');
INSERT INTO `creature_text` VALUES ('31126', '0', '0', 'Ah, you\'ve finally arrived Prince Arthas. You\'re here just in the nick of time.', '12', '0', '100', '0', '0', '0', '0', '0', 'culling SAY_PHASE302');
INSERT INTO `creature_text` VALUES ('31126', '1', '0', 'There\'s no need for you to understand, Arthas. All you need to do is die.', '12', '0', '100', '0', '0', '0', '0', '0', 'culling SAY_PHASE305');
INSERT INTO `creature_text` VALUES ('26533', '0', '0', 'Yes, this is the beginning. I\'ve been waiting for you, young prince. I am Mal\'Ganis.', '14', '0', '100', '0', '0', '14410', '0', '0', 'malganis SAY_INTRO_1 | culling SAY_PHASE206');
INSERT INTO `creature_text` VALUES ('26533', '1', '0', 'As you can see, your people are now mine. I will now turn this city household by household, until the flame of life has been snuffed out... forever.', '14', '0', '100', '0', '0', '14411', '0', '0', 'malganis SAY_INTRO_2 | culling SAY_PHASE207');
INSERT INTO `creature_text` VALUES ('26533', '2', '0', 'This will be a fine test...Prince Arthas...', '14', '0', '100', '0', '0', '14413', '0', '0', 'malganis SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('26533', '3', '0', 'All too easy...', '14', '0', '100', '0', '0', '14416', '0', '0', 'malganis SAY_KILL_1');
INSERT INTO `creature_text` VALUES ('26533', '3', '1', 'The dark lord is displeased with your interference...', '14', '0', '100', '0', '0', '14417', '0', '0', 'malganis SAY_KILL_2');
INSERT INTO `creature_text` VALUES ('26533', '3', '2', 'It is Prince Arthas I want... not you...', '14', '0', '100', '0', '0', '14418', '0', '0', 'malganis SAY_KILL_3');
INSERT INTO `creature_text` VALUES ('26533', '4', '0', 'Anak\'Keri...', '14', '0', '100', '0', '0', '14422', '0', '0', 'malganis SAY_SLAY_1');
INSERT INTO `creature_text` VALUES ('26533', '4', '1', 'My onslaught will wash over the Lich King\'s forces...', '14', '0', '100', '0', '0', '14423', '0', '0', 'malganis SAY_SLAY_2');
INSERT INTO `creature_text` VALUES ('26533', '4', '2', 'Your death is in vain, tiny mortal...', '14', '0', '100', '0', '0', '14424', '0', '0', 'malganis SAY_SLAY_3');
INSERT INTO `creature_text` VALUES ('26533', '4', '3', 'Your time has come to an end!', '14', '0', '100', '0', '0', '14425', '0', '0', 'malganis SAY_SLAY_4');
INSERT INTO `creature_text` VALUES ('26533', '5', '0', 'Time out...', '14', '0', '100', '0', '0', '14414', '0', '0', 'malganis SAY_SLEEP_1');
