INSERT INTO `creature_text` VALUES ('26533', '6', '0', 'I spent too much time in that weak little shell...', '14', '0', '100', '0', '0', '14426', '0', '0', 'malganis SAY_30HEALTH');
INSERT INTO `creature_text` VALUES ('26533', '7', '0', '(Eredun) I AM MAL\'GANIS! I AM ETERNAL!', '14', '0', '100', '0', '0', '14427', '0', '0', 'malganis SAY_15HEALTH');
INSERT INTO `creature_text` VALUES ('26533', '8', '0', 'ENOUGH! I waste my time here...I must gather my strength on the home world...', '14', '0', '100', '0', '0', '14428', '0', '0', 'malganis SAY_ESCAPE_SPEECH_1');
INSERT INTO `creature_text` VALUES ('26533', '9', '0', 'You\'ll never defeat the Lich King without my forces! I\'ll have my revenge...on him, AND you...', '14', '0', '100', '0', '0', '14429', '0', '0', 'malganis SAY_ESCAPE_SPEECH_2');
INSERT INTO `creature_text` VALUES ('26533', '10', '0', 'Your journey has just begun, young prince. Gather your forces and meet me in the artic land of Northrend. It is there that we shall settle the score between us. It is there that your true destiny will unfold.', '14', '0', '100', '0', '0', '14412', '0', '0', 'malganis SAY_OUTRO');
INSERT INTO `creature_text` VALUES ('17879', '0', '0', 'Why do you aid the Magus? Just think of how many lives could be saved if the portal is never opened, if the resulting wars could be erased ...', '14', '0', '100', '0', '0', '10412', '0', '0', 'chrono lord deja SAY_ENTER');
INSERT INTO `creature_text` VALUES ('17879', '1', '0', 'If you will not cease this foolish quest, then you will die!', '14', '0', '100', '0', '0', '10414', '0', '0', 'chrono lord deja SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('17879', '2', '0', 'You have outstayed your welcome, Timekeeper. Begone!', '14', '0', '100', '0', '0', '10413', '0', '0', 'chrono lord deja SAY_BANISH');
INSERT INTO `creature_text` VALUES ('17879', '3', '0', 'I told you it was a fool\'s quest!', '14', '0', '100', '0', '0', '10415', '0', '0', 'chrono lord deja SAY_SLAY1');
INSERT INTO `creature_text` VALUES ('17879', '3', '1', 'Leaving so soon?', '14', '0', '100', '0', '0', '10416', '0', '0', 'chrono lord deja SAY_SLAY2');
INSERT INTO `creature_text` VALUES ('17879', '4', '0', 'Time ... is on our side.', '14', '0', '100', '0', '0', '10417', '0', '0', 'chrono lord deja SAY_DEATH');
INSERT INTO `creature_text` VALUES ('17881', '0', '0', 'The time has come to shatter this clockwork universe forever! Let us no longer be slaves of the hourglass! I warn you: those who do not embrace the greater path shall become victims of its passing!', '14', '0', '100', '0', '0', '10400', '0', '0', 'aeonus SAY_ENTER');
INSERT INTO `creature_text` VALUES ('17881', '1', '0', 'Let us see what fate lays in store...', '14', '0', '100', '0', '0', '10402', '0', '0', 'aeonus SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('17881', '2', '0', 'Your time is up, slave of the past!', '14', '0', '100', '0', '0', '10401', '0', '0', 'aeonus SAY_BANISH');
INSERT INTO `creature_text` VALUES ('17881', '3', '0', 'One less obstacle in our way!', '14', '0', '100', '0', '0', '10403', '0', '0', 'aeonus SAY_SLAY1');
INSERT INTO `creature_text` VALUES ('17881', '3', '1', 'No one can stop us! No one!', '14', '0', '100', '0', '0', '10404', '0', '0', 'aeonus SAY_SLAY2');
INSERT INTO `creature_text` VALUES ('17881', '4', '0', 'It is only a matter...of time.', '14', '0', '100', '0', '0', '10405', '0', '0', 'aeonus SAY_DEATH');
INSERT INTO `creature_text` VALUES ('17881', '5', '0', 'goes into a frenzy!', '16', '0', '100', '0', '0', '0', '0', '0', 'aeonus EMOTE_FRENZY');
INSERT INTO `creature_text` VALUES ('15608', '0', '0', 'The time has come! Gul\'dan, order your warlocks to double their efforts! Moments from now the gateway will open and your Horde will be released upon this ripe, unsuspecting world!', '14', '0', '100', '0', '0', '10435', '0', '0', 'medivh SAY_ENTER');
INSERT INTO `creature_text` VALUES ('15608', '1', '0', 'What is this? Champions, coming to my aid? I sense the hand of the dark one in this. Truly this sacred event bears his blessing?', '14', '0', '100', '0', '0', '10436', '0', '0', 'medivh SAY_INTRO');
INSERT INTO `creature_text` VALUES ('15608', '2', '0', 'Champions! My shield grows weak!', '14', '0', '100', '0', '0', '10437', '0', '0', 'medivh SAY_WEAK75');
INSERT INTO `creature_text` VALUES ('15608', '3', '0', 'My powers must be concentrated on the portal! I do not have time to hold the shield!', '14', '0', '100', '0', '0', '10438', '0', '0', 'medivh SAY_WEAK50');
INSERT INTO `creature_text` VALUES ('15608', '4', '0', 'The shield is nearly gone! All that I have worked for is in danger!', '14', '0', '100', '0', '0', '10439', '0', '0', 'medivh SAY_WEAK25');
INSERT INTO `creature_text` VALUES ('15608', '5', '0', 'No! Damn this feeble, mortal coil!', '14', '0', '100', '0', '0', '10441', '0', '0', 'medivh SAY_DEATH');
INSERT INTO `creature_text` VALUES ('15608', '6', '0', 'I am grateful for your aid, champions. Now, Gul\'dan\'s Horde will sweep across this world like a locust swarm, and all my designs, all my carefully-laid plans will at last fall into place.', '14', '0', '100', '0', '0', '10440', '0', '0', 'medivh SAY_WIN');
INSERT INTO `creature_text` VALUES ('15608', '7', '0', 'Orcs of the Horde! This portal is the gateway to your new destiny! Azeroth lies before you, ripe for the taking!', '14', '0', '100', '0', '0', '0', '0', '0', 'medivh SAY_ORCS_ENTER');
INSERT INTO `creature_text` VALUES ('15608', '8', '0', 'Gul\'dan speaks the truth! We should return at once to tell our brothers of the news! Retreat back trought the portal!', '14', '0', '100', '0', '0', '0', '0', '0', 'medivh SAY_ORCS_ANSWER');
INSERT INTO `creature_text` VALUES ('17880', '0', '0', 'Why do you persist? Surely you can see the futility of it all. It is not too late! You may still leave with your lives ...', '14', '0', '100', '0', '0', '10442', '0', '0', 'temporus SAY_ENTER');
INSERT INTO `creature_text` VALUES ('17880', '1', '0', 'So be it ... you have been warned.', '14', '0', '100', '0', '0', '10444', '0', '0', 'temporus SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('17880', '2', '0', 'Time... sands of time is run out for you.', '14', '0', '100', '0', '0', '10443', '0', '0', 'temporus SAY_BANISH');
INSERT INTO `creature_text` VALUES ('17880', '3', '0', 'You should have left when you had the chance.', '14', '0', '100', '0', '0', '10445', '0', '0', 'temporus SAY_SLAY1');
INSERT INTO `creature_text` VALUES ('17880', '3', '1', 'Your days are done.', '14', '0', '100', '0', '0', '10446', '0', '0', 'temporus SAY_SLAY2');
INSERT INTO `creature_text` VALUES ('17880', '4', '0', 'My death means ... little.', '14', '0', '100', '0', '0', '10447', '0', '0', 'temporus SAY_DEATH');
INSERT INTO `creature_text` VALUES ('23002', '0', '0', 'You, there! Hand over that moonstone and nobody gets hurt!', '14', '0', '100', '0', '0', '0', '0', '0', 'sprysprocket SAY_START');
INSERT INTO `creature_text` VALUES ('23002', '1', '0', 'Just chill!', '15', '0', '100', '0', '0', '0', '0', '0', 'sprysprocket SAY_WHISPER_CHILL');
INSERT INTO `creature_text` VALUES ('23002', '2', '0', 'All right, you win! I surrender! Just don\'t hurt me!', '14', '0', '100', '0', '0', '0', '0', '0', 'sprysprocket SAY_END');
INSERT INTO `creature_text` VALUES ('23002', '3', '0', '%s takes the Southfury moonstone and escapes into the river. Follow her!', '41', '0', '100', '0', '0', '0', '0', '0', 'sprysprocket EMOTE_START');
INSERT INTO `creature_text` VALUES ('3568', '0', '0', 'Mist! I feared I would never see you again! Yes, I am well, do not worry for me. You must rest and recover your health.', '12', '0', '100', '0', '0', '0', '0', '0', 'mist SAY_AT_HOME');
INSERT INTO `creature_text` VALUES ('3568', '1', '0', '%s growls in acknowledgement before straightening and making her way off into the forest.', '16', '0', '100', '0', '0', '0', '0', '0', 'mist EMOTE_AT_HOME');
INSERT INTO `creature_text` VALUES ('15381', '0', '0', 'We must act quickly or all shall be lost!', '12', '0', '100', '0', '0', '0', '0', '0', 'anachronos the ancient ANACHRONOS_SAY_1');
INSERT INTO `creature_text` VALUES ('15381', '1', '0', 'NOW, STAGHELM! WE GO NOW! Prepare your magic!', '12', '0', '100', '0', '0', '0', '0', '0', 'anachronos the ancient ANACHRONOS_SAY_2');
INSERT INTO `creature_text` VALUES ('15381', '2', '0', 'Stay close...', '12', '0', '100', '0', '0', '0', '0', '0', 'anachronos the ancient ANACHRONOS_SAY_3');
INSERT INTO `creature_text` VALUES ('15381', '3', '0', 'The sands of time will halt, but only for a moment! I will now conjure the barrier.', '12', '0', '100', '0', '0', '0', '0', '0', 'anachronos the ancient ANACHRONOS_SAY_4');
INSERT INTO `creature_text` VALUES ('15381', '4', '0', 'FINISH THE SPELL STAGHELM! I CANNOT HOLD THE GLYPHS OF WARDING IN PLACE MUCH LONGER! CALL FORTH THE ROOTS!', '12', '0', '100', '0', '0', '0', '0', '0', 'anachronos the ancient ANACHRONOS_SAY_5');
INSERT INTO `creature_text` VALUES ('15381', '5', '0', 'It... It is over, Lord Staghelm. We are victorious. Albeit the cost for this victory was great.', '12', '0', '100', '0', '0', '0', '0', '0', 'anachronos the ancient ANACHRONOS_SAY_6');
INSERT INTO `creature_text` VALUES ('15381', '6', '0', 'There is but one duty that remains...', '12', '0', '100', '0', '0', '0', '0', '0', 'anachronos the ancient ANACHRONOS_SAY_7');
INSERT INTO `creature_text` VALUES ('15381', '7', '0', 'Before I leave this place, I make one final offreing to you, Lord Staghelm. Should a time arise in which you must gain entry to this accursed fortress, use the Scepter of the Shifting Sands on the sacred gong. The magic holding the barrier together will dissipate and the horrors of Ahn\'Qiraj will be unleashed upon the world once more.', '12', '0', '100', '0', '0', '0', '0', '0', 'anachronos the ancient ANACHRONOS_SAY_8');
INSERT INTO `creature_text` VALUES ('15381', '8', '0', 'Lord Staghelm, where are you going? You would shatter our bond for the sake of pride?', '12', '0', '100', '0', '0', '0', '0', '0', 'anachronos the ancient ANACHRONOS_SAY_9');
INSERT INTO `creature_text` VALUES ('15381', '9', '0', 'And now you know all that there is to know, mortal...', '12', '0', '100', '0', '0', '0', '0', '0', 'anachronos the ancient ANACHRONOS_SAY_10');
INSERT INTO `creature_text` VALUES ('15381', '10', '0', 'hands the Scepter of the Shifting Sands to Fandral Staghelm.', '16', '0', '100', '0', '0', '0', '0', '0', 'anachronos the ancient ANACHRONOS_EMOTE_1');
INSERT INTO `creature_text` VALUES ('15381', '11', '0', 'shakes his head in dissapointment.', '16', '0', '100', '0', '0', '0', '0', '0', 'anachronos the ancient ANACHRONOS_EMOTE_2');
INSERT INTO `creature_text` VALUES ('15381', '12', '0', 'kneels down to pick up the fragments of the shattered scepter.', '16', '0', '100', '0', '0', '0', '0', '0', 'anachronos the ancient ANACHRONOS_EMOTE_3');
INSERT INTO `creature_text` VALUES ('15382', '0', '0', 'My forces cannot overcome the Qiraji defenses. We will not be able to get close enough to place your precious barrier, dragon.', '12', '0', '100', '0', '0', '0', '0', '0', 'anachronos the ancient FANDRAL_SAY_1');
INSERT INTO `creature_text` VALUES ('15382', '1', '0', 'It is done dragon. Lead the way...', '12', '0', '100', '0', '0', '0', '0', '0', 'anachronos the ancient FANDRAL_SAY_2');
INSERT INTO `creature_text` VALUES ('15382', '2', '0', 'Ancient ones guide my hand... Wake from your slumber! WAKE AND SEAL THIS CURSED PLACE!', '12', '0', '100', '0', '0', '0', '0', '0', 'anachronos the ancient FANDRAL_SAY_3');
INSERT INTO `creature_text` VALUES ('15382', '3', '0', 'After the savagery that my people have witnessed and felt, you expect me to accept another burden, dragon? Surely you are mad.', '12', '0', '100', '0', '0', '0', '0', '0', 'anachronos the ancient FANDRAL_SAY_4');
INSERT INTO `creature_text` VALUES ('15382', '4', '0', 'I want nothing to do with Silithus, the Qiraji and least of all, any damned dragons!', '12', '0', '100', '0', '0', '0', '0', '0', 'anachronos the ancient FANDRAL_SAY_5');
INSERT INTO `creature_text` VALUES ('15382', '5', '0', 'My son\'s soul will find no comfort in this hollow victory, dragon. I will have him back.Thought it takes a millennia, I WILL have my son back!', '12', '0', '100', '0', '0', '0', '0', '0', 'anachronos the ancient FANDRAL_SAY_6');
INSERT INTO `creature_text` VALUES ('15382', '6', '0', 'falls to one knee - exhausted.', '16', '0', '100', '0', '0', '0', '0', '0', 'anachronos the ancient FANDRAL_EMOTE_1');
