INSERT INTO `creature_text` VALUES ('15379', '0', '0', 'Aye, Fandral, remember these words: Let not your grief guide your faith. These thoughts you hold... dark places you go, night elf. Absolution cannot be had through misguided vengeance.', '12', '0', '100', '0', '0', '0', '0', '0', 'anachronos the ancient CAELESTRASZ_SAY_1');
INSERT INTO `creature_text` VALUES ('15379', '1', '0', 'Do not forget the sacrifices made on this day, night elf. We have all suffered immensely at the hands of these beasts.', '12', '0', '100', '0', '0', '0', '0', '0', 'anachronos the ancient CAELESTRASZ_SAY_2');
INSERT INTO `creature_text` VALUES ('15379', '2', '0', 'Alexstrasza grant me the resolve to drive our enemies back!', '14', '0', '100', '0', '0', '0', '0', '0', 'anachronos the ancient CAELESTRASZ_YELL_1');
INSERT INTO `creature_text` VALUES ('15380', '0', '0', 'This distraction will give you and the young druid time enough to seal the gate. Do not falter. Now, let us see how they deal with chaotic magic.', '12', '0', '100', '0', '0', '0', '0', '0', 'anachronos the ancient ARYGOS_SAY_1');
