INSERT INTO `creature_text` VALUES ('17312', '5', '0', '%s hugs her father.', '16', '0', '100', '0', '0', '0', '0', '0', 'magwin EMOTE_HUG');
INSERT INTO `creature_text` VALUES ('17243', '4', '0', 'The Master has gone to great lengths to secure information about the whereabouts of the Exodar. You could have blown the entire operation, including the cover of our spy on the inside.', '12', '0', '100', '0', '0', '0', '0', '0', 'geezle SPARK_SAY_3');
INSERT INTO `creature_text` VALUES ('17318', '1', '0', 'Relax, Spark! I have it all under control. We\'ll strip mine the Exodar right out from under \'em - making both you and I very, very rich in the process.', '12', '0', '100', '0', '0', '0', '0', '0', 'geezle GEEZLE_SAY_4');
INSERT INTO `creature_text` VALUES ('17243', '3', '0', 'What\'s the big idea? You nearly blew my cover, idiot! I told you to put the compass and navigation maps somewhere safe - not out in the open for any fool to discover.', '12', '0', '100', '0', '0', '0', '0', '0', 'geezle SPARK_SAY_2');
INSERT INTO `creature_text` VALUES ('17243', '1', '0', '%s puts the shell to his ear.', '16', '0', '100', '0', '0', '0', '0', '0', 'engineer spark EMOTE_SHELL');
INSERT INTO `creature_text` VALUES ('17243', '2', '0', 'Now I cut you!', '14', '0', '100', '0', '0', '0', '0', '0', 'engineer spark SAY_ATTACK');
INSERT INTO `creature_text` VALUES ('17318', '2', '0', 'Yes, sir. It won\'t happen again...', '12', '0', '100', '0', '0', '0', '0', '0', 'geezle GEEZLE_SAY_7');
