INSERT INTO `creature_text` VALUES ('11218', '0', '0', 'Liladris has been waiting for me at Maestra\'s Post, so we should make haste, $N.', '12', '0', '100', '0', '0', '0', '0', '0', 'kerlonian SAY_KER_START');
