INSERT INTO `creature_text` VALUES ('11218', '5', '0', '%s wakes up!', '16', '0', '100', '0', '0', '0', '6612', '0', 'kerlonian EMOTE_KER_AWAKEN');
INSERT INTO `creature_text` VALUES ('2917', '0', '0', 'Ok, $n, let\'s go find where I left that mysterious fossil. Follow me!', '12', '0', '100', '0', '0', '0', '0', '0', 'remtravel SAY_REM_START');
