INSERT INTO `creature_text` VALUES ('2917', '10', '0', 'Ah. I remember now! I gave the mysterious fossil to Hollee! Check with her.', '12', '0', '100', '0', '0', '0', '0', '0', 'remtravel SAY_REM_REMEMBER');
