INSERT INTO `creature_text` VALUES ('4966', '0', '0', 'Why don\'t we deal with you now, Hendel? Lady Proudmoore will speak for you back in the tower.', '12', '0', '100', '0', '0', '0', '0', '0', 'hendel SAY_PROGRESS_1_TER');
