INSERT INTO `creature_text` VALUES ('3678', '0', '0', 'At last! Naralex can be awakened! Come aid me, brave adventurers!', '14', '0', '100', '0', '0', '0', '0', '0', 'disciple SAY_AT_LAST');
INSERT INTO `creature_text` VALUES ('3678', '1', '0', 'I must make the necessary preparations before the awakening ritual can begin. You must protect me!', '12', '0', '100', '0', '0', '0', '0', '0', 'disciple SAY_MAKE_PREPARATIONS');
INSERT INTO `creature_text` VALUES ('3678', '2', '0', 'These caverns were once a temple of promise for regrowth in the Barrens. Now, they are the halls of nightmares.', '12', '0', '100', '0', '0', '0', '0', '0', 'disciple SAY_TEMPLE_OF_PROMISE');
INSERT INTO `creature_text` VALUES ('3678', '3', '0', 'Come. We must continue. There is much to be done before we can pull Naralex from his nightmare.', '12', '0', '100', '0', '0', '0', '0', '0', 'disciple SAY_MUST_CONTINUE');
INSERT INTO `creature_text` VALUES ('3678', '4', '0', 'Within this circle of fire I must cast the spell to banish the spirits of the slain Fanglords.', '12', '0', '100', '0', '0', '0', '0', '0', 'disciple SAY_BANISH_THE_SPIRITS');
INSERT INTO `creature_text` VALUES ('3678', '5', '0', 'The caverns have been purified. To Naralex\'s chamber we go!', '12', '0', '100', '0', '0', '0', '0', '0', 'disciple SAY_CAVERNS_PURIFIED');
INSERT INTO `creature_text` VALUES ('3678', '6', '0', 'Beyond this corridor, Naralex lies in fitful sleep. Let us go awaken him before it is too late.', '12', '0', '100', '0', '0', '0', '0', '0', 'disciple SAY_BEYOND_THIS_CORRIDOR');
INSERT INTO `creature_text` VALUES ('3678', '7', '0', 'Protect me brave souls as I delve into this Emerald Dream to rescue Naralex and put an end to this corruption!', '12', '0', '100', '0', '0', '0', '0', '0', 'disciple SAY_EMERALD_DREAM');
INSERT INTO `creature_text` VALUES ('3678', '8', '0', '%s begins to perform the awakening ritual on Naralex.', '16', '0', '100', '0', '0', '0', '0', '0', 'disciple EMOTE_AWAKENING_RITUAL');
INSERT INTO `creature_text` VALUES ('3679', '0', '0', '%s tosses fitfully in troubled sleep.', '16', '0', '100', '0', '0', '0', '0', '0', 'naralex EMOTE_TROUBLED_SLEEP');
INSERT INTO `creature_text` VALUES ('3679', '1', '0', '%s writhes in agony. The Disciple seems to be breaking through.', '16', '0', '100', '0', '0', '0', '0', '0', 'naralex EMOTE_WRITHE_IN_AGONY');
INSERT INTO `creature_text` VALUES ('3679', '2', '0', '%s dreams up a horrendous vision. Something stirs beneath the murky waters.', '16', '0', '100', '0', '0', '0', '0', '0', 'naralex EMOTE_HORRENDOUS_VISION');
INSERT INTO `creature_text` VALUES ('3678', '9', '0', 'This Mutanus the Devourer is a minion from Naralex\'s nightmare no doubt!', '12', '0', '100', '0', '0', '0', '0', '0', 'disciple SAY_MUTANUS_THE_DEVOURER');
INSERT INTO `creature_text` VALUES ('3679', '3', '0', 'I AM AWAKE, AT LAST!', '14', '0', '100', '0', '0', '0', '0', '0', 'naralex SAY_I_AM_AWAKE');
INSERT INTO `creature_text` VALUES ('3678', '10', '0', 'At last! Naralex awakes from the nightmare.', '12', '0', '100', '0', '0', '0', '0', '0', 'disciple SAY_NARALEX_AWAKES');
INSERT INTO `creature_text` VALUES ('3679', '4', '0', 'Ah, to be pulled from the dreaded nightmare! I thank you, my loyal Disciple, along with your brave companions.', '12', '0', '100', '0', '0', '0', '0', '0', 'naralex SAY_THANK_YOU');
INSERT INTO `creature_text` VALUES ('3679', '5', '0', 'We must go and gather with the other Disciples. There is much work to be done before I can make another attempt to restore the Barrens. Farewell, brave souls!', '12', '0', '100', '0', '0', '0', '0', '0', 'naralex SAY_FAREWELL');
INSERT INTO `creature_text` VALUES ('3678', '11', '0', 'Attacked! Help get this $N off of me!', '12', '0', '100', '0', '0', '0', '0', '0', 'disciple SAY_ATTACKED');
INSERT INTO `creature_text` VALUES ('7607', '0', '0', 'Oh no!  Here they come!', '12', '0', '100', '0', '0', '0', '0', '0', 'weegli blastfuse SAY_WEEGLI_OHNO');
INSERT INTO `creature_text` VALUES ('7607', '1', '0', 'Ok, here I go!', '12', '0', '100', '0', '0', '0', '0', '0', 'weegli blastfuse SAY_WEEGLI_OK_I_GO');
INSERT INTO `creature_text` VALUES ('29120', '0', '0', 'Eternal aggony awaits you!', '14', '0', '100', '0', '0', '14054', '0', '0', 'anub arak SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('29120', '1', '0', 'Soon, the Master\'s voice will call to you.', '14', '0', '100', '0', '0', '14057', '0', '0', 'anub arak SAY_SLAY_1');
INSERT INTO `creature_text` VALUES ('29120', '1', '1', 'You have made your choice.', '14', '0', '100', '0', '0', '14056', '0', '0', 'anub arak SAY_SLAY_2');
INSERT INTO `creature_text` VALUES ('29120', '1', '2', 'You shall experience my torment, first-hand!', '14', '0', '100', '0', '0', '14055', '0', '0', 'anub arak SAY_SLAY_3');
INSERT INTO `creature_text` VALUES ('29120', '2', '0', 'Ahhh... RAAAAAGH! Never thought... I would be free of him...', '14', '0', '100', '0', '0', '14069', '0', '0', 'anub arak SAY_DEATH');
INSERT INTO `creature_text` VALUES ('29120', '3', '0', 'Your armor is useless againts my locusts.', '14', '0', '100', '0', '0', '14060', '0', '0', 'anub arak SAY_LOCUST_1');
INSERT INTO `creature_text` VALUES ('29120', '3', '1', 'Uunak-hissss tik-k-k-k-k!', '14', '0', '100', '0', '0', '14067', '0', '0', 'anub arak SAY_LOCUST_2');
INSERT INTO `creature_text` VALUES ('29120', '3', '2', 'The pestilence upon you!', '14', '0', '100', '0', '0', '14068', '0', '0', 'anub arak SAY_LOCUST_3');
INSERT INTO `creature_text` VALUES ('29120', '4', '0', 'Auum na-l ak-k-k-k, isshhh.', '14', '0', '100', '0', '0', '14058', '0', '0', 'anub arak SAY_SUBMERGE_1');
INSERT INTO `creature_text` VALUES ('29120', '4', '1', 'Come forth my brethren! Feast on their flesh.', '14', '0', '100', '0', '0', '14059', '0', '0', 'anub arak SAY_SUBMERGE_2');
INSERT INTO `creature_text` VALUES ('29120', '5', '0', 'I was king of this empire once, long ago. In life I stood as champion. In death I returned as conqueror. Now I protect the kingdom once more. Ironic, yes? ', '14', '0', '100', '0', '0', '14053', '0', '0', 'anub arak SAY_INTRO');
INSERT INTO `creature_text` VALUES ('28684', '0', '0', 'This kingdom belongs to the Scourge! Only the dead may enter.', '14', '0', '100', '0', '0', '14075', '0', '0', 'krik thir SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('28684', '1', '0', 'You were foolish to come.', '14', '0', '100', '0', '0', '14077', '0', '0', 'krik thir SAY_SLAY_1');
INSERT INTO `creature_text` VALUES ('28684', '1', '1', 'As Anub\'Arak commands!', '14', '0', '100', '0', '0', '14078', '0', '0', 'krik thir SAY_SLAY_2');
INSERT INTO `creature_text` VALUES ('28684', '2', '0', 'I should be grateful. But I long ago lost the capacity.', '14', '0', '100', '0', '0', '14087', '0', '0', 'krik thir SAY_DEATH');
INSERT INTO `creature_text` VALUES ('28684', '3', '0', 'They hunger.', '14', '0', '100', '0', '0', '14085', '0', '0', 'krik thir SAY_SWARM_1');
INSERT INTO `creature_text` VALUES ('28684', '3', '1', 'Dinner time, my pets.', '14', '0', '100', '0', '0', '14086', '0', '0', 'krik thir SAY_SWARM_2');
INSERT INTO `creature_text` VALUES ('28684', '4', '0', 'Keep an eye on the tunnel. We must not let anyone through!', '14', '0', '100', '0', '0', '14082', '0', '0', 'krik thir SAY_PREFIGHT_1');
INSERT INTO `creature_text` VALUES ('28684', '4', '1', 'I hear footsteps. Be on your guard.', '14', '0', '100', '0', '0', '14083', '0', '0', 'krik thir SAY_PREFIGHT_2');
INSERT INTO `creature_text` VALUES ('28684', '4', '2', 'I sense the living. Be ready.', '14', '0', '100', '0', '0', '14084', '0', '0', 'krik thir SAY_PREFIGHT_3');
INSERT INTO `creature_text` VALUES ('28684', '5', '0', 'We are besieged. Strike out and bring back their corpses!', '14', '0', '100', '0', '0', '14079', '0', '0', 'krik thir SAY_SEND_GROUP_1');
INSERT INTO `creature_text` VALUES ('28684', '5', '1', 'We must hold the gate. Attack! Tear them limb from limb!', '14', '0', '100', '0', '0', '14080', '0', '0', 'krik thir SAY_SEND_GROUP_2');
INSERT INTO `creature_text` VALUES ('28684', '5', '2', 'The gate must be protected at all costs. Rip them to shreds!', '14', '0', '100', '0', '0', '14081', '0', '0', 'krik thir SAY_SEND_GROUP_3');
INSERT INTO `creature_text` VALUES ('28189', '0', '0', 'My liege, the infiltration and control of the Alliance power structure by our cultists is well underway.', '12', '0', '100', '0', '0', '0', '0', '0', 'thassarian SAY_TALBOT_1');
INSERT INTO `creature_text` VALUES ('28189', '1', '0', 'The power you\'ve bestowed upon me has allowed me great mental influence over human minds. I bear these offerings as proof of my progress.', '12', '0', '100', '0', '0', '0', '0', '0', 'thassarian SAY_TALBOT_2');
INSERT INTO `creature_text` VALUES ('28189', '2', '0', 'Allow me to take care of the intruders, lord. I will feed their entrails to the maggots.', '12', '0', '100', '0', '0', '0', '0', '0', 'thassarian SAY_TALBOT_3');
INSERT INTO `creature_text` VALUES ('28189', '3', '0', 'Yes, my lord!', '12', '0', '100', '0', '0', '0', '25365', '0', 'thassarian SAY_TALBOT_4');
INSERT INTO `creature_text` VALUES ('26203', '0', '0', 'Your progress in this region has been impressive, Blood Prince. I am pleased...', '12', '0', '100', '0', '0', '0', '0', '0', 'thassarian SAY_LICH_1');
INSERT INTO `creature_text` VALUES ('26203', '1', '0', 'Now this is a surprise, Thassarian. I hadn\'t heard from Mograine or the other death knights for months. You\'ve come to rejoin the Scourge, I take it?', '12', '0', '100', '0', '0', '0', '0', '0', 'thassarian SAY_LICH_2');
INSERT INTO `creature_text` VALUES ('26203', '2', '0', 'Do not fail me, San\'layn. Return to Icecrown with this fool\'s head or do not bother to return.', '12', '0', '100', '0', '0', '0', '0', '0', 'thassarian SAY_LICH_3');
INSERT INTO `creature_text` VALUES ('26170', '0', '0', 'Leryssa!', '14', '0', '100', '0', '0', '0', '0', '0', 'thassarian SAY_THASSARIAN_1');
INSERT INTO `creature_text` VALUES ('26170', '1', '0', 'What have you done to my sister, you motherless elf scum!?', '14', '0', '100', '0', '0', '0', '0', '0', 'thassarian SAY_THASSARIAN_2');
INSERT INTO `creature_text` VALUES ('26170', '2', '0', 'I would sooner slit my own throat. You will pay for what you did to your own men, Arthas... for what you did to me! I swear it.', '12', '0', '100', '0', '0', '0', '0', '0', 'thassarian SAY_THASSARIAN_3');
INSERT INTO `creature_text` VALUES ('26170', '3', '0', 'Leryssa... you... you\'re all right!', '12', '0', '100', '0', '0', '0', '0', '0', 'thassarian SAY_THASSARIAN_4');
INSERT INTO `creature_text` VALUES ('26170', '4', '0', 'I cannot return home with you just yet, Leryssa. I am not quite done with the Scourge.', '12', '0', '100', '0', '0', '0', '0', '0', 'thassarian SAY_THASSARIAN_5');
INSERT INTO `creature_text` VALUES ('26170', '5', '0', 'You might be right, sister. My obligations to my land and King have been fulfilled. But there is still something that I owe to myself.', '12', '0', '100', '0', '0', '0', '0', '0', 'thassarian SAY_THASSARIAN_6');
INSERT INTO `creature_text` VALUES ('26170', '6', '0', 'Do not worry, Leryssa. I will come back to you when I\'m done. Nothing in the world will stop me from coming home to the only family that I have left.', '12', '0', '100', '0', '0', '0', '0', '0', 'thassarian SAY_THASSARIAN_7');
INSERT INTO `creature_text` VALUES ('25250', '0', '0', 'What... what happened to me?', '12', '0', '100', '0', '0', '0', '25841', '0', 'thassarian SAY_ARLOS_1');
INSERT INTO `creature_text` VALUES ('25250', '1', '0', 'Ugh! My head won\'t stop spinning...', '12', '0', '100', '0', '0', '0', '0', '0', 'thassarian SAY_ARLOS_2');
INSERT INTO `creature_text` VALUES ('25251', '0', '0', 'Thassarian, you\'re alive!', '14', '0', '100', '0', '0', '0', '0', '0', 'thassarian SAY_LERYSSA_1');
INSERT INTO `creature_text` VALUES ('25251', '1', '0', 'I thought... I thought you were... dead.', '12', '0', '100', '0', '0', '0', '0', '0', 'thassarian SAY_LERYSSA_2');
INSERT INTO `creature_text` VALUES ('25251', '2', '0', 'Don\'t leave me again! You want to fight for your country, but they don\'t even want you! They sent you here to die!', '12', '0', '100', '0', '0', '0', '0', '0', 'thassarian SAY_LERYSSA_3');
INSERT INTO `creature_text` VALUES ('25251', '3', '0', 'I know that look in your eye... I\'m not going to be able to talk you out of this. If you die on me again...', '12', '0', '100', '0', '0', '0', '0', '0', 'thassarian SAY_LERYSSA_4');
INSERT INTO `creature_text` VALUES ('25504', '0', '0', 'My father\'s aura is quite strong,he cannot be far. Could you be a doll and fight off the monsters wandering throught the mist?', '12', '0', '100', '0', '0', '0', '0', '0', 'mootoo the younger SAY_1');
INSERT INTO `creature_text` VALUES ('25504', '1', '0', 'Watch out for the monsters!Which way should we go first? Let\'s try this way...', '12', '0', '100', '0', '0', '0', '0', '0', 'mootoo the younger SAY_2');
INSERT INTO `creature_text` VALUES ('25504', '2', '0', 'What could this be?', '12', '0', '100', '0', '0', '0', '0', '0', 'mootoo the younger SAY_3');
INSERT INTO `creature_text` VALUES ('25504', '3', '0', 'There\'s no sign of it ending! Where could my father be?', '12', '0', '100', '0', '0', '0', '0', '0', 'mootoo the younger SAY_4');
INSERT INTO `creature_text` VALUES ('25504', '4', '0', 'Father! You\'re safe!', '12', '0', '100', '0', '0', '0', '0', '0', 'mootoo the younger SAY_5');
INSERT INTO `creature_text` VALUES ('25478', '0', '0', 'Pathetic fool! A servant of Malygos would sooner die than aid an enemy...', '12', '0', '100', '0', '0', '0', '0', '0', 'imprisoned beryl sorcerer SAY_IMPRISIONED_BERYL_1');
INSERT INTO `creature_text` VALUES ('25478', '1', '0', 'Aargh! Do your worst, $C ! I\'ll tell you NOTHING!', '12', '0', '100', '0', '0', '0', '0', '0', 'imprisoned beryl sorcerer SAY_IMPRISIONED_BERYL_2');
INSERT INTO `creature_text` VALUES ('25478', '2', '0', 'Aahhhh! Release me! I am of no use to you. I swear it!', '12', '0', '100', '0', '0', '0', '0', '0', 'imprisoned beryl sorcerer SAY_IMPRISIONED_BERYL_3');
INSERT INTO `creature_text` VALUES ('25478', '3', '0', 'Stop! I beg you, please stop. Please...', '12', '0', '100', '0', '0', '0', '0', '0', 'imprisoned beryl sorcerer SAY_IMPRISIONED_BERYL_4');
INSERT INTO `creature_text` VALUES ('25478', '4', '0', 'Alright! I am beaten. Your previous archmage is held in a prison, elevated and sealed. Even if you manage to reach her, Salrand herself holds the key. Your mission is folly!', '12', '0', '100', '0', '0', '0', '0', '0', 'imprisoned beryl sorcerer SAY_IMPRISIONED_BERYL_5');
INSERT INTO `creature_text` VALUES ('25478', '5', '0', 'I\'ve given you the information, $C ! You\'re wasting your time....', '12', '0', '100', '0', '0', '0', '0', '0', 'imprisoned beryl sorcerer SAY_IMPRISIONED_BERYL_6');
INSERT INTO `creature_text` VALUES ('25478', '6', '0', 'Noooo! This torture is inhumane! You have what you want... why don\'t you just kill me?', '12', '0', '100', '0', '0', '0', '0', '0', 'imprisoned beryl sorcerer SAY_IMPRISIONED_BERYL_7');
INSERT INTO `creature_text` VALUES ('25589', '0', '0', 'I AM NOT AN APPETIZER!', '12', '0', '100', '0', '0', '0', '0', '0', 'bonker togglevolt');
INSERT INTO `creature_text` VALUES ('25589', '1', '0', 'I\'ll make you a deal: If you get me out of here alive,you\'ll get a reward larger than you can imagine!', '12', '0', '100', '0', '0', '0', '0', '0', 'bonker togglevolt');
INSERT INTO `creature_text` VALUES ('25827', '0', '0', 'Well...', '12', '0', '100', '0', '0', '0', '0', '0', 'hidden cultist SAY_HIDDEN_CULTIST_1');
INSERT INTO `creature_text` VALUES ('25827', '1', '0', 'Finally! This charade is over... Arthas give me strength!', '12', '0', '100', '0', '0', '0', '0', '0', 'hidden cultist SAY_HIDDEN_CULTIST_2');
INSERT INTO `creature_text` VALUES ('25827', '2', '0', 'You don\'t know who you\'re messing with! Death beckons!', '12', '0', '100', '0', '0', '0', '0', '0', 'hidden cultist SAY_HIDDEN_CULTIST_3');
