INSERT INTO `creature_text` VALUES ('4979', '0', '0', 'Hey, thanks.', '12', '0', '100', '0', '0', '0', '0', '0', 'theramore guard SAY_QUEST1');
INSERT INTO `creature_text` VALUES ('4979', '1', '0', '...receive 50 percent off deserter undergarments? What is this garbage?', '12', '0', '100', '0', '0', '0', '0', '0', 'theramore guard SAY_QUEST2');
INSERT INTO `creature_text` VALUES ('4979', '1', '1', '...to establish a fund for the purchase of hair gel? I like my hair the way it is, thanks!', '12', '0', '100', '0', '0', '0', '0', '0', 'theramore guard SAY_QUEST2');
INSERT INTO `creature_text` VALUES ('4979', '1', '2', '...the deserters seek a Theramore where citizens will be free to juggle at all hours of the day? What is this nonsense?', '12', '0', '100', '0', '0', '0', '0', '0', 'theramore guard SAY_QUEST2');
INSERT INTO `creature_text` VALUES ('4979', '1', '3', '...to establish the chicken as the official symbol of Theramore?! These guys are nuts!', '12', '0', '100', '0', '0', '0', '0', '0', 'theramore guard SAY_QUEST2');
INSERT INTO `creature_text` VALUES ('4979', '1', '4', '...as a deserter, you\'ll enjoy activities like tethered swimming and dog grooming? How ridiculous!', '12', '0', '100', '0', '0', '0', '0', '0', 'theramore guard SAY_QUEST2');
INSERT INTO `creature_text` VALUES ('4979', '2', '0', 'This... this is a joke, right?', '12', '0', '100', '0', '0', '0', '0', '0', 'theramore guard SAY_QUEST3');
INSERT INTO `creature_text` VALUES ('4979', '2', '1', 'I\'d never join anything like this. Better keep this, though. It\'ll come in handy in the privy...', '12', '0', '100', '0', '0', '0', '0', '0', 'theramore guard SAY_QUEST3');
INSERT INTO `creature_text` VALUES ('4979', '2', '2', 'What a bunch of lunatics! You actually believe this stuff?', '12', '0', '100', '0', '0', '0', '0', '0', 'theramore guard SAY_QUEST3');
INSERT INTO `creature_text` VALUES ('28860', '0', '0', 'It is my charge to watch over these eggs. I will see you burn before any harm comes to them!', '14', '0', '100', '0', '0', '14093', '0', '0', 'sartharion SAY_SARTHARION_AGGRO');
INSERT INTO `creature_text` VALUES ('28860', '1', '0', 'This pathetic siege ends NOW!', '14', '0', '100', '0', '0', '14103', '0', '0', 'sartharion SAY_SARTHARION_BERSERK');
