INSERT INTO `creature_text` VALUES ('38112', '0', '0', 'Men, women and children... None were spared the master\'s wrath. Your death will be no different.', '14', '0', '100', '0', '0', '16710', '0', '0', 'falric SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('38112', '1', '0', 'Sniveling maggot!', '14', '0', '100', '0', '0', '16711', '0', '0', 'falric SAY_SLAY_1');
INSERT INTO `creature_text` VALUES ('38112', '1', '1', 'The children of Stratholme fought with more ferocity!', '14', '0', '100', '0', '0', '16712', '0', '0', 'falric SAY_SLAY_2');
INSERT INTO `creature_text` VALUES ('38112', '2', '0', 'Marwyn, finish them...', '14', '0', '100', '0', '0', '16713', '0', '0', 'falric SAY_DEATH');
INSERT INTO `creature_text` VALUES ('38112', '3', '0', 'Despair... so delicious...', '14', '0', '100', '0', '0', '16715', '0', '0', 'falric SAY_IMPENDING_DESPAIR');
INSERT INTO `creature_text` VALUES ('38112', '4', '0', 'Fear... so exhilarating...', '14', '0', '100', '0', '0', '16716', '0', '0', 'falric SAY_DEFILING_HORROR');
INSERT INTO `creature_text` VALUES ('37221', '3', '0', 'Stand back! Touch that blade and your soul will be scarred for all eternity! I must attempt to commune with the spirits locked away within Frostmourne. Give me space, back up please!', '14', '0', '100', '0', '0', '16634', '0', '0', 'Jaina SAY_JAINA_INTRO_4');
INSERT INTO `creature_text` VALUES ('37221', '2', '0', 'Frostmourne! The blade that destroyed our kingdom...', '14', '0', '100', '0', '0', '16633', '0', '0', 'Jaina SAY_JAINA_INTRO_3');
INSERT INTO `creature_text` VALUES ('37221', '1', '0', 'What is that? Up ahead! Could it be... ? Heroes at my side!', '14', '0', '100', '0', '0', '16632', '0', '0', 'Jaina SAY_JAINA_INTRO_2');
INSERT INTO `creature_text` VALUES ('37221', '0', '0', 'The chill of this place... Brr... I can feel my blood freezing.', '14', '0', '100', '0', '0', '16631', '0', '0', 'Jaina SAY_JAINA_INTRO_1');
INSERT INTO `creature_text` VALUES ('37221', '4', '0', 'Uther! Dear Uther! ... I... I\'m so sorry.', '12', '0', '100', '0', '0', '16635', '0', '0', 'Jaina SAY_JAINA_INTRO_5');
INSERT INTO `creature_text` VALUES ('37221', '5', '0', 'Arthas is here? Maybe I...', '12', '0', '100', '0', '0', '16636', '0', '0', 'Jaina SAY_JAINA_INTRO_6');
INSERT INTO `creature_text` VALUES ('37221', '6', '0', 'But Uther, if there\'s any hope of reaching Arthas. I... I must try.', '12', '0', '100', '0', '0', '16637', '0', '0', 'Jaina SAY_JAINA_INTRO_7');
INSERT INTO `creature_text` VALUES ('37221', '7', '0', 'Tell me how, Uther? How do I destroy my prince? My...', '12', '0', '100', '0', '0', '16638', '0', '0', 'Jaina SAY_JAINA_INTRO_8');
INSERT INTO `creature_text` VALUES ('37221', '8', '0', 'You\'re right, Uther. Forgive me. I... I don\'t know what got a hold of me. We will deliver this information to the King and the knights that battle the Scourge within Icecrown Citadel.', '12', '0', '100', '0', '0', '16639', '0', '0', 'Jaina SAY_JAINA_INTRO_9');
INSERT INTO `creature_text` VALUES ('37221', '9', '0', 'Who could bear such a burden?', '12', '0', '100', '0', '0', '16640', '0', '0', 'Jaina SAY_JAINA_INTRO_10');
INSERT INTO `creature_text` VALUES ('37221', '10', '0', 'Then maybe there is still hope...', '12', '0', '100', '0', '0', '16641', '0', '0', 'Jaina SAY_JAINA_INTRO_11');
INSERT INTO `creature_text` VALUES ('37221', '11', '0', 'You won\'t deny me this Arthas! I must know! I must find out!', '14', '0', '100', '0', '0', '16642', '0', '0', 'Jaina SAY_JAINA_INTRO_END');
INSERT INTO `creature_text` VALUES ('37226', '0', '0', 'SILENCE, PALADIN!', '14', '0', '100', '0', '0', '17225', '0', '0', 'Lich King SAY_LK_INTRO_1');
INSERT INTO `creature_text` VALUES ('37226', '1', '0', 'So you wish to commune with the dead? You shall have your wish.', '14', '0', '100', '0', '0', '17226', '0', '0', 'Lich King SAY_LK_INTRO_2');
INSERT INTO `creature_text` VALUES ('37226', '2', '0', 'Falric. Marwyn. Bring their corpses to my chamber when you are through.', '14', '0', '100', '0', '0', '17227', '0', '0', 'Lich King SAY_LK_INTRO_3');
INSERT INTO `creature_text` VALUES ('38113', '0', '0', 'Death is all that you will find here!', '14', '0', '100', '0', '0', '16734', '0', '0', 'marwyn SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('38112', '5', '0', 'As you wish, my lord.', '14', '0', '100', '0', '0', '16717', '0', '0', 'Falric SAY_FALRIC_INTRO_1');
INSERT INTO `creature_text` VALUES ('38112', '6', '0', 'Soldiers of Lordaeron, rise to meet your master\'s call!', '14', '0', '100', '0', '0', '16714', '0', '0', 'Falric SAY_FALRIC_INTRO_2');
INSERT INTO `creature_text` VALUES ('37223', '0', '0', 'I... I don\'t believe it! Frostmourne stands before us, unguarded! Just as the Gnome claimed. Come, heroes!', '14', '0', '100', '0', '0', '17049', '0', '0', 'Sylvanas SAY_SYLVANAS_INTRO_1');
INSERT INTO `creature_text` VALUES ('37223', '1', '0', 'Standing this close to the blade that ended my life... The pain... It is renewed.', '14', '0', '100', '0', '0', '17050', '0', '0', 'Sylvanas SAY_SYLVANAS_INTRO_2');
INSERT INTO `creature_text` VALUES ('37223', '2', '0', 'I dare not touch it. Stand back! Stand back as I attempt to commune with the blade! Perhaps our salvation lies within...', '14', '0', '100', '0', '0', '17051', '0', '0', 'Sylvanas SAY_SYLVANAS_INTRO_3');
INSERT INTO `creature_text` VALUES ('37223', '3', '0', 'Uther...Uther the Lightbringer. How...', '12', '0', '100', '0', '0', '17052', '0', '0', 'Sylvanas SAY_SYLVANAS_INTRO_4');
INSERT INTO `creature_text` VALUES ('37223', '4', '0', 'The Lich King is here? Then my destiny shall be fulfilled today!', '12', '0', '100', '0', '0', '17053', '0', '0', 'Sylvanas SAY_SYLVANAS_INTRO_5');
INSERT INTO `creature_text` VALUES ('37223', '5', '0', 'There must be a way... ', '12', '0', '100', '0', '0', '17054', '0', '0', 'Sylvanas SAY_SYLVANAS_INTRO_6');
INSERT INTO `creature_text` VALUES ('37223', '6', '0', 'Who could bear such a burden?', '12', '0', '100', '0', '0', '17055', '0', '0', 'Sylvanas SAY_SYLVANAS_INTRO_7');
INSERT INTO `creature_text` VALUES ('37223', '7', '0', 'The Frozen Throne...', '12', '0', '100', '0', '0', '17056', '0', '0', 'Sylvanas SAY_SYLVANAS_INTRO_8');
INSERT INTO `creature_text` VALUES ('37223', '8', '0', 'You will not escape me that easily, Arthas! I will have my vengeance!', '14', '0', '100', '0', '0', '17057', '0', '0', 'Sylvanas SAY_SYLVANAS_INTRO_END');
INSERT INTO `creature_text` VALUES ('37225', '0', '0', 'Jaina! Could it truly be you?', '14', '0', '100', '0', '0', '16666', '0', '0', 'Uther SAY_UTHER_INTRO_A2_1');
INSERT INTO `creature_text` VALUES ('37225', '1', '0', 'Jaina you haven\'t much time. The Lich King sees what the sword sees. He will be here shortly!', '12', '0', '100', '0', '0', '16667', '0', '0', 'Uther SAY_UTHER_INTRO_A2_2');
INSERT INTO `creature_text` VALUES ('37225', '2', '0', 'No, girl. Arthas is not here. Arthas is merely a presence within the Lich King\'s mind. A dwindling presence...', '12', '0', '100', '0', '0', '16668', '0', '0', 'Uther SAY_UTHER_INTRO_A2_3');
INSERT INTO `creature_text` VALUES ('37225', '3', '0', 'Jaina, listen to me. You must destroy the Lich King. You cannot reason with him. He will kill you and your allies and raise you all as powerful soldiers of the Scourge.', '12', '0', '100', '0', '0', '16669', '0', '0', 'Uther SAY_UTHER_INTRO_A2_4');
INSERT INTO `creature_text` VALUES ('37225', '4', '0', 'Snap out of it, girl. You must destroy the Lich King at the place where he merged with Ner\'zhul - atop the spire, at the Frozen Throne. It is the only way.', '12', '0', '100', '0', '0', '16670', '0', '0', 'Uther SAY_UTHER_INTRO_A2_5');
INSERT INTO `creature_text` VALUES ('37225', '5', '0', 'There is... something else that you should know about the Lich King. Control over the Scourge must never be lost. Even if you were to strike down the Lich King, another would have to take his place. For without the control of its master, the Scourge would run rampant across the world - destroying all living things.', '12', '0', '100', '0', '0', '16671', '0', '0', 'Uther SAY_UTHER_INTRO_A2_6');
INSERT INTO `creature_text` VALUES ('37225', '6', '0', 'A grand sacrifice by a noble soul...', '12', '0', '100', '0', '0', '16672', '0', '0', 'Uther SAY_UTHER_INTRO_A2_7');
INSERT INTO `creature_text` VALUES ('37225', '7', '0', 'I do not know, Jaina. I suspect that the piece of Arthas that might be left inside the Lich King is all that holds the Scourge from annihilating Azeroth.', '12', '0', '100', '0', '0', '16673', '0', '0', 'Uther SAY_UTHER_INTRO_A2_8');
INSERT INTO `creature_text` VALUES ('37225', '8', '0', 'No, Jaina! Aargh! He... He is coming! You... You must...', '12', '0', '100', '0', '0', '16674', '0', '0', 'Uther SAY_UTHER_INTRO_A2_9');
INSERT INTO `creature_text` VALUES ('37225', '9', '0', 'Careful, girl. I\'ve heard talk of that cursed blade saving us before. Look around you and see what has been born of Frostmourne.', '12', '0', '100', '0', '0', '16659', '0', '0', 'Uther SAY_UTHER_INTRO_H2_1');
INSERT INTO `creature_text` VALUES ('37225', '10', '0', 'You haven\'t much time. The Lich King sees what the sword sees. He will be here shortly.', '12', '0', '100', '0', '0', '16660', '0', '0', 'Uther SAY_UTHER_INTRO_H2_2');
INSERT INTO `creature_text` VALUES ('37225', '11', '0', 'You cannot defeat the Lich King. Not here. You would be a fool to try. He will kill those who follow you and raise them as powerful servants of the Scourge. But for you, Sylvanas, his reward for you would be worse than the last.', '12', '0', '100', '0', '0', '16661', '0', '0', 'Uther SAY_UTHER_INTRO_H2_3');
INSERT INTO `creature_text` VALUES ('37225', '12', '0', 'Perhaps, but know this: there must always be a Lich King. Even if you were to strike down Arthas, another would have to take his place, for without the control of the Lich King, the Scourge would wash over this world like locusts, destroying all that they touched.', '12', '0', '100', '0', '0', '16662', '0', '0', 'Uther SAY_UTHER_INTRO_H2_4');
INSERT INTO `creature_text` VALUES ('37225', '13', '0', 'I do not know, Banshee Queen. I suspect that the piece of Arthas that might be left inside the Lich King is all that holds the Scourge from annihilating Azeroth.', '12', '0', '100', '0', '0', '16663', '0', '0', 'Uther SAY_UTHER_INTRO_H2_5');
INSERT INTO `creature_text` VALUES ('37225', '14', '0', 'Alas, the only way to defeat the Lich King is to destroy him at the place he was created.', '12', '0', '100', '0', '0', '16664', '0', '0', 'Uther SAY_UTHER_INTRO_H2_6');
INSERT INTO `creature_text` VALUES ('37225', '15', '0', 'I... Aargh... He... He is coming... You... You must...', '12', '0', '100', '0', '0', '16665', '0', '0', 'Uther SAY_UTHER_INTRO_H2_7');
INSERT INTO `creature_text` VALUES ('36477', '0', '0', 'Our work must not be interrupted! Ick! Take care of them!', '14', '0', '100', '0', '0', '16926', '0', '0', 'Krick SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('36477', '1', '0', 'Ooh...We could probably use these parts!', '14', '0', '100', '0', '0', '16927', '0', '0', 'Krick SAY_SLAY_1');
INSERT INTO `creature_text` VALUES ('36477', '1', '1', 'Arms and legs are in short supply...Thanks for your contribution!', '14', '0', '100', '0', '0', '16928', '0', '0', 'Krick SAY_SLAY_2');
INSERT INTO `creature_text` VALUES ('36477', '2', '0', 'Enough moving around! Hold still while I blow them all up!', '14', '0', '100', '0', '0', '16929', '0', '0', 'Krick SAY_BARRAGE_1');
INSERT INTO `creature_text` VALUES ('36477', '3', '0', 'Krick begins rapidly conjuring explosive mines!', '41', '0', '100', '0', '0', '0', '0', '0', 'Krick SAY_BARRAGE_2');
INSERT INTO `creature_text` VALUES ('36477', '4', '0', 'Quickly! Poison them all while they\'re still close!', '14', '0', '100', '0', '0', '16930', '0', '0', 'Krick SAY_POISON_NOVA');
INSERT INTO `creature_text` VALUES ('36477', '5', '0', 'No! That one! That one! Get that one!', '14', '0', '100', '0', '0', '16931', '0', '0', 'Krick SAY_CHASE_1');
INSERT INTO `creature_text` VALUES ('36477', '5', '1', 'I\'ve changed my mind...go get that one instead!', '14', '0', '100', '0', '0', '16932', '0', '0', 'Krick SAY_CHASE_2');
INSERT INTO `creature_text` VALUES ('36477', '5', '2', 'What are you attacking him for? The dangerous one is over there,fool!', '14', '0', '100', '0', '0', '16933', '0', '0', 'Krick SAY_CHASE_3');
INSERT INTO `creature_text` VALUES ('36477', '6', '0', 'Wait! Stop! Don\'t kill me, please! I\'ll tell you everything!', '14', '0', '100', '0', '0', '16934', '0', '0', 'Krick SAY_KRICK_OUTRO_1');
INSERT INTO `creature_text` VALUES ('36477', '7', '0', 'What you seek is in the master\'s lair, but you must destroy Tyrannus to gain entry. Within the Halls of Reflection you will find Frostmourne. It... it holds the truth.', '14', '0', '100', '0', '0', '16935', '0', '0', 'Krick SAY_KRICK_OUTRO_3');
INSERT INTO `creature_text` VALUES ('36477', '8', '0', 'I swear it is true! Please, don\'t kill me!!', '14', '0', '100', '0', '0', '16936', '0', '0', 'Krick SAY_KRICK_OUTRO_5');
INSERT INTO `creature_text` VALUES ('36477', '9', '0', 'Urg... no!!', '14', '0', '100', '0', '0', '16937', '0', '0', 'Krick SAY_KRICK_OUTRO_8');
INSERT INTO `creature_text` VALUES ('36476', '0', '0', 'Ick begins to unleash a toxic poison cloud!', '41', '0', '100', '0', '0', '0', '0', '0', 'Ick SAY_ICK_POISON_NOVA');
INSERT INTO `creature_text` VALUES ('36476', '1', '0', 'Ick is chasing you!', '41', '0', '100', '0', '0', '0', '0', '0', 'Ick SAY_ICK_CHASE_1');
INSERT INTO `creature_text` VALUES ('36658', '0', '0', 'Another shall take his place. You waste your time.', '14', '0', '100', '0', '0', '16752', '0', '0', 'Tyrannus SAY_TYRANNUS_DEATH');
INSERT INTO `creature_text` VALUES ('36794', '1', '0', 'Worthless gnat! Death is all that awaits you!', '14', '0', '100', '0', '0', '16753', '0', '0', 'Tyrannus SAY_TYRANNUS_OUTRO_7');
INSERT INTO `creature_text` VALUES ('36794', '2', '0', 'Do not think that I shall permit you entry into my master\'s sanctum so easily. Pursue me if you dare.', '14', '0', '100', '0', '0', '16754', '0', '0', 'Tyrannus SAY_TYRANNUS_OUTRO_9');
INSERT INTO `creature_text` VALUES ('36658', '3', '0', 'Your pursuit shall be in vain, adventurers, for the Lich King has placed an army of undead at my command! Behold!', '14', '0', '100', '0', '0', '16755', '0', '0', 'Tyrannus SAY_AMBUSH_1');
INSERT INTO `creature_text` VALUES ('36658', '4', '0', 'Persistent whelps! You will not reach the entrance of my lord\'s lair! Soldiers, destroy them!', '14', '0', '100', '0', '0', '16756', '0', '0', 'Tyrannus SAY_AMBUSH_2');
INSERT INTO `creature_text` VALUES ('36658', '5', '0', 'Rimefang! Trap them within the tunnel! Bury them alive!', '14', '0', '100', '0', '0', '16757', '0', '0', 'Tyrannus SAY_GAUNTLET_START');
INSERT INTO `creature_text` VALUES ('36658', '6', '0', 'Alas, brave, brave adventurers, your meddling has reached its end. Do you hear the clatter of bone and steel coming up the tunnel behind you? That is the sound of your impending demise.', '14', '0', '100', '0', '0', '16758', '0', '0', 'Tyrannus SAY_TYRANNUS_INTRO_1');
INSERT INTO `creature_text` VALUES ('36658', '7', '0', 'Ha, such an amusing gesture from the rabble. When I have finished with you, my master\'s blade will feast upon your souls. Die!', '14', '0', '100', '0', '0', '16759', '0', '0', 'Tyrannus SAY_TYRANNUS_INTRO_3');
INSERT INTO `creature_text` VALUES ('36658', '8', '0', 'I shall not fail The Lich King! Come and meet your end!', '14', '0', '100', '0', '0', '16760', '0', '0', 'Tyrannus SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('36658', '9', '0', 'Such a shameful display... You are better off dead!', '14', '0', '100', '0', '0', '16761', '0', '0', 'Tyrannus SAY_SLAY_1');
INSERT INTO `creature_text` VALUES ('36658', '9', '1', 'Perhaps you should have stayed in the mountains!', '14', '0', '100', '0', '0', '16762', '0', '0', 'Tyrannus SAY_SLAY_2');
INSERT INTO `creature_text` VALUES ('36658', '10', '0', 'Impossible! Rimefang... Warn...', '14', '0', '100', '0', '0', '16763', '0', '0', 'Tyrannus SAY_DEATH');
INSERT INTO `creature_text` VALUES ('36658', '11', '0', 'Rimefang, destroy this fool!', '14', '0', '100', '0', '0', '16764', '0', '0', 'Tyrannus SAY_MARK_RIMEFANG_1');
INSERT INTO `creature_text` VALUES ('36658', '12', '0', 'The frostwyrm Rimefang gazes at $N and readies an icy attack!', '41', '0', '100', '0', '0', '0', '0', '0', 'Tyrannus SAY_MARK_RIMEFANG_2');
INSERT INTO `creature_text` VALUES ('36658', '13', '0', 'Power... overwhelming!', '14', '0', '100', '0', '0', '16765', '0', '0', 'Tyrannus SAY_DARK_MIGHT_1');
INSERT INTO `creature_text` VALUES ('36658', '14', '0', 'Scourgelord Tyrannus roars and swells with dark might!', '41', '0', '100', '0', '0', '0', '0', '0', 'Tyrannus SAY_DARK_MIGHT_2');
INSERT INTO `creature_text` VALUES ('37592', '0', '0', 'Heroes! We will hold off the undead as long as we can, even to our dying breath. Deal with the Scourgelord!', '14', '0', '100', '0', '0', '17150', '0', '0', 'Gorkun SAY_GORKUN_INTRO_2');
INSERT INTO `creature_text` VALUES ('37592', '1', '0', 'Brave champions, we owe you our lives, our freedom... Though it be a tiny gesture in the face of this enormous debt, I pledge that from this day forth, all will know of your deeds, and the blazing path of light you cut through the shadow of this dark citadel.', '14', '0', '100', '0', '0', '0', '0', '0', 'Gorkun SAY_GORKUN_OUTRO_1');
INSERT INTO `creature_text` VALUES ('37592', '2', '0', 'This day will stand as a testament not only to your valor, but to the fact that no foe, not even the Lich King himself, can stand when Alliance and Horde set aside their differences and ---', '14', '0', '100', '0', '0', '0', '0', '0', 'Gorkun SAY_GORKUN_OUTRO_2');
INSERT INTO `creature_text` VALUES ('36993', '0', '0', 'I\'m not so naive as to believe your appeal for clemency, but I will listen.', '14', '0', '100', '0', '0', '16611', '0', '0', 'Jaina SAY_JAINA_OUTRO_2');
INSERT INTO `creature_text` VALUES ('36993', '1', '0', 'Frostmourne lies unguarded? Impossible!', '14', '0', '100', '0', '0', '16612', '0', '0', 'Jaina SAY_JAINA_OUTRO_4');
INSERT INTO `creature_text` VALUES ('36993', '2', '0', 'What a cruel end. Come, heroes. We must see if the gnome\'s story is true. If we can separate Arthas from Frostmourne, we might have a chance at stopping him.', '14', '0', '100', '0', '0', '16613', '0', '0', 'Jaina SAY_JAINA_OUTRO_10');
INSERT INTO `creature_text` VALUES ('36993', '3', '0', 'Heroes, to me!', '14', '0', '100', '0', '0', '16614', '0', '0', 'Jaina SAY_JAYNA_OUTRO_3');
INSERT INTO `creature_text` VALUES ('36993', '4', '0', 'The Frost Queen is gone. We must keep moving - our objective is near.', '12', '0', '100', '0', '0', '16615', '0', '0', 'Jaina SAY_JAYNA_OUTRO_4');
INSERT INTO `creature_text` VALUES ('36993', '5', '0', 'I... I could not save them... Damn you, Arthas! DAMN YOU!', '12', '0', '100', '0', '0', '16616', '0', '0', 'Jaina SAY_JAYNA_OUTRO_5');
INSERT INTO `creature_text` VALUES ('36990', '0', '0', 'Why should the Banshee Queen spare your miserable life?', '14', '0', '100', '0', '0', '17033', '0', '0', 'Sylvanas SAY_SYLVANAS_OUTRO_2');
INSERT INTO `creature_text` VALUES ('36990', '1', '0', 'Frostmourne? The Lich King is never without his blade! If you are lying to me...', '14', '0', '100', '0', '0', '17034', '0', '0', 'Sylvanas SAY_SYLVANAS_OUTRO_4');
INSERT INTO `creature_text` VALUES ('36990', '2', '0', 'A fitting end for a traitor. Come, we must free the slaves and see what is within the Lich King\'s chamber for ourselves.', '14', '0', '100', '0', '0', '17035', '0', '0', 'Sylvanas SAY_SYLVANAS_OUTRO_10');
INSERT INTO `creature_text` VALUES ('36990', '3', '0', 'Take cover behind me! Quickly!', '14', '0', '100', '0', '0', '17037', '0', '0', 'Sylvanas SAY_SYLVANAS_OUTRO_3');
INSERT INTO `creature_text` VALUES ('36990', '4', '0', 'I thought he\'d never shut up. At last, Sindragosa silenced that long-winded fool. To the Halls of Reflection, champions! Our objective is near... I can sense it.', '12', '0', '100', '0', '0', '17036', '0', '0', 'Sylvanas SAY_SYLVANAS_OUTRO_4');
INSERT INTO `creature_text` VALUES ('26588', '0', '0', 'Um... I think one of those wolves is back...', '12', '0', '100', '0', '0', '0', '0', '0', 'emily SAY_WORGHAGGRO1');
INSERT INTO `creature_text` VALUES ('26588', '1', '0', 'He\'s going for Mr. Floppy! ', '12', '0', '100', '0', '0', '0', '0', '0', 'emily SAY_WORGHAGGRO2');
INSERT INTO `creature_text` VALUES ('26588', '2', '0', 'Oh, no! Look, it\'s another wolf, and it\'s a biiiiiig one!', '12', '0', '100', '0', '0', '0', '0', '0', 'emily SAY_WORGRAGGRO3');
