INSERT INTO `creature_text` VALUES ('30899', '3', '0', 'System online. Life form pattern recognized. Welcome Branbronzan. Query?', '14', '0', '100', '0', '0', '13769', '0', '0', 'brann SAY_EVENT_D_4_ABED');
INSERT INTO `creature_text` VALUES ('30899', '4', '0', 'Accessing prehistoric data. Retrieved. In the beginning Earthen were created to-', '14', '0', '100', '0', '0', '13770', '0', '0', 'brann SAY_EVENT_END_03_ABED');
INSERT INTO `creature_text` VALUES ('30899', '5', '0', 'Accessing. In the early stages of its development cycle Azeroth suffered infection by parasitic, necrophotic symbiotes.', '14', '0', '100', '0', '0', '13771', '0', '0', 'brann SAY_EVENT_END_05_ABED');
INSERT INTO `creature_text` VALUES ('30899', '6', '0', 'Designation: Old Gods. Old Gods rendered all systems, including Earthen defenseless in order to facilitate assimilation. This matrix destabilization has been termed the Curse of Flesh. Effects of destabilization increased over time.', '14', '0', '100', '0', '0', '13772', '0', '0', 'brann SAY_EVENT_END_07_ABED');
INSERT INTO `creature_text` VALUES ('30899', '7', '0', 'Acknowledged Branbronzan. Session terminated.', '14', '0', '100', '0', '0', '13773', '0', '0', 'brann SAY_EVENT_END_21_ABED');
