INSERT INTO `creature_text` VALUES ('19830', '1', '0', 'Yes, my lord.', '12', '0', '100', '0', '0', '0', '19189', '0', 'dawnforge SAY_ARCANIST_ARDONIS_2');
INSERT INTO `creature_text` VALUES ('21504', '0', '0', 'Indeed, it is not a request.', '12', '0', '100', '0', '0', '0', '0', '0', 'dawnforge SAY_PATHALEON_CULATOR_IMAGE_1');
INSERT INTO `creature_text` VALUES ('21504', '1', '0', 'Duro will be reinforced! Ultris was a complete disaster. I will NOT have that mistake repeated!', '12', '0', '100', '0', '0', '0', '0', '0', 'dawnforge SAY_PATHALEON_CULATOR_IMAGE_2');
INSERT INTO `creature_text` VALUES ('21504', '2', '0', 'We\'ve had too many setbacks along the way: Hellfire Citadel, Fallen Sky Ridge, Firewing Point... Prince Kael\'thas will tolerate no further delays. I will tolerate nothing other than complete success!', '12', '0', '100', '0', '0', '0', '0', '0', 'dawnforge SAY_PATHALEON_CULATOR_IMAGE_2_1');
INSERT INTO `creature_text` VALUES ('21504', '3', '0', 'I am returning to Tempest Keep. See to it that I do not have reason to return!', '12', '0', '100', '0', '0', '0', '0', '0', 'dawnforge SAY_PATHALEON_CULATOR_IMAGE_2_2');
INSERT INTO `creature_text` VALUES ('19831', '0', '0', 'We need you to send reinforcements to Manaforge Duro, Ardonis. This is not a request, it\'s an order.', '12', '0', '100', '0', '0', '0', '0', '0', 'dawnforge SAY_COMMANDER_DAWNFORGE_1');
INSERT INTO `creature_text` VALUES ('19831', '1', '0', 'You will do as ordered. Manaforge Duro has come under heavy attack by mana creatures and the situation is out of control. Failure to comply will not be tolerated!', '12', '0', '100', '0', '0', '0', '0', '0', 'dawnforge SAY_COMMANDER_DAWNFORGE_2');
INSERT INTO `creature_text` VALUES ('19831', '2', '0', 'My lord!', '12', '0', '100', '0', '0', '0', '0', '0', 'dawnforge SAY_COMMANDER_DAWNFORGE_3');
INSERT INTO `creature_text` VALUES ('19831', '3', '0', 'Yes, my lord.', '12', '0', '100', '0', '0', '0', '19189', '0', 'dawnforge SAY_COMMANDER_DAWNFORGE_4');
INSERT INTO `creature_text` VALUES ('19831', '4', '0', 'See to it, Ardonis!', '12', '0', '100', '0', '0', '0', '0', '0', 'dawnforge SAY_COMMANDER_DAWNFORGE_5');
INSERT INTO `creature_text` VALUES ('20415', '0', '0', 'Bessy, is that you?', '12', '0', '100', '0', '0', '0', '0', '0', 'bessy SAY_THADELL_1');
INSERT INTO `creature_text` VALUES ('20415', '1', '0', 'Thank you for bringing back my Bessy, $N. I couldn\'t live without her!', '12', '0', '100', '0', '0', '0', '0', '0', 'bessy SAY_THADELL_2');
INSERT INTO `creature_text` VALUES ('21648', '0', '0', 'Thank you, mortal.', '12', '0', '100', '0', '0', '0', '0', '0', 'mature netherwing drake SAY_JUST_EATEN');
INSERT INTO `creature_text` VALUES ('23139', '0', '0', 'Come, $N. Lord Stormrage awaits.', '12', '0', '100', '0', '0', '0', '0', '0', 'overlord morghor OVERLORD_SAY_1');
INSERT INTO `creature_text` VALUES ('23139', '1', '0', 'Lord Illidan will be here shortly.', '12', '0', '100', '0', '0', '0', '0', '0', 'overlord morghor OVERLORD_SAY_2');
INSERT INTO `creature_text` VALUES ('23139', '3', '0', 'But... My lord, I do not understand. $N... He is the orc that has...', '12', '0', '100', '0', '0', '0', '0', '0', 'overlord morghor OVERLORD_SAY_4');
INSERT INTO `creature_text` VALUES ('23139', '4', '0', 'It will be done, my lord.', '12', '0', '100', '0', '0', '0', '0', '0', 'overlord morghor OVERLORD_SAY_5');
INSERT INTO `creature_text` VALUES ('23139', '5', '0', 'So you thought to make a fool of Mor\'ghor, eh? Before you are delivered to Lord Illidan, you will feel pain that you could not know to exist. I will take pleasure in exacting my own vengeance.', '12', '0', '100', '0', '0', '0', '0', '0', 'overlord morghor OVERLORD_SAY_6');
INSERT INTO `creature_text` VALUES ('23139', '6', '0', 'Warriors of Dragonmaw, gather \'round! One among you has attained the rank of highlord! Bow your heads in reverence! Show your respect and allegiance to Highlord $N!', '14', '0', '100', '0', '0', '0', '0', '0', 'overlord morghor OVERLORD_YELL_1');
INSERT INTO `creature_text` VALUES ('23139', '7', '0', 'All hail Lord Illidan!', '14', '0', '100', '0', '0', '0', '0', '0', 'overlord morghor OVERLORD_YELL_2');
INSERT INTO `creature_text` VALUES ('22083', '0', '0', 'What is the meaning of this, Mor\'ghor?', '12', '0', '100', '0', '0', '0', '0', '0', 'overlord morghor LORD_ILLIDAN_SAY_1');
INSERT INTO `creature_text` VALUES ('22083', '1', '0', 'SILENCE!', '12', '0', '100', '0', '0', '0', '32240', '0', 'overlord morghor LORD_ILLIDAN_SAY_2');
INSERT INTO `creature_text` VALUES ('22083', '2', '0', 'Blathering idiot. You incomprehensibly incompetent buffoon...', '12', '0', '100', '0', '0', '0', '0', '0', 'overlord morghor LORD_ILLIDAN_SAY_3');
INSERT INTO `creature_text` VALUES ('22083', '3', '0', 'THIS is your hero?', '12', '0', '100', '0', '0', '0', '0', '0', 'overlord morghor LORD_ILLIDAN_SAY_4');
INSERT INTO `creature_text` VALUES ('22083', '4', '0', 'You have been deceived, imbecile.', '12', '0', '100', '0', '0', '0', '0', '0', 'overlord morghor LORD_ILLIDAN_SAY_5');
INSERT INTO `creature_text` VALUES ('22083', '5', '0', 'This... whole... operation... HAS BEEN COMPROMISED!', '12', '0', '100', '0', '0', '0', '0', '0', 'overlord morghor LORD_ILLIDAN_SAY_6');
INSERT INTO `creature_text` VALUES ('22083', '6', '0', 'I expect to see this insect\'s carcass in pieces in my lair within the hour. Fail and you will suffer a fate so much worse than death.', '12', '0', '100', '0', '0', '0', '0', '0', 'overlord morghor LORD_ILLIDAN_SAY_7');
INSERT INTO `creature_text` VALUES ('23141', '0', '0', 'You will not harm the boy, Mor\'ghor! Quickly, $N, climb on my back!', '12', '0', '100', '0', '0', '0', '0', '0', 'overlord morghor YARZILL_THE_MERC_SAY');
INSERT INTO `creature_text` VALUES ('21027', '0', '0', 'I sense the tortured spirits, $n. They are this way, come quickly!', '12', '0', '100', '0', '0', '0', '0', '0', 'wilda SAY_WIL_START');
INSERT INTO `creature_text` VALUES ('21027', '1', '0', 'Watch out!', '12', '0', '100', '0', '0', '0', '32401', '0', 'wilda SAY_WIL_AGGRO1');
INSERT INTO `creature_text` VALUES ('21027', '1', '1', 'Naga attackers! Defend yourself!', '12', '0', '100', '0', '0', '0', '0', '0', 'wilda SAY_WIL_AGGRO2');
INSERT INTO `creature_text` VALUES ('21027', '2', '0', 'Grant me protection $n, I must break trough their foul magic!', '12', '0', '100', '0', '0', '0', '0', '0', 'wilda SAY_WIL_PROGRESS1');
INSERT INTO `creature_text` VALUES ('21027', '3', '0', 'The naga of Coilskar are exceptionally cruel to their prisoners. It is a miracle that I survived inside that watery prison for as long as I did. Earthmother be praised.', '12', '0', '100', '0', '0', '0', '0', '0', 'wilda SAY_WIL_PROGRESS2');
INSERT INTO `creature_text` VALUES ('21027', '3', '1', 'Lady Vashj must answer for these atrocities. She must be brought to justice!', '12', '0', '100', '0', '0', '0', '0', '0', 'wilda SAY_WIL_PROGRESS4');
INSERT INTO `creature_text` VALUES ('21027', '3', '2', 'The tumultuous nature of the great waterways of Azeroth and Draenor are a direct result of tormented water spirits.', '12', '0', '100', '0', '0', '0', '0', '0', 'wilda SAY_WIL_PROGRESS5');
INSERT INTO `creature_text` VALUES ('21027', '4', '0', 'Now we must find the exit.', '12', '0', '100', '0', '0', '0', '0', '0', 'wilda SAY_WIL_FIND_EXIT');
INSERT INTO `creature_text` VALUES ('21027', '5', '0', 'It shouldn\'t be much further, $n. The exit is just up ahead.', '12', '0', '100', '0', '0', '0', '0', '0', 'wilda SAY_WIL_JUST_AHEAD');
INSERT INTO `creature_text` VALUES ('21027', '6', '0', 'Thank you, $n. Please return to my brethren at the Altar of Damnation, near the Hand of Gul\'dan, and tell them that Wilda is safe. May the Earthmother watch over you...', '12', '0', '100', '0', '0', '0', '0', '0', 'wilda SAY_WIL_END');
INSERT INTO `creature_text` VALUES ('19685', '0', '0', 'Follow me, stranger. This won\'t take long.', '12', '0', '100', '0', '0', '0', '0', '0', 'kservant SAY_KHAD_SERV_0');
INSERT INTO `creature_text` VALUES ('19685', '1', '0', 'Shattrath was once the draenei capital of this world. Its name means \"dwelling of light.\"', '15', '0', '100', '0', '0', '0', '0', '0', 'kservant SAY_KHAD_SERV_1');
INSERT INTO `creature_text` VALUES ('19685', '2', '0', 'When the Burning Legion turned the orcs against the draenei, the fiercest battle was fought here. The draenei fought tooth and nail, but in the end the city fell.', '15', '0', '100', '0', '0', '0', '0', '0', 'kservant SAY_KHAD_SERV_2');
INSERT INTO `creature_text` VALUES ('19685', '3', '0', 'The city was left in ruins and darkness... until the Sha\'tar arrived.', '15', '0', '100', '0', '0', '0', '0', '0', 'kservant SAY_KHAD_SERV_3');
INSERT INTO `creature_text` VALUES ('19685', '4', '0', 'Let us go into the Lower City. I will warn you that as one of the only safe havens in Outland, Shattrath has attracted droves of refugees from all wars, current and past.', '15', '0', '100', '0', '0', '0', '0', '0', 'kservant SAY_KHAD_SERV_4');
INSERT INTO `creature_text` VALUES ('19685', '5', '0', 'The Sha\'tar, or \"born from light\" are the naaru that came to Outland to fight the demons of the Burning Legion.', '15', '0', '100', '0', '0', '0', '0', '0', 'kservant SAY_KHAD_SERV_5');
INSERT INTO `creature_text` VALUES ('19685', '6', '0', 'They were drawn to the ruins of Shattrath City where a small remnant of the draenei priesthood conducted its rites inside a ruined temple on this very spot.', '15', '0', '100', '0', '0', '0', '0', '0', 'kservant SAY_KHAD_SERV_6');
INSERT INTO `creature_text` VALUES ('19685', '7', '0', 'The priesthood, known as the Aldor, quickly regained its strength as word spread that the naaru had returned and reconstruction soon began. The ruined temple is now used as an infirmary for injured refugees.', '15', '0', '100', '0', '0', '0', '0', '0', 'kservant SAY_KHAD_SERV_7');
INSERT INTO `creature_text` VALUES ('19685', '8', '0', 'It wouldn\'t be long, however, before the city came under attack once again. This time, the attack came from Illidan\'s armies. A large regiment of blood elves had been sent by Illidan\'s ally, Kael\'thas Sunstrider, to lay waste to the city.', '15', '0', '100', '0', '0', '0', '0', '0', 'kservant SAY_KHAD_SERV_8');
INSERT INTO `creature_text` VALUES ('19685', '9', '0', 'As the regiment of blood elves crossed this very bridge, the Aldor\'s exarchs and vindicators lined up to defend the Terrace of Light. But then the unexpected happened.', '15', '0', '100', '0', '0', '0', '0', '0', 'kservant SAY_KHAD_SERV_9');
INSERT INTO `creature_text` VALUES ('19685', '10', '0', 'The blood elves laid down their weapons in front of the city\'s defenders; their leader, a blood elf elder known as Voren\'thal, stormed into the Terrace of Light and demanded to speak to A\'dal.', '15', '0', '100', '0', '0', '0', '0', '0', 'kservant SAY_KHAD_SERV_10');
INSERT INTO `creature_text` VALUES ('19685', '11', '0', 'As the naaru approached him, Voren\'thal kneeled before him and uttered the following words: \"I\'ve seen you in a vision, naaru. My race\'s only hope for survival lies with you. My followers and I are here to serve you.\"', '15', '0', '100', '0', '0', '0', '0', '0', 'kservant SAY_KHAD_SERV_11');
INSERT INTO `creature_text` VALUES ('19685', '12', '0', 'The defection of Voren\'thal and his followers was the largest loss ever incurred by Kael\'s forces. And these weren\'t just any blood elves. Many of the best and brightest amongst Kael\'s scholars and magisters had been swayed by Voren\'thal\'s influence.', '15', '0', '100', '0', '0', '0', '0', '0', 'kservant SAY_KHAD_SERV_12');
INSERT INTO `creature_text` VALUES ('19685', '13', '0', 'The naaru accepted the defectors, who would become known as the Scryers; their dwelling lies in the platform above. Only those initiated with the Scryers are allowed there.', '15', '0', '100', '0', '0', '0', '0', '0', 'kservant SAY_KHAD_SERV_13');
INSERT INTO `creature_text` VALUES ('19685', '14', '0', 'The Aldor are followers of the Light and forgiveness and redemption are values they understand. However, they found hard to forget the deeds of the blood elves while under Kael\'s command.', '15', '0', '100', '0', '0', '0', '0', '0', 'kservant SAY_KHAD_SERV_14');
INSERT INTO `creature_text` VALUES ('19685', '15', '0', 'Many of the priesthood had been slain by the same magisters who now vowed to serve the naaru. They were not happy to share the city with their former enemies.', '15', '0', '100', '0', '0', '0', '0', '0', 'kservant SAY_KHAD_SERV_15');
INSERT INTO `creature_text` VALUES ('19685', '16', '0', 'The Aldor\'s most holy temple and its surrounding dwellings lie on the terrace above. As a holy site, only the initiated are welcome inside.', '15', '0', '100', '0', '0', '0', '0', '0', 'kservant SAY_KHAD_SERV_16');
INSERT INTO `creature_text` VALUES ('19685', '17', '0', 'The attacks against Shattrath continued, but the city did not fall, as you can see. On the contrary, the naaru known as Xi\'ri led a successful incursion into Shadowmoon Valley - Illidan\'s doorstep.', '15', '0', '100', '0', '0', '0', '0', '0', 'kservant SAY_KHAD_SERV_17');
INSERT INTO `creature_text` VALUES ('19685', '18', '0', 'There he continues to wage war on Illidan with the assistance of the Aldor and the Scryers. The two factions have not given up on their old feuds, though.', '15', '0', '100', '0', '0', '0', '0', '0', 'kservant SAY_KHAD_SERV_18');
INSERT INTO `creature_text` VALUES ('19685', '19', '0', 'Such is their animosity that they vie for the honor of being sent to assist the naaru there. Each day, that decision is made here by A\'dal. The armies gather here to receive A\'dal\'s blessing before heading to Shadowmoon.', '15', '0', '100', '0', '0', '0', '0', '0', 'kservant SAY_KHAD_SERV_19');
INSERT INTO `creature_text` VALUES ('19685', '20', '0', 'Khadgar should be ready to see you again. Just remember that to serve the Sha\'tar you will most likely have to ally with the Aldor or the Scryers. And seeking the favor of one group will cause the others\' dislike.', '15', '0', '100', '0', '0', '0', '0', '0', 'kservant SAY_KHAD_SERV_20');
INSERT INTO `creature_text` VALUES ('19685', '21', '0', 'Good luck stranger, and welcome to Shattrath City.', '15', '0', '100', '0', '0', '0', '0', '0', 'kservant SAY_KHAD_SERV_21');
INSERT INTO `creature_text` VALUES ('19720', '3', '0', '2...', '12', '0', '100', '0', '0', '0', '29260', '0', '\"Dirty\" Larry');
INSERT INTO `creature_text` VALUES ('19720', '4', '0', 'Time to meet your maker!', '12', '0', '100', '0', '0', '0', '0', '0', '\"Dirty\" Larry');
INSERT INTO `creature_text` VALUES ('19720', '2', '0', '1...', '12', '0', '100', '0', '0', '0', '29261', '0', '\"Dirty\" Larry');
INSERT INTO `creature_text` VALUES ('19720', '1', '0', 'I\'m now going to give you to the count of \"3\" to get out of here before I sick the dogs on you.', '12', '0', '100', '0', '0', '0', '0', '0', '\"Dirty\" Larry');
INSERT INTO `creature_text` VALUES ('19720', '0', '0', 'Time to teach you a lesson in manners, little boy! ', '12', '0', '100', '0', '0', '0', '0', '0', '\"Dirty\" Larry');
INSERT INTO `creature_text` VALUES ('20912', '0', '0', 'It is a small matter to control the mind of the weak... for I bear allegiance to powers untouched by time, unmoved by fate. No force on this world or beyond harbors the strength to bend our knee... not even the mighty Legion!', '14', '0', '100', '0', '0', '11122', '0', '0', 'skyriss SAY_INTRO');
INSERT INTO `creature_text` VALUES ('20912', '1', '0', 'Bear witness to the agent of your demise!', '14', '0', '100', '0', '0', '11123', '0', '0', 'skyriss SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('20912', '2', '0', 'Your fate is written!', '14', '0', '100', '0', '0', '11124', '0', '0', 'skyriss SAY_KILL_1');
INSERT INTO `creature_text` VALUES ('20912', '2', '1', 'The chaos I have sown here is but a taste...', '14', '0', '100', '0', '0', '11125', '0', '0', 'skyriss SAY_KILL_2');
INSERT INTO `creature_text` VALUES ('20912', '3', '0', 'You will do my bidding, weakling.', '14', '0', '100', '0', '0', '11127', '0', '0', 'skyriss SAY_MIND_1');
INSERT INTO `creature_text` VALUES ('20912', '3', '1', 'Your will is no longer your own.', '14', '0', '100', '0', '0', '11128', '0', '0', 'skyriss SAY_MIND_2');
INSERT INTO `creature_text` VALUES ('20912', '4', '0', 'Flee in terror!', '14', '0', '100', '0', '0', '11129', '0', '0', 'skyriss SAY_FEAR_1');
INSERT INTO `creature_text` VALUES ('20912', '4', '1', 'I will show you horrors undreamed of!', '14', '0', '100', '0', '0', '11130', '0', '0', 'skyriss SAY_FEAR_2');
INSERT INTO `creature_text` VALUES ('20912', '5', '0', 'We span the universe, as countless as the stars!', '14', '0', '100', '0', '0', '11131', '0', '0', 'skyriss SAY_IMAGE');
INSERT INTO `creature_text` VALUES ('20912', '6', '0', 'I am merely one of... infinite multitudes.', '14', '0', '100', '0', '0', '11126', '0', '0', 'skyriss SAY_DEATH');
INSERT INTO `creature_text` VALUES ('17975', '2', '0', 'Endorel aluminor!', '14', '0', '100', '0', '0', '11147', '0', '0', 'High Botanist Freywinn - Shapeshifting Say');
INSERT INTO `creature_text` VALUES ('17975', '1', '1', 'You will feed the worms.', '14', '0', '100', '0', '0', '11146', '0', '0', 'High Botanist Freywinn - Shapeshifting Say');
INSERT INTO `creature_text` VALUES ('17975', '1', '0', 'Your lifecycle is now concluded!', '14', '0', '100', '0', '0', '11145', '0', '0', 'High Botanist Freywinn - Kill Plant as Bloodelf Say');
INSERT INTO `creature_text` VALUES ('17975', '0', '0', 'What are you doing? These specimens are very delicate!', '14', '0', '100', '0', '0', '11144', '0', '0', 'High Botanist Freywinn - Aggro Say');
INSERT INTO `creature_text` VALUES ('17980', '0', '0', 'emits a strange noise.', '16', '0', '100', '0', '0', '0', '0', '0', 'laj EMOTE_SUMMON');
INSERT INTO `creature_text` VALUES ('17977', '0', '0', 'Who disturbs this sanctuary?', '14', '0', '100', '0', '0', '11230', '0', '0', 'warp SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('17977', '1', '0', 'You must die! But wait: this does not-- No, no... you must die!', '14', '0', '100', '0', '0', '11231', '0', '0', 'warp SAY_SLAY_1');
INSERT INTO `creature_text` VALUES ('17977', '1', '1', 'What am I doing? Why do I...', '14', '0', '100', '0', '0', '11232', '0', '0', 'warp SAY_SLAY_2');
INSERT INTO `creature_text` VALUES ('17977', '2', '0', 'Children, come to me!', '14', '0', '100', '0', '0', '11233', '0', '0', 'warp SAY_SUMMON_1');
INSERT INTO `creature_text` VALUES ('17977', '2', '1', 'Maybe this is not-- No, we fight! Come to my aid.', '14', '0', '100', '0', '0', '11234', '0', '0', 'warp SAY_SUMMON_2');
INSERT INTO `creature_text` VALUES ('17977', '3', '0', 'So... confused. Do not... belong here!', '14', '0', '100', '0', '0', '11235', '0', '0', 'warp SAY_DEATH');
INSERT INTO `creature_text` VALUES ('18805', '0', '0', 'Tal anu\'men no Sin\'dorei!', '14', '0', '100', '0', '0', '11134', '0', '0', 'solarian SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('18805', '1', '0', 'Ha ha ha! You are hopelessly outmatched!', '14', '0', '100', '0', '0', '11139', '0', '0', 'solarian SAY_SUMMON1');
INSERT INTO `creature_text` VALUES ('18805', '2', '0', 'I will crush your delusions of grandeur!', '14', '0', '100', '0', '0', '11140', '0', '0', 'solarian SAY_SUMMON2');
INSERT INTO `creature_text` VALUES ('18805', '3', '0', 'Your soul belongs to the Abyss!', '14', '0', '100', '0', '0', '11136', '0', '0', 'solarian SAY_KILL1');
INSERT INTO `creature_text` VALUES ('18805', '3', '1', 'By the blood of the Highborne!', '14', '0', '100', '0', '0', '11137', '0', '0', 'solarian SAY_KILL2');
INSERT INTO `creature_text` VALUES ('18805', '3', '2', 'For the Sunwell!', '14', '0', '100', '0', '0', '11138', '0', '0', 'solarian SAY_KILL3');
INSERT INTO `creature_text` VALUES ('18805', '4', '0', 'The warmth of the sun... awaits.', '14', '0', '100', '0', '0', '11135', '0', '0', 'solarian SAY_DEATH');
INSERT INTO `creature_text` VALUES ('18805', '5', '0', 'Enough of this! Now I call upon the fury of the cosmos itself.', '14', '0', '100', '0', '0', '0', '0', '0', 'solarian SAY_VOIDA');
INSERT INTO `creature_text` VALUES ('18805', '6', '0', 'I become ONE... with the VOID!', '14', '0', '100', '0', '0', '0', '0', '0', 'solarian SAY_VOIDB');
INSERT INTO `creature_text` VALUES ('19622', '0', '0', 'Energy. Power. My people are addicted to it... a dependence made manifest after the Sunwell was destroyed. Welcome... to the future. A pity you are too late to stop it. No one can stop me now! Selama ashal\'anore!', '14', '0', '100', '0', '0', '11256', '0', '0', 'kaelthas SAY_INTRO');
INSERT INTO `creature_text` VALUES ('19622', '1', '0', 'Capernian will see to it that your stay here is a short one.', '14', '0', '100', '0', '0', '11257', '0', '0', 'kaelthas SAY_INTRO_CAPERNIAN');
INSERT INTO `creature_text` VALUES ('19622', '2', '0', 'Well done, you have proven worthy to test your skills against my master engineer, Telonicus.', '14', '0', '100', '0', '0', '11258', '0', '0', 'kaelthas SAY_INTRO_TELONICUS');
INSERT INTO `creature_text` VALUES ('19622', '3', '0', 'Let us see how your nerves hold up against the Darkener, Thaladred.', '14', '0', '100', '0', '0', '11259', '0', '0', 'kaelthas SAY_INTRO_THALADRED');
