INSERT INTO `creature_text` VALUES ('16589', '0', '0', 'So there we were - me and the battalion, doing a routine recon down the bone road. We\'d sent the scout up ahead to look around in case anything dangerous come up.', '12', '0', '100', '0', '0', '0', '0', '0', 'Guard Captain Cragtar - Speech Text 0');
INSERT INTO `creature_text` VALUES ('16589', '1', '0', 'Sure enough, scount came runnin\' back, talkin\' bout some orcs down the path. They hadn\'t seen us yet so we figured we\'d go get aquainted.', '12', '0', '100', '0', '0', '0', '0', '0', 'Guard Captain Cragtar - Speech Text 1');
INSERT INTO `creature_text` VALUES ('16589', '2', '0', 'Big red bastards! Bigger than any orc I\'d ever seen. Muscles out to here, tusks out to here...', '12', '0', '100', '0', '0', '0', '0', '0', 'Guard Captain Cragtar - Speech Text 2');
