INSERT INTO `creature_text` VALUES ('18929', '0', '0', 'Ooh what a dreadful job! All day, surrounded by the stench of bad feet.', '12', '0', '100', '1', '0', '0', '0', '0', 'Kyrenna');
INSERT INTO `creature_text` VALUES ('18929', '0', '1', 'Why couldn\'t mother have chosen something else to dedicate herself to? Cakes, pastries, even bread - anything but cheese!', '12', '0', '100', '1', '0', '0', '0', '0', 'Kyrenna');
INSERT INTO `creature_text` VALUES ('18929', '0', '2', 'If only I had some wine to go with my cheese...', '12', '0', '100', '18', '0', '0', '0', '0', 'Kyrenna');
INSERT INTO `creature_text` VALUES ('20098', '0', '0', 'I\'m right here, daddy. You\'ve been asleep all day long. You sure must be tired!', '12', '0', '100', '1', '0', '0', '0', '0', 'Jane');
INSERT INTO `creature_text` VALUES ('20100', '0', '0', 'Was... Was it all a dream? Azuremyst, Bloodymyst, Zangarmarsh, Shadowmoon... even Zul\'Aman... Jane! Jane where are you!', '12', '0', '100', '1', '0', '0', '0', '0', 'Jessel');
