INSERT INTO `creature_text` VALUES ('2276', '0', '0', 'More agents of the Syndicate I\'ll wager! You\'ll never take back Alterac... Or Southshore!', '12', '7', '100', '0', '0', '0', '0', '0', 'Magistrate Henry Maleb');
INSERT INTO `creature_text` VALUES ('2396', '0', '0', 'I spent my whole life caring for these trees. Pruning and nurturing them... only fitting that they have joined me in death.', '12', '1', '100', '0', '0', '0', '0', '0', 'Hans Zandin');
INSERT INTO `creature_text` VALUES ('2396', '0', '1', 'This orchard is now as barren and lifeless as my rotting shell. Once these trees bloomed with fruit but now there is only rot.', '12', '1', '100', '0', '0', '0', '0', '0', 'Hans Zandin');
INSERT INTO `creature_text` VALUES ('2405', '0', '0', 'Intruders! Attack the intruders!', '12', '33', '100', '0', '0', '0', '0', '0', 'Tarren Mill Deathguard');
INSERT INTO `creature_text` VALUES ('2428', '0', '0', 'Never cross a Dark Iron, $C.', '12', '0', '100', '0', '0', '0', '0', '0', 'Jailor Marlgen');
INSERT INTO `creature_text` VALUES ('2428', '0', '1', 'Time to die, $C!', '12', '0', '100', '0', '0', '0', '0', '0', 'Jailor Marlgen');
INSERT INTO `creature_text` VALUES ('2428', '0', '2', 'Feel the power of the Dark Iron Dwarves!', '12', '0', '100', '0', '0', '0', '0', '0', 'Jailor Marlgen');
INSERT INTO `creature_text` VALUES ('2433', '0', '0', 'Revenge shall be mine at last!', '14', '0', '100', '1', '0', '0', '0', '0', 'Helcular\'s Remains');
INSERT INTO `creature_text` VALUES ('2433', '0', '1', 'I have risen!', '14', '0', '100', '1', '0', '0', '0', '0', 'Helcular\'s Remains');
INSERT INTO `creature_text` VALUES ('2433', '0', '2', 'Southshore shall pay in blood!', '14', '0', '100', '1', '0', '0', '0', '0', 'Helcular\'s Remains');
INSERT INTO `creature_text` VALUES ('2433', '1', '0', 'The undead shall feast on your soul, $N.', '14', '0', '100', '1', '0', '0', '0', '0', 'Helcular\'s Remains');
INSERT INTO `creature_text` VALUES ('2435', '0', '0', 'Help me! Help! Assassins in the hills! To arms! To arms!', '14', '0', '100', '22', '0', '0', '0', '0', 'Southshore Crier');
INSERT INTO `creature_text` VALUES ('2435', '1', '0', 'Guards to your posts! They must be after the Magistrate again. He must be protected! They could come from any direction!', '12', '0', '100', '1', '0', '0', '0', '0', 'Southshore Crier');
INSERT INTO `creature_text` VALUES ('2435', '2', '0', '%s grabs the back of his neck and his eyes go wide.', '16', '0', '100', '0', '0', '0', '0', '0', 'Southshore Crier');
INSERT INTO `creature_text` VALUES ('2435', '3', '0', 'Assassins... save... th... Magistraaa...', '12', '0', '100', '65', '0', '0', '0', '0', 'Southshore Crier');
INSERT INTO `creature_text` VALUES ('24960', '0', '0', 'Mine! You shall not take this place!', '12', '0', '100', '0', '0', '0', '0', '0', 'Wretched Devourer');
INSERT INTO `creature_text` VALUES ('24966', '0', '0', 'Get away, this place is ours!', '12', '0', '100', '0', '0', '0', '0', '0', 'Wretched Fiend');
INSERT INTO `creature_text` VALUES ('25132', '0', '0', 'Shattered Sun scum! Fire at will!', '14', '0', '100', '0', '0', '0', '0', '0', 'Sunblade Lookout');
INSERT INTO `creature_text` VALUES ('25144', '0', '0', 'Dragonhawk incoming from the west! Shoot that druid down!', '12', '0', '100', '0', '0', '0', '0', '0', 'Shattered Sun Bombardier');
INSERT INTO `creature_text` VALUES ('25144', '0', '1', 'Keep your eye on the demons. We\'re not concerned with killing Scourge today.', '14', '0', '100', '0', '0', '0', '0', '0', 'Shattered Sun Bombardier');
INSERT INTO `creature_text` VALUES ('25162', '0', '0', 'ATTENTION!', '12', '0', '100', '0', '0', '0', '26245', '0', 'Drill Sergeant Bahduum');
INSERT INTO `creature_text` VALUES ('25162', '1', '0', 'Alright, quiet down. We have a lot of work to do and very little time to do it in, people.', '12', '0', '100', '0', '0', '0', '0', '0', 'Drill Sergeant Bahduum');
INSERT INTO `creature_text` VALUES ('25162', '2', '0', 'I\'ve stood with some of you draenei before at Skettis, and others in Shadowmoon Valley. But now we\'re on Azeroth, and the situation looks grim.', '12', '0', '100', '0', '0', '0', '0', '0', 'Drill Sergeant Bahduum');
INSERT INTO `creature_text` VALUES ('25162', '3', '0', 'First though, you sin\'dorei listen up. I run a tight ship... this isn\'t going to be like what you\'re used to with the Scryers. Pull your weight, or get back on the boat!', '12', '0', '100', '0', '0', '0', '0', '0', 'Drill Sergeant Bahduum');
INSERT INTO `creature_text` VALUES ('25162', '4', '0', 'Now that that\'s out of the way, welcome to the Isle of Quel\'Danas. The Sunwell\'s on the plateau to the south, and Prince Kael\'thas is holed up in the Magisters\' Terrace to the east.', '12', '0', '100', '0', '0', '0', '0', '0', 'Drill Sergeant Bahduum');
INSERT INTO `creature_text` VALUES ('25162', '5', '0', 'We\'re here as support for the so-called \'adventurers\' that the brass called in. We\'ll help them hold the key points on the island, and keep the heat off so that they can do their jobs.', '12', '0', '100', '0', '0', '0', '0', '0', 'Drill Sergeant Bahduum');
INSERT INTO `creature_text` VALUES ('25162', '6', '0', 'If none of you ladies have any questions, you can get further instruction and assignments from your squad leaders. That\'s it. Good luck!', '12', '0', '100', '0', '0', '0', '0', '0', 'Drill Sergeant Bahduum');
INSERT INTO `creature_text` VALUES ('25162', '7', '0', 'ATTENTION!', '12', '0', '100', '0', '0', '0', '26245', '0', 'Drill Sergeant Bahduum');
INSERT INTO `creature_text` VALUES ('25162', '8', '0', 'Welcome to the Sunwell. Despite the mild temperatures and scenic vistas, you\'re not on vacation, ladies and gentlemen!', '12', '0', '100', '0', '0', '0', '0', '0', 'Drill Sergeant Bahduum');
INSERT INTO `creature_text` VALUES ('25162', '9', '0', 'The current situation looks like this: we\'re holding what we can while those with more experience and better equipment are moving against Prince Kael\'thas.', '12', '0', '100', '0', '0', '0', '0', '0', 'Drill Sergeant Bahduum');
INSERT INTO `creature_text` VALUES ('25162', '10', '0', 'I know, we thought he was dead too, but that\'s not the case. He\'s trying to use the Sunwell to bring the master of the Burning Legion, Kil\'jaeden, into this world.', '12', '0', '100', '0', '0', '0', '0', '0', 'Drill Sergeant Bahduum');
INSERT INTO `creature_text` VALUES ('25162', '11', '0', 'Let me assure you people that if he\'s successful, this world will suffer far worse than Outland ever did! So let\'s make sure that doesn\'t come to pass.', '12', '0', '100', '0', '0', '0', '0', '0', 'Drill Sergeant Bahduum');
INSERT INTO `creature_text` VALUES ('25162', '12', '0', 'Warriors will be deployed to hold key positions, while you marksmen will be assigned to keep the enemy fliers at bay.', '12', '0', '100', '0', '0', '0', '0', '0', 'Drill Sergeant Bahduum');
INSERT INTO `creature_text` VALUES ('25162', '13', '0', 'I\'m awaiting orders on your specific assignments. In the meantime, stay here and be ready to move at a moment\'s notice!', '12', '0', '100', '0', '0', '0', '0', '0', 'Drill Sergeant Bahduum');
INSERT INTO `creature_text` VALUES ('25162', '14', '0', 'ATTENTION!', '12', '0', '100', '0', '0', '0', '26245', '0', 'Drill Sergeant Bahduum');
INSERT INTO `creature_text` VALUES ('25162', '15', '0', 'Hello to you new recruits. I\'m Drill Sergeant Bahduum. For the next few days, you\'re going to receive orientation.', '12', '0', '100', '0', '0', '0', '0', '0', 'Drill Sergeant Bahduum');
INSERT INTO `creature_text` VALUES ('25162', '16', '0', 'The Isle of Quel\'Danas has been overrun by the forces of Prince Kael\'thas Sunstrider. Intel had him pegged as deceased. Intel was wrong.', '12', '0', '100', '0', '0', '0', '0', '0', 'Drill Sergeant Bahduum');
INSERT INTO `creature_text` VALUES ('25162', '17', '0', 'Up on the plateau, he and his demon friends are trying to reignite the Sunwell and open a rift to bring their head-honcho, Kil\'Jaeden back into the world.', '12', '0', '100', '0', '0', '0', '0', '0', 'Drill Sergeant Bahduum');
INSERT INTO `creature_text` VALUES ('25162', '18', '0', 'Our job is to clear out as much of the fodder as we can, while supporting the specialists that the powers-that-be are having brought in.', '12', '0', '100', '0', '0', '0', '0', '0', 'Drill Sergeant Bahduum');
INSERT INTO `creature_text` VALUES ('25162', '19', '0', 'Needless to say, a lot of us won\'t be going home. But if you remember your training and keep your wits about you, you\'ll greatly increase your chances.', '12', '0', '100', '0', '0', '0', '0', '0', 'Drill Sergeant Bahduum');
INSERT INTO `creature_text` VALUES ('25162', '20', '0', 'That\'s all I have to say for now. Stay focused, boys and girls.', '12', '0', '100', '0', '0', '0', '0', '0', 'Drill Sergeant Bahduum');
