INSERT INTO `creature_text` VALUES ('27378', '1', '0', 'If you\'d be so kind, please let me out of here!', '12', '0', '100', '0', '0', '0', '0', '0', 'Senior Scrivener Barriga upon LoS');
INSERT INTO `creature_text` VALUES ('27379', '0', '0', 'No! I beg you! Please don\'t kill me!', '12', '0', '100', '0', '0', '0', '0', '0', 'Engineer Burke before going hostile');
INSERT INTO `creature_text` VALUES ('27379', '1', '0', 'Can Someone let me out of here? I need to get back to Venomspite and finish my shift.', '12', '0', '100', '0', '0', '0', '0', '0', 'Engineer Burke upon LoS');
INSERT INTO `creature_text` VALUES ('27381', '0', '0', 'What is the meaning of this? Stop! I\'ll double whatever they\'re paying you!', '12', '0', '100', '0', '0', '0', '0', '0', 'Chancellor Amai before going hostile');
INSERT INTO `creature_text` VALUES ('27381', '1', '0', 'I just arrived in Venomspite. How could i have possibly wronged you?', '12', '0', '100', '0', '0', '0', '0', '0', 'Chancellor Amai upon Los');
INSERT INTO `creature_text` VALUES ('26179', '0', '0', 'Victory or death! For the Horde!', '14', '1', '100', '15', '0', '0', '0', '0', 'Taunka\'le Refugee');
INSERT INTO `creature_text` VALUES ('26184', '0', '0', 'Victory or death! For the Horde!', '14', '1', '100', '15', '0', '0', '0', '0', 'Taunka\'le Refugee');
INSERT INTO `creature_text` VALUES ('28859', '15', '0', 'Alexstrasza! Another of your brood falls!', '14', '0', '100', '0', '3600', '14534', '0', '0', 'Malygos - Killed Player (Phase 3)');
INSERT INTO `creature_text` VALUES ('28859', '15', '1', 'Little more then gnats!', '14', '0', '100', '0', '2650', '14535', '0', '0', 'Malygos - Killed Player (Phase 3)');
INSERT INTO `creature_text` VALUES ('28859', '15', '2', 'Your red allies will share your fate...', '14', '0', '100', '0', '3000', '14536', '0', '0', 'Malygos - Killed Player (Phase 3)');
INSERT INTO `creature_text` VALUES ('28859', '16', '0', 'Still standing? Not for long...', '14', '0', '100', '0', '3600', '14537', '0', '0', 'Malygos - Spell Casting 1(Phase 3)');
INSERT INTO `creature_text` VALUES ('28859', '16', '1', 'Your cause is lost!', '14', '0', '100', '0', '2000', '14538', '0', '0', 'Malygos - Spell Casting 2 (Phase 3)');
INSERT INTO `creature_text` VALUES ('28859', '16', '2', 'Your fragile mind will be shattered!', '14', '0', '100', '0', '4000', '14539', '0', '0', 'Malygos - Spell Casting 3 (Phase 3)');
INSERT INTO `creature_text` VALUES ('28859', '17', '0', 'Unthinkable! The mortals will destroy... everything! My sister, what have you...', '14', '0', '100', '0', '8500', '14540', '0', '0', 'Malygos - Death');
INSERT INTO `creature_text` VALUES ('28859', '18', '0', '%s fixes his eyes on you!', '42', '0', '100', '0', '3000', '0', '0', '0', 'Malygos - Surge of Power warning (Phase 3)');
INSERT INTO `creature_text` VALUES ('28859', '19', '0', '%s goes into a berserker rage!', '41', '0', '100', '0', '10000', '0', '0', '0', 'Malygos - Hit berserk timer (Any phase)');
INSERT INTO `creature_text` VALUES ('32295', '0', '0', 'I did what I had to, brother. You gave me no alternative.', '14', '0', '100', '0', '4000', '14406', '0', '0', 'Alexstrasza - Yell text 1');
INSERT INTO `creature_text` VALUES ('32295', '1', '0', 'And so ends the Nexus War.', '14', '0', '100', '0', '4000', '14407', '0', '0', 'Alexstrasza - Yell text 2');
INSERT INTO `creature_text` VALUES ('32295', '2', '0', 'This resolution pains me deeply, but the destruction, the monumental loss of life had to end. Regardless of Malygos\'s recent transgressions, I will mourn his loss. He was once a guardian, a protector. This day, one of the world\'s mightiest has fallen.', '14', '0', '100', '0', '24000', '14408', '0', '0', 'Alexstrasza - Yell text 3');
INSERT INTO `creature_text` VALUES ('32295', '3', '0', 'The red dragonflight will take on the burden of mending the devastation wrought on Azeroth. Return home to your people and rest. Tomorrow will bring you new challenges, and you must be ready to face them. Life... goes on.', '14', '0', '100', '0', '22000', '14409', '0', '0', 'Alexstrasza - Yell text 4');
INSERT INTO `creature_text` VALUES ('23837', '0', '0', 'Scrying upon many insignificant situations within Icecrown, you stumble upon something interesting...', '42', '0', '100', '0', '0', '0', '0', '0', 'Eye of Lich King Ambient');
INSERT INTO `creature_text` VALUES ('30835', '0', '0', 'My lady.', '12', '0', '100', '0', '0', '0', '0', '0', 'Shadow Cultist');
INSERT INTO `creature_text` VALUES ('30835', '1', '0', 'There is word from Jotunheim. The sleep-watchers there believe that they have found someone of significance.', '12', '0', '100', '0', '0', '0', '0', '0', 'Shadow Cultist');
INSERT INTO `creature_text` VALUES ('30835', '2', '0', 'The cultist practically sputters.', '16', '0', '100', '0', '0', '0', '0', '0', 'Shadow Cultist');
INSERT INTO `creature_text` VALUES ('30835', '3', '0', 'Look like, my lady? A vrykul, I suppose. They did not actually show him to me. Ever since The Shadow Vault....', '12', '0', '100', '0', '0', '0', '0', '0', 'Shadow Cultist');
INSERT INTO `creature_text` VALUES ('30835', '4', '0', 'A name? Oh, yes, the name! I believe it was Iskalder.', '12', '0', '100', '0', '0', '0', '0', '0', 'Shadow Cultist');
INSERT INTO `creature_text` VALUES ('30835', '5', '0', 'Right away, my lady.', '12', '0', '100', '2', '0', '0', '0', '0', 'Shadow Cultist');
INSERT INTO `creature_text` VALUES ('30836', '0', '0', 'Report.', '12', '0', '100', '1', '0', '0', '0', '0', 'Vardmadra');
INSERT INTO `creature_text` VALUES ('30836', '1', '0', 'Describe this vrykul. What does he look like?', '12', '0', '100', '1', '0', '0', '0', '0', 'Vardmadra');
INSERT INTO `creature_text` VALUES ('30836', '2', '0', 'I am not interested in excuses. Perhaps they gave you a name?', '12', '0', '100', '1', '0', '0', '0', '0', 'Vardmadra');
INSERT INTO `creature_text` VALUES ('30836', '3', '0', 'Iskalder?! You fool! Have you no idea who that is? He\'s only the greatest vrykul warrior who ever lived!', '12', '0', '100', '1', '0', '0', '0', '0', 'Vardmadra');
INSERT INTO `creature_text` VALUES ('30836', '4', '0', 'Return to Jotunheim and tell them to keep him asleep until I arrive. I will judge this vrykul with my own eyes.', '12', '0', '100', '1', '0', '0', '0', '0', 'Vardmadra');
INSERT INTO `creature_text` VALUES ('21502', '0', '0', 'Doom Lord Kazzak will be pleased. You are to increase the pace of your attacks. Destroy the orcish and dwarven strongholds with all haste.', '12', '0', '100', '1', '0', '0', '0', '0', 'Razuun');
INSERT INTO `creature_text` VALUES ('21502', '1', '0', 'Don\'t worry about that. I\'ve increased production at the Deathforge. You\'ll have all the infernals you need to carry out your orders. Don\'t fail, Jovaan.', '12', '0', '100', '1', '0', '0', '0', '0', 'Razuun');
INSERT INTO `creature_text` VALUES ('21502', '2', '0', 'Yes?', '12', '0', '100', '6', '0', '0', '27076', '0', 'Razuun');
INSERT INTO `creature_text` VALUES ('21502', '3', '0', 'Crate? I didn\'t send you a crate, Jovaan. Don\'t you have more important things to worry about? Go see to them!', '12', '0', '100', '274', '0', '0', '0', '0', 'Razuun');
INSERT INTO `creature_text` VALUES ('21633', '0', '0', 'Everything is in readiness, warbringer.', '12', '0', '100', '0', '0', '0', '0', '0', 'Jovaan');
INSERT INTO `creature_text` VALUES ('21633', '1', '0', 'Warbringer, that will require the use of all the hold\'s infernals. It may leave us vulnerable to a counterattack.', '12', '0', '100', '0', '0', '0', '0', '0', 'Jovaan');
INSERT INTO `creature_text` VALUES ('21633', '2', '0', 'It shall be as you say, warbringer. One last question, if I may...', '12', '0', '100', '6', '0', '0', '0', '0', 'Jovaan');
INSERT INTO `creature_text` VALUES ('21633', '3', '0', 'What\'s in the crate?', '12', '0', '100', '6', '0', '0', '0', '0', 'Jovaan');
INSERT INTO `creature_text` VALUES ('33273', '0', '0', 'Are those winter hyacinths? For me?', '12', '0', '100', '6', '0', '0', '0', '0', 'Maiden of Drak\'Mar');
INSERT INTO `creature_text` VALUES ('33273', '1', '0', 'It\'s been so long since a traveler has come here bearing flowers.', '12', '0', '100', '1', '0', '0', '0', '0', 'Maiden of Drak\'Mar');
INSERT INTO `creature_text` VALUES ('33273', '2', '0', 'The lake has been too lonely these past years. The travelers stopped coming and evil came to these waters.', '12', '0', '100', '1', '0', '0', '0', '0', 'Maiden of Drak\'Mar');
INSERT INTO `creature_text` VALUES ('33273', '3', '0', 'Your gift is a rare kindness, traveler. Please take this blade with my gratitude. Long ago, another traveler left it here, but I have little use for it.', '12', '0', '100', '1', '0', '0', '0', '0', 'Maiden of Drak\'Mar');
INSERT INTO `creature_text` VALUES ('29621', '0', '0', 'How did you find me? Did Landgren tell?', '12', '0', '100', '0', '0', '14201', '0', '0', 'Grand Admiral Westwind');
INSERT INTO `creature_text` VALUES ('29621', '1', '0', 'You thought I would just let you kill me?', '12', '0', '100', '0', '0', '14205', '0', '0', 'Grand Admiral Westwind');
INSERT INTO `creature_text` VALUES ('29621', '2', '0', 'WHAT?! No matter. Even without my sphere, I will crush you! Behold my true identity and despair!', '14', '0', '100', '0', '0', '14207', '0', '0', 'Grand Admiral Westwind');
INSERT INTO `creature_text` VALUES ('29621', '3', '0', 'Gah! I spent too much time in that weak little shell.', '14', '0', '100', '0', '0', '14426', '0', '0', 'Grand Admiral Westwind');
INSERT INTO `creature_text` VALUES ('29621', '4', '0', 'Kirel narak! I am Mal\'Ganis. I AM ETERNAL!', '14', '0', '100', '0', '0', '14427', '0', '0', 'Grand Admiral Westwind');
INSERT INTO `creature_text` VALUES ('29621', '5', '0', 'ENOUGH! I waste my time here. I must gather my strength on the homeworld.', '14', '0', '100', '0', '0', '14428', '0', '0', 'Grand Admiral Westwind');
INSERT INTO `creature_text` VALUES ('29621', '6', '0', 'You\'ll never defeat the Lich King without my forces. I\'ll have my revenge... on him AND you!', '14', '0', '100', '0', '0', '14429', '0', '0', 'Grand Admiral Westwind');
INSERT INTO `creature_text` VALUES ('26811', '0', '0', 'I could sleep forever, mon....', '12', '0', '100', '0', '0', '0', '0', '0', 'Ancient Drakkari Warmonger');
INSERT INTO `creature_text` VALUES ('26811', '0', '1', 'Finally, I can be restin\'...', '12', '0', '100', '0', '0', '0', '0', '0', 'Ancient Drakkari Warmonger');
INSERT INTO `creature_text` VALUES ('26811', '0', '2', 'Free at last, mon.', '12', '0', '100', '0', '0', '0', '0', '0', 'Ancient Drakkari Warmonger');
INSERT INTO `creature_text` VALUES ('26811', '0', '3', 'Where\'s mah mummy?', '12', '0', '100', '0', '0', '0', '0', '0', 'Ancient Drakkari Warmonger');
INSERT INTO `creature_text` VALUES ('26812', '0', '0', 'I could sleep forever, mon....', '12', '0', '100', '0', '0', '0', '0', '0', 'Ancient Drakkari Soothsayer');
INSERT INTO `creature_text` VALUES ('26812', '0', '1', 'Finally, I can be restin\'...', '12', '0', '100', '0', '0', '0', '0', '0', 'Ancient Drakkari Soothsayer');
INSERT INTO `creature_text` VALUES ('26812', '0', '2', 'Free at last, mon.', '12', '0', '100', '0', '0', '0', '0', '0', 'Ancient Drakkari Soothsayer');
INSERT INTO `creature_text` VALUES ('26812', '0', '3', 'Where\'s mah mummy?', '12', '0', '100', '0', '0', '0', '0', '0', 'Ancient Drakkari Soothsayer');
INSERT INTO `creature_text` VALUES ('24405', '0', '0', 'Thank you!', '12', '0', '100', '17', '0', '0', '37237', '0', 'Adarrah');
INSERT INTO `creature_text` VALUES ('26814', '0', '0', 'Alright, kid. Stay behind me and you\'ll be fine.', '12', '0', '100', '60', '0', '0', '0', '0', 'Harrison Jones');
INSERT INTO `creature_text` VALUES ('26814', '1', '0', 'Their ceremonial chamber, where I was to be sacrificed...', '12', '0', '100', '396', '0', '0', '0', '0', 'Harrison Jones');
INSERT INTO `creature_text` VALUES ('26814', '2', '0', 'Time to put an end to all this!', '12', '0', '100', '0', '0', '0', '0', '0', 'Harrison Jones');
INSERT INTO `creature_text` VALUES ('26814', '3', '0', 'You\'re free to go, miss.', '12', '0', '100', '0', '0', '0', '0', '0', 'Harrison Jones');
INSERT INTO `creature_text` VALUES ('26814', '4', '0', 'Odd. That usually does it.', '12', '0', '100', '396', '0', '0', '0', '0', 'Harrison Jones');
INSERT INTO `creature_text` VALUES ('26814', '5', '0', 'Just as well, I\'ve had enough of this place.', '12', '0', '100', '0', '0', '0', '0', '0', 'Harrison Jones');
INSERT INTO `creature_text` VALUES ('26814', '6', '0', 'What\'s this?', '12', '0', '100', '0', '0', '0', '26036', '0', 'Harrison Jones');
INSERT INTO `creature_text` VALUES ('26814', '7', '0', 'Aww, not a snake!', '12', '0', '100', '0', '0', '0', '0', '0', 'Harrison Jones');
INSERT INTO `creature_text` VALUES ('26814', '8', '0', 'Listen, kid. I can handle this thing. You just watch my back!', '12', '0', '100', '0', '0', '0', '0', '0', 'Harrison Jones');
INSERT INTO `creature_text` VALUES ('26814', '9', '0', 'See ya \'round, kid!', '12', '0', '100', '0', '0', '0', '0', '0', 'Harrison Jones');
INSERT INTO `creature_text` VALUES ('30395', '0', '0', 'My brother, Stormhoof, was a far greater warrior than I. While I trained to be the chieftain of our clan, he prepared for a larger quest.', '12', '0', '100', '1', '0', '0', '0', '0', 'Chieftain Swiftspear');
INSERT INTO `creature_text` VALUES ('30395', '1', '0', 'How could he be in danger? All he wanted was to give us power over the elements that made life harsh for us here.', '12', '0', '100', '1', '0', '0', '0', '0', 'Chieftain Swiftspear');
INSERT INTO `creature_text` VALUES ('30395', '2', '0', 'He sought an artifact... a horn, I think. He left the village on a long journey in search of it.', '12', '0', '100', '6', '0', '0', '0', '0', 'Chieftain Swiftspear');
INSERT INTO `creature_text` VALUES ('30395', '3', '0', 'I know that a terrible enemy pursued him after he won the horn, but I... I don\'t recall... what happened... next.', '12', '0', '100', '5', '0', '0', '0', '0', 'Chieftain Swiftspear');
INSERT INTO `creature_text` VALUES ('30395', '4', '0', 'It is as though my memories are shrouded in mist. I cannot even recall what became of my brother. Is this how you mean that he is in danger?', '12', '0', '100', '274', '0', '0', '0', '0', 'Chieftain Swiftspear');
INSERT INTO `creature_text` VALUES ('30395', '5', '0', 'The disturbances in the tomb, they must be involved. Look around you, $N. Do you see them?', '12', '0', '100', '25', '0', '0', '0', '0', 'Chieftain Swiftspear');
INSERT INTO `creature_text` VALUES ('30395', '6', '0', 'Yes, they are at fault, but I am powerless to stop them. Will you warn the people of Tunka\'lo, stranger, that their past and their ancestors are in danger?', '12', '0', '100', '1', '0', '0', '0', '0', 'Chieftain Swiftspear');
INSERT INTO `creature_text` VALUES ('26593', '0', '0', 'Perfect.  As long as you don\'t go hacking and slashing your way through, they\'ll never know the difference.', '12', '0', '100', '0', '0', '0', '0', '0', 'Serinar');
INSERT INTO `creature_text` VALUES ('10300', '0', '0', 'Remember, I need your help to properly channel. I will ask you to aid me several times in our path, so please be ready.', '12', '0', '100', '0', '0', '0', '0', '0', 'Ranshalla SAY_QUEST_START');
INSERT INTO `creature_text` VALUES ('10300', '1', '0', 'This blue light... It\'s strange. What do you think it means?', '12', '0', '100', '25', '0', '0', '0', '0', 'Ranshalla SAY_ENTER_OWL_THICKET');
INSERT INTO `creature_text` VALUES ('10300', '2', '0', 'We\'ve found it!', '12', '0', '100', '0', '0', '0', '0', '0', 'Ranshalla SAY_REACH_TORCH_1');
INSERT INTO `creature_text` VALUES ('10300', '2', '1', 'Please, light this while I am channeling', '12', '0', '100', '0', '0', '0', '0', '0', 'Ranshalla SAY_REACH_TORCH_2');
INSERT INTO `creature_text` VALUES ('10300', '2', '2', 'This is the place. Let\'s light it.', '12', '0', '100', '0', '0', '0', '0', '0', 'Ranshalla SAY_REACH_TORCH_3');
INSERT INTO `creature_text` VALUES ('10300', '3', '0', 'Let\'s find the next one.', '12', '0', '100', '0', '0', '0', '0', '0', 'Ranshalla SAY_AFTER_TORCH_1');
INSERT INTO `creature_text` VALUES ('10300', '3', '1', 'We must continue on now.', '12', '0', '100', '0', '0', '0', '0', '0', 'Ranshalla SAY_AFTER_TORCH_2');
INSERT INTO `creature_text` VALUES ('10300', '4', '0', 'It is time for the final step; we must activate the altar.', '12', '0', '100', '0', '0', '0', '0', '0', 'Ranshalla SAY_REACH_ALTAR_1');
INSERT INTO `creature_text` VALUES ('10300', '5', '0', 'I will read the words carved into the stone, and you must find a way to light it.', '12', '0', '100', '0', '0', '0', '0', '0', 'Ranshalla SAY_REACH_ALTAR_2');
INSERT INTO `creature_text` VALUES ('10300', '6', '0', 'The altar is glowing! We have done it!', '12', '0', '100', '0', '0', '0', '0', '0', 'Ranshalla SAY_RANSHALLA_ALTAR_1');
INSERT INTO `creature_text` VALUES ('10300', '7', '0', 'What is happening? Look!', '12', '0', '100', '0', '0', '0', '0', '0', 'Ranshalla SAY_RANSHALLA_ALTAR_2');
INSERT INTO `creature_text` VALUES ('12116', '8', '0', 'It has been many years...', '12', '0', '100', '0', '0', '0', '0', '0', 'Priestess of Elune SAY_PRIESTESS_ALTAR_3');
INSERT INTO `creature_text` VALUES ('12116', '9', '0', 'Who has disturbed the altar of the goddess?', '12', '0', '100', '0', '0', '0', '0', '0', 'Priestess of Elune SAY_PRIESTESS_ALTAR_4');
INSERT INTO `creature_text` VALUES ('10300', '10', '0', 'Please, priestesses, forgive us for our intrusion. We do not wish any harm here.', '12', '0', '100', '0', '0', '0', '0', '0', 'Ranshalla SAY_RANSHALLA_ALTAR_5');
INSERT INTO `creature_text` VALUES ('10300', '11', '0', 'We only wish to know why the wildkin guard this area...', '12', '0', '100', '0', '0', '0', '0', '0', 'Ranshalla SAY_RANSHALLA_ALTAR_6');
INSERT INTO `creature_text` VALUES ('12116', '12', '0', 'Enu thora\'serador. This is a sacred place.', '12', '0', '100', '0', '0', '0', '0', '0', 'Priestess of Elune SAY_PRIESTESS_ALTAR_7');
INSERT INTO `creature_text` VALUES ('12116', '13', '0', 'We will show you...', '12', '0', '100', '0', '0', '0', '0', '0', 'Priestess of Elune SAY_PRIESTESS_ALTAR_8');
INSERT INTO `creature_text` VALUES ('12116', '14', '0', 'Look above you; thara dormil dorah...', '12', '0', '100', '0', '0', '0', '0', '0', 'Priestess of Elune SAY_PRIESTESS_ALTAR_9');
INSERT INTO `creature_text` VALUES ('12116', '15', '0', 'This gem once allowed direct communication with Elune, herself.', '12', '0', '100', '0', '0', '0', '0', '0', 'Priestess of Elune SAY_PRIESTESS_ALTAR_10');
INSERT INTO `creature_text` VALUES ('12116', '16', '0', 'Through the gem, Elune channeled her infinite wisdom...', '12', '0', '100', '0', '0', '0', '0', '0', 'Priestess of Elune SAY_PRIESTESS_ALTAR_11');
INSERT INTO `creature_text` VALUES ('12116', '17', '0', 'Realizing that the gem needed to be protected, we turned to the goddess herself.', '12', '0', '100', '0', '0', '0', '0', '0', 'Priestess of Elune SAY_PRIESTESS_ALTAR_12');
INSERT INTO `creature_text` VALUES ('12116', '18', '0', 'Soon after, we began to have visions of a creature... A creature with the feathers of an owl, but the will and might of a bear...', '12', '0', '100', '0', '0', '0', '0', '0', 'Priestess of Elune SAY_PRIESTESS_ALTAR_13');
INSERT INTO `creature_text` VALUES ('12116', '19', '0', 'It was on that day that the wildkin were given to us. Fierce guardians, the goddess assigned the wildkin to protect all of her sacred places.', '12', '0', '100', '0', '0', '0', '0', '0', 'Priestess of Elune SAY_PRIESTESS_ALTAR_14');
INSERT INTO `creature_text` VALUES ('12152', '20', '0', 'Anu\'dorini Talah, Ru shallora enudoril.', '12', '0', '100', '0', '0', '0', '0', '0', 'Voice of Elune SAY_VOICE_ALTAR_15');
INSERT INTO `creature_text` VALUES ('12116', '21', '0', 'But now, many years later, the wildkin have grown more feral, and without the guidance of the goddess, they are confused...', '12', '0', '100', '0', '0', '0', '0', '0', 'Priestess of Elune SAY_PRIESTESS_ALTAR_16');
INSERT INTO `creature_text` VALUES ('12116', '22', '0', 'Without a purpose, they wander... But many find their way back to the sacred areas that they once were sworn to protect.', '12', '0', '100', '0', '0', '0', '0', '0', 'Priestess of Elune SAY_PRIESTESS_ALTAR_17');
INSERT INTO `creature_text` VALUES ('12116', '23', '0', 'Wildkin are inherently magical; this power was bestowed upon them by the goddess.', '12', '0', '100', '0', '0', '0', '0', '0', 'Priestess of Elune SAY_PRIESTESS_ALTAR_18');
INSERT INTO `creature_text` VALUES ('12116', '24', '0', 'Know that wherever you might find them in the world, they are protecting something of importance, as they were entrusted to do so long ago.', '12', '0', '100', '0', '0', '0', '0', '0', 'Priestess of Elune SAY_PRIESTESS_ALTAR_19');
INSERT INTO `creature_text` VALUES ('12116', '25', '0', 'Please, remember what we have shown you...', '12', '0', '100', '0', '0', '0', '0', '0', 'Priestess of Elune SAY_PRIESTESS_ALTAR_20');
INSERT INTO `creature_text` VALUES ('12116', '26', '0', 'Farewell.', '12', '0', '100', '0', '0', '0', '0', '0', 'Priestess of Elune SAY_PRIESTESS_ALTAR_21');
