INSERT INTO `creature_text` VALUES ('33552', '0', '0', 'A thousand deaths... or one murder.', '12', '0', '100', '0', '0', '15762', '0', '0', 'Yogg-Saron');
INSERT INTO `creature_text` VALUES ('33552', '1', '0', 'Your petty quarrels only make me stronger!', '12', '0', '100', '0', '0', '15764', '0', '0', 'Yogg-Saron');
INSERT INTO `creature_text` VALUES ('33552', '2', '0', 'Yrr n\'lyeth... shuul anagg!', '12', '0', '100', '0', '0', '15766', '0', '0', 'Yogg-Saron');
INSERT INTO `creature_text` VALUES ('33552', '3', '0', 'He will learn... no king rules forever; only death is eternal!', '12', '0', '100', '0', '0', '15767', '0', '0', 'Yogg-Saron');
INSERT INTO `creature_text` VALUES ('33552', '4', '0', 'His brood learned their lesson before too long. You will soon learn yours!', '12', '0', '100', '0', '0', '15765', '0', '0', 'Yogg-Saron');
INSERT INTO `creature_text` VALUES ('33241', '0', '0', 'As you wish, $N.', '12', '0', '100', '0', '0', '0', '0', '0', 'Freya (Observation Ring) - On Gossip Select');
INSERT INTO `creature_text` VALUES ('33241', '1', '0', 'Eonar, your servant calls for your blessing!', '14', '0', '100', '0', '0', '15535', '0', '0', 'Freya (Observation Ring) - On Gossip Select');
INSERT INTO `creature_text` VALUES ('33213', '0', '0', 'As you wish, $N.', '12', '0', '100', '0', '0', '0', '0', '0', 'Hodir (Observation Ring) - On Gossip Select');
INSERT INTO `creature_text` VALUES ('33213', '1', '0', 'The veil of winter will protect you, champions!', '14', '0', '100', '0', '0', '15559', '0', '0', 'Hodir (Observation Ring) - On Gossip Select');
INSERT INTO `creature_text` VALUES ('33242', '0', '0', 'As you wish, $N.', '12', '0', '100', '0', '0', '0', '0', '0', 'Thorim (Observation Ring) - On Gossip Select');
INSERT INTO `creature_text` VALUES ('33242', '1', '0', 'Golganneth, lend me your strengh! Grant my mortal allies the power of thunder!', '14', '0', '100', '0', '0', '15750', '0', '0', 'Thorim (Observation Ring) - On Gossip Select');
INSERT INTO `creature_text` VALUES ('33244', '0', '0', 'As you wish, $N.', '12', '0', '100', '0', '0', '0', '0', '0', 'Mimiron (Observation Ring) - On Gossip Select');
INSERT INTO `creature_text` VALUES ('33244', '1', '0', 'Combat matrix enhanced. Behold wonderous rapidity!', '14', '0', '100', '0', '0', '15630', '0', '0', 'Mimiron (Observation Ring) - On Gossip Select');
INSERT INTO `creature_text` VALUES ('31254', '0', '0', 'The undead are upon us!', '12', '0', '10', '0', '0', '0', '0', '0', 'Lordaeron Footsoldier to Ghoulish Minion');
INSERT INTO `creature_text` VALUES ('31254', '0', '1', 'The rumors were true!  The prince has gone mad!', '12', '0', '10', '0', '0', '0', '0', '0', 'Lordaeron Footsoldier to Ghoulish Minion');
INSERT INTO `creature_text` VALUES ('32414', '0', '0', 'You\'ve come to address the men, sir?', '12', '0', '100', '66', '0', '0', '0', '0', 'Lordaeron Captain');
INSERT INTO `creature_text` VALUES ('32443', '0', '0', 'Rise, Sindragosa! Rise and lead the Frostbrood into war!', '12', '0', '100', '16', '0', '0', '0', '0', 'Lich King');
INSERT INTO `creature_text` VALUES ('32588', '0', '0', 'Prepare to die!', '12', '0', '100', '0', '0', '0', '39063', '0', 'Illidan Stormrage');
INSERT INTO `creature_text` VALUES ('26648', '0', '0', 'Little $N, why do you call me forth? Are you working with the trolls of this land? Have you come to kill me and take my power as your own?', '14', '0', '100', '33', '0', '11557', '0', '0', 'Oacha\'noa');
INSERT INTO `creature_text` VALUES ('26648', '1', '0', 'I sense uncertainty in you, and I do not trust it whether you are with them, or not. If you wish my augury for the Kalu\'ak, you will have to prove yourself first.', '14', '0', '100', '33', '0', '11557', '0', '0', 'Oacha\'noa');
INSERT INTO `creature_text` VALUES ('26648', '2', '0', 'I will lay a mild compulsion upon you. Jump into the depths before me so that you put yourself into my element and thereby display your submission.', '14', '0', '100', '33', '0', '11557', '0', '0', 'Oacha\'noa');
INSERT INTO `creature_text` VALUES ('26648', '3', '0', 'Though you are compelled, the choice, and the last step before you leap, are yours. You have twenty more seconds to decide.', '14', '0', '100', '33', '0', '11557', '0', '0', 'Oacha\'noa');
INSERT INTO `creature_text` VALUES ('26648', '4', '0', 'I\'m rather curious as to what will happen, should you ignore this and NOT jump into the water.', '14', '0', '100', '33', '0', '11557', '0', '0', 'Oacha\'noa');
INSERT INTO `creature_text` VALUES ('26648', '5', '0', 'Well done, $N. Your display of respect is duly noted. Now, I have information for you that you must convey to the Kalu\'ak.', '14', '0', '100', '33', '0', '11557', '0', '0', 'Oacha\'noa');
INSERT INTO `creature_text` VALUES ('26648', '6', '0', 'Simply put, you must tell the tuskarr that they cannot run. If they do so, their spirits will be destroyed by the evil rising within Northrend.', '15', '0', '100', '0', '0', '0', '0', '0', 'Oacha\'noa');
INSERT INTO `creature_text` VALUES ('26648', '7', '0', 'Tell the mystic that his people are to stand and fight alongside the Horde and Alliance against the forces of Malygos and the Lich King.', '15', '0', '100', '0', '0', '0', '0', '0', 'Oacha\'noa');
INSERT INTO `creature_text` VALUES ('26648', '8', '0', 'Now swim back with the knowledge I have granted you. Do what you can for them, $r.', '15', '0', '100', '0', '0', '0', '0', '0', 'Oacha\'noa');
INSERT INTO `creature_text` VALUES ('26648', '9', '0', 'Very well, $N, you have failed to act. The prophecy is not yours to learn. Do not call upon me again until you have found your backbone.', '14', '0', '100', '33', '0', '11557', '0', '0', 'Oacha\'noa');
INSERT INTO `creature_text` VALUES ('30299', '0', '0', 'You didn\'t think that I was going to let you walk in here and take the Heart of the Storm, did you?', '12', '0', '100', '6', '0', '0', '0', '0', 'Overseer Narvir');
INSERT INTO `creature_text` VALUES ('30299', '1', '0', 'You may have killed Valduran, but that will not stop me from completing the colossus.', '12', '0', '100', '1', '0', '0', '0', '0', 'Overseer Narvir');
INSERT INTO `creature_text` VALUES ('37226', '3', '0', 'Foolish girl. You seek that, which I killed long ago. He is nearly a ghost now. A fate ago in my mind.', '14', '0', '100', '0', '0', '17229', '0', '0', 'Lich King SAY_LK_JAINA_INTRO_END');
INSERT INTO `creature_text` VALUES ('37226', '4', '0', 'I will not make the same mistake again. This time there will be no escape. You fail to serve me in undead. Now, all that remains for you is oblivion!', '14', '0', '100', '0', '0', '17228', '0', '0', 'Lich King SAY_LK_SYLVANAS_INTRO_END');
INSERT INTO `creature_text` VALUES ('36723', '0', '0', 'You are not worthy to face the Lich King!', '14', '0', '100', '0', '0', '16921', '0', '0', 'general SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('36723', '1', '0', 'Master, I have failed...', '14', '0', '100', '0', '0', '16922', '0', '0', 'general SAY_DEATH');
INSERT INTO `creature_text` VALUES ('36954', '0', '0', 'Your allies have arrived, Jaina, just as you promised. You will all become powerful agents of the Scourge.', '14', '0', '100', '0', '0', '17212', '0', '0', 'Lich King SAY_LICH_KING_AGGRO_A');
INSERT INTO `creature_text` VALUES ('36954', '1', '0', 'I will not make the same mistake again, Sylvanas. This time there will be no escape. You will all serve me in death!', '14', '0', '100', '0', '0', '17213', '0', '0', 'Lich King SAY_LICH_KING_AGGRO_H');
INSERT INTO `creature_text` VALUES ('36954', '2', '0', 'Succumb to the chill of the grave.', '14', '0', '100', '0', '0', '17218', '0', '0', 'Lich King SAY_LICH_KING_WALL_02');
INSERT INTO `creature_text` VALUES ('36954', '3', '0', 'Another dead end.', '14', '0', '100', '0', '0', '17219', '0', '0', 'Lich King SAY_LICH_KING_WALL_03');
INSERT INTO `creature_text` VALUES ('36954', '4', '0', 'How long can you fight it?', '14', '0', '100', '0', '0', '17220', '0', '0', 'Lich King SAY_LICH_KING_WALL_04');
INSERT INTO `creature_text` VALUES ('36954', '5', '0', 'There is no escape!', '14', '0', '100', '0', '0', '17217', '0', '0', 'Lich King SAY_LICH_KING_WALL_01');
INSERT INTO `creature_text` VALUES ('36954', '6', '0', 'Rise minions, do not left them us!', '14', '0', '100', '0', '0', '17216', '0', '0', 'Lich King SAY_LICH_KING_GNOUL');
INSERT INTO `creature_text` VALUES ('36954', '7', '0', 'Minions sees them. Bring their corpses back to me!', '14', '0', '100', '0', '0', '17222', '0', '0', 'Lich King SAY_LICH_KING_ABON');
INSERT INTO `creature_text` VALUES ('36954', '8', '0', 'Death\'s cold embrace awaits.', '14', '0', '100', '0', '0', '17221', '0', '0', 'Lich King SAY_LICH_KING_WINTER');
INSERT INTO `creature_text` VALUES ('36954', '9', '0', 'Nowhere to run! You\'re mine now...', '14', '0', '100', '0', '0', '17223', '0', '0', 'Lich King SAY_LICH_KING_END_DUN');
INSERT INTO `creature_text` VALUES ('36954', '10', '0', 'All is lost!', '14', '0', '100', '0', '0', '17215', '0', '0', 'Lich King SAY_LICH_KING_WIN');
INSERT INTO `creature_text` VALUES ('36955', '0', '0', 'He is too powerful, we must leave this place at once! My magic will hold him in place for only a short time! Come quickly, heroes!', '14', '0', '100', '1', '0', '16644', '0', '0', 'Jaina SAY_JAINA_AGGRO');
INSERT INTO `creature_text` VALUES ('36955', '1', '0', 'I will destroy this barrier. You must hold the undead back!', '14', '0', '100', '0', '0', '16607', '0', '0', 'Jaina SAY_JAINA_WALL_01');
INSERT INTO `creature_text` VALUES ('36955', '2', '0', 'Another ice wall! Keep the undead from interrupting my incantation so that I may bring this wall down!', '14', '0', '100', '0', '0', '16608', '0', '0', 'Jaina SAY_JAINA_WALL_02');
INSERT INTO `creature_text` VALUES ('36955', '3', '0', 'He is toying with us! I will show him what happens to ice when it meets fire!', '14', '0', '100', '0', '0', '16609', '0', '0', 'Jaina SAY_JAINA_WALL_03');
INSERT INTO `creature_text` VALUES ('36955', '4', '0', 'Your barriers can\'t hold us back much longer, monster. I will shatter them all!', '14', '0', '100', '0', '0', '16610', '0', '0', 'Jaina SAY_JAINA_WALL_04');
INSERT INTO `creature_text` VALUES ('36955', '5', '0', 'There\'s an opening up ahead. GO NOW!', '14', '0', '100', '1', '0', '16645', '0', '0', 'Jaina SAY_JAINA_ESCAPE_01');
INSERT INTO `creature_text` VALUES ('36955', '6', '0', 'We\'re almost there... Don\'t give up!', '14', '0', '100', '0', '0', '16646', '0', '0', 'Jaina SAY_JAINA_ESCAPE_02');
INSERT INTO `creature_text` VALUES ('36955', '7', '0', 'It... It\'s a dead end. We have no choice but to fight. Steel yourself heroes, for this is our last stand!', '14', '0', '100', '1', '0', '16647', '0', '0', 'Jaina SAY_JAINA_TRAP');
INSERT INTO `creature_text` VALUES ('36955', '8', '0', 'Forgive me heroes, I should have listened to Uther. I ... I had to see for myself, to look into his eyes one last time ... I am sorry.', '14', '0', '100', '1', '0', '16648', '0', '0', 'Jaina SAY_JAINA_FINAL_2');
INSERT INTO `creature_text` VALUES ('36955', '9', '0', 'We now know what must be done. I will deliver this news to King Varian and Highlord Fordring.', '14', '0', '100', '1', '0', '16649', '0', '0', 'Jaina SAY_JAINA_FINAL_3');
INSERT INTO `creature_text` VALUES ('37554', '0', '0', 'He\'s too powerful! Heroes, quickly, come to me! We must leave this place immediately! I will do what I can do hold him in place while we flee.', '14', '0', '100', '0', '0', '17058', '0', '0', 'Sylvanas SAY_SYLVANA_AGGRO');
INSERT INTO `creature_text` VALUES ('37554', '1', '0', 'No wall can hold the Banshee Queen! Keep the undead at bay, heroes! I will tear this barrier down!', '14', '0', '100', '0', '0', '17029', '0', '0', 'Sylvanas SAY_SYLVANA_WALL_01');
INSERT INTO `creature_text` VALUES ('37554', '2', '0', 'Another barrier? Stand strong, champions! I will bring the wall down!', '14', '0', '100', '0', '0', '17030', '0', '0', 'Sylvanas SAY_SYLVANA_WALL_02');
INSERT INTO `creature_text` VALUES ('37554', '3', '0', 'I grow tired of these games, Arthas! Your walls can\'t stop me!', '14', '0', '100', '0', '0', '17031', '0', '0', 'Sylvanas SAY_SYLVANA_WALL_03');
INSERT INTO `creature_text` VALUES ('37554', '4', '0', 'You won\'t impede our escape, fiend. Keep the undead off me while I bring this barrier down!', '14', '0', '100', '0', '0', '17032', '0', '0', 'Sylvanas SAY_SYLVANA_WALL_04');
INSERT INTO `creature_text` VALUES ('37554', '5', '0', 'There\'s an opening up ahead. GO NOW!', '14', '0', '100', '1', '0', '17059', '0', '0', 'Sylvanas SAY_SYLVANA_ESCAPE_01');
INSERT INTO `creature_text` VALUES ('37554', '6', '0', 'We\'re almost there! Don\'t give up!', '14', '0', '100', '0', '0', '17060', '0', '0', 'Sylvanas SAY_SYLVANA_ESCAPE_02');
INSERT INTO `creature_text` VALUES ('37554', '7', '0', 'BLASTED DEAD END! So this is how it ends. Prepare yourselves, heroes, for today we make our final stand!', '14', '0', '100', '5', '0', '17061', '0', '0', 'Sylvanas SAY_SYLVANA_TRAP');
INSERT INTO `creature_text` VALUES ('37554', '8', '0', 'We are safe... for now. His strength has increased tenfold since our last battle. It will take a mighty army to destroy the Lich King. An army greater than even the Horde can rouse.', '14', '0', '100', '1', '0', '17062', '0', '0', 'Sylvanas SAY_SYLVANA_FINAL_2');
INSERT INTO `creature_text` VALUES ('30344', '0', '0', 'Fire, fire!', '14', '0', '100', '0', '0', '16721', '0', '0', 'Bartlett SAY_BARTLETT_FIRE');
INSERT INTO `creature_text` VALUES ('30344', '1', '0', 'Quickly, climb aboard! We mustn\'t tarry here. There\'s no telling when this whole mountainside will collapse!', '14', '0', '100', '0', '0', '16722', '0', '0', 'Bartlett SAY_BARTLETT_FINAL_1');
INSERT INTO `creature_text` VALUES ('30824', '0', '0', 'FIRE! FIRE!', '14', '0', '100', '0', '0', '0', '38681', '0', 'Korm SAY_KORM_FIRE');
INSERT INTO `creature_text` VALUES ('30824', '1', '0', 'Quickly, climb aboard! We mustn\'t tarry here. There\'s no telling when this whole mountainside will collapse!', '14', '0', '100', '0', '0', '0', '0', '0', 'Korm SAY_KORM_FINAL_1');
INSERT INTO `creature_text` VALUES ('29542', '0', '0', 'I will not die so quickly this time!', '14', '0', '100', '0', '0', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('29560', '0', '0', 'Well done. Let\'s get this over with.', '12', '0', '100', '1', '0', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('29560', '1', '0', 'Come, Landgren, cough up your soul so that I can ask you a very important question.', '12', '0', '100', '0', '0', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('29560', '2', '0', 'YOU WILL TELL ME WHERE WESTWIND IS OR I WILL DESTROY YOUR SOUL!', '14', '0', '100', '25', '0', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('29560', '3', '0', 'Then you leave me no choice. I won\'t say that I won\'t enjoy this.', '12', '0', '100', '1', '0', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('29560', '4', '0', 'Very well. Tell me!', '12', '0', '100', '0', '0', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('29560', '5', '0', 'A hidden hollow? How very interesting. You\'ve served your purpose, Landgren, but I\'m afraid there\'ll be no resurrection for you this time!', '12', '0', '100', '6', '0', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('29560', '6', '0', 'Now that the unpleasantness is finished with, let\'s talk about you dealing with Grand Admiral Westwind.', '12', '0', '100', '11', '0', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('29572', '0', '0', 'You\'ll get nothing out of me, monster. I am beyond your ability to influence.', '12', '0', '100', '0', '0', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('29572', '1', '0', 'No.', '12', '0', '100', '0', '0', '0', '39548', '0', '');
INSERT INTO `creature_text` VALUES ('29572', '2', '0', 'STOP! It isn\'t worth it. I\'ll tell you where he is.', '12', '0', '100', '0', '0', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('29572', '3', '0', 'On the south end of the island is a cave -- a hidden hollow. The grand admiral has holed himself up in there, preparing for the final battle against the Lich King.', '12', '0', '100', '0', '0', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('29572', '4', '0', 'AAAEEEEIIIiiiiiiiiiiiiiiiiiiiiiiiiiiii........................................', '14', '0', '100', '0', '0', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('29919', '1', '0', 'By order of Duke Lankral, Commander of the Knights of the Ebon Blade, Vanquisher of the Scourge and Conquerer of The Shadow Vault, you are hearby served notice to cease and desist from all activities providing materiel assistance, vrykul, and any other type of support to the Lich King. Failure to comply will result in your immediate destruction. My messenger will serve as the instrument of my will. What is your decision?', '12', '0', '100', '0', '0', '0', '0', '0', 'Thane');
INSERT INTO `creature_text` VALUES ('29919', '2', '0', 'What is this?', '14', '0', '100', '0', '0', '0', '0', '0', 'Thane');
INSERT INTO `creature_text` VALUES ('29919', '3', '0', 'My answer? Here\'s my answer, little messenger!', '14', '0', '100', '0', '0', '0', '0', '0', 'Thane');
INSERT INTO `creature_text` VALUES ('29919', '4', '0', 'I will feed you to the dogs!', '14', '0', '100', '0', '0', '0', '0', '0', 'Thane');
INSERT INTO `creature_text` VALUES ('30924', '0', '0', 'Now fight me, $N! Kill Iskalder!', '14', '0', '100', '0', '0', '1167', '0', '0', 'Possessed Iskalder');
INSERT INTO `creature_text` VALUES ('30945', '0', '0', 'Iskalder, there you are. What is this? Engaged in battle already?', '14', '0', '100', '457', '0', '13824', '0', '0', 'Vardmadra');
INSERT INTO `creature_text` VALUES ('30945', '1', '0', 'NO! How is this possible?', '14', '0', '100', '457', '0', '13825', '0', '0', 'Vardmadra');
INSERT INTO `creature_text` VALUES ('30945', '2', '0', 'I know not how this was possible, but you must still be judged Iskalder. Wait... what is this?', '14', '0', '100', '457', '0', '13824', '0', '0', 'Vardmadra');
INSERT INTO `creature_text` VALUES ('30945', '3', '0', 'Stay away from me creature! Do not touch me!', '14', '0', '100', '0', '0', '1168', '0', '0', 'Vardmadra to Nightswood');
INSERT INTO `creature_text` VALUES ('30945', '4', '0', 'Ahahahahahaha! It is done. Return to my cave. We have much to discuss!', '14', '0', '100', '457', '0', '1167', '0', '0', 'Vardmadra to ');
INSERT INTO `creature_text` VALUES ('30955', '0', '0', 'This? This is me taking control of you. This is me setting up my final revenge!', '14', '0', '100', '457', '0', '13824', '0', '0', 'Lady Nightswood');
INSERT INTO `creature_text` VALUES ('27455', '0', '0', 'Captain, why are the guards not at their posts?', '12', '0', '100', '6', '0', '12725', '0', '0', 'Prince Arthas');
INSERT INTO `creature_text` VALUES ('27455', '1', '0', 'Uther had my troops recalled? Damn it! If my warriors abandon me, I\'ll never defeat Mal\'Ganis. The ships must be burned before the men reach the shore!', '12', '0', '100', '5', '0', '12726', '0', '0', 'Prince Arthas');
INSERT INTO `creature_text` VALUES ('27455', '2', '0', 'Burned down to their frames! No one goes home until our job here is done!', '12', '0', '100', '15', '0', '12727', '0', '0', 'Prince Arthas');
INSERT INTO `creature_text` VALUES ('27455', '3', '0', 'Spare me, Muradin. You weren\'t there to see what Mal\'Ganis did to my homeland.', '12', '0', '100', '1', '0', '12728', '0', '0', 'Prince Arthas');
INSERT INTO `creature_text` VALUES ('27455', '4', '0', 'Behold, Muradin, our salvation, Frostmourne.', '12', '0', '100', '1', '0', '12729', '0', '0', 'Prince Arthas');
INSERT INTO `creature_text` VALUES ('27455', '5', '0', 'I would gladly bear any curse to save my homeland.', '12', '0', '100', '1', '0', '12730', '0', '0', 'Prince Arthas');
INSERT INTO `creature_text` VALUES ('27455', '6', '0', 'Damn the men! Nothing shall prevent me from having my revenge, old friend. Not even you.', '12', '0', '100', '5', '0', '12731', '0', '0', 'Prince Arthas');
INSERT INTO `creature_text` VALUES ('27455', '7', '0', 'Now, I call out to the spirits of this place. I will give anything or pay any price, if only you will help me save my people.', '12', '0', '100', '396', '0', '12732', '0', '0', 'Prince Arthas');
INSERT INTO `creature_text` VALUES ('27480', '0', '0', 'Isn\'t that a bit much, lad?', '12', '0', '100', '6', '0', '12733', '0', '0', 'Muradin');
INSERT INTO `creature_text` VALUES ('27480', '1', '0', 'You lied to your men and betrayed the mercenaries who fought for you. What\'s happening to you, Arthas? Is vengeance all that\'s important to you?', '12', '0', '100', '1', '0', '12734', '0', '0', 'Muradin');
INSERT INTO `creature_text` VALUES ('27480', '2', '0', 'Hold, lad. There\'s an inscription on the dais. It\'s a warning. It says, \"Whomsoever takes up this blade shall wield power eternal. Just as the blade rends flesh, so must power scar the spirit.\" Oh, I should\'ve known. The blade is cursed! Let\'s get the hell out of here!', '12', '0', '100', '0', '0', '12735', '0', '0', 'Muradin');
INSERT INTO `creature_text` VALUES ('27480', '3', '0', 'Leave it be, Arthas. Forget this business and lead your men home.', '12', '0', '100', '396', '0', '12736', '0', '0', 'Muradin');
