INSERT INTO `creature_text` VALUES ('21293', '0', '0', 'Hide! Hide before they see you! Back up!', '15', '0', '100', '0', '0', '0', '0', '0', 'Borak');
INSERT INTO `creature_text` VALUES ('21293', '1', '0', 'The time to strike is at hand. Terminate Icarius.', '15', '0', '100', '0', '0', '0', '0', '0', 'Borak');
INSERT INTO `creature_text` VALUES ('21293', '2', '0', 'You have it. Do not lose it again!', '12', '0', '100', '5', '0', '0', '0', '0', 'Borak');
